#include <stdio.h>
#include <stdlib.h>

static void lfs_reloc_log(const char *evt, lfs_t *lfs, lfs_mdir_t *dir,
        const lfs_block_t pair[2], const struct lfs_mattr *attrs, int attrcount,
        lfs_mdir_t *pdir, int code) {
    FILE *f = fopen("/tmp/logs/18.txt", "a");
    if (!f) {
        return;
    }

    fprintf(f, "EVENT=%s code=%d lfs=%p dir=%p pdir=%p pair=[%llu,%llu] count=%d erased=%d off=%llu etag=%llu\n",
            evt, code, (void*)lfs, (void*)dir, (void*)pdir,
            (unsigned long long)pair[0], (unsigned long long)pair[1],
            dir ? (int)dir->count : -1, dir ? (int)dir->erased : -1,
            dir ? (unsigned long long)dir->off : 0ULL,
            dir ? (unsigned long long)dir->etag : 0ULL);

    if (attrs && attrcount > 0) {
        fprintf(f, "  attrs count=%d\n", attrcount);
        for (int i = 0; i < attrcount; i++) {
            fprintf(f, "    [%d] tag=%u buffer=%p\n", i,
                    (unsigned)attrs[i].tag, (void*)attrs[i].buffer);
        }
    }

    fclose(f);
}

static int lfs_dir_relocatingcommit(lfs_t *lfs, lfs_mdir_t *dir,
        const lfs_block_t pair[2],
        const struct lfs_mattr *attrs, int attrcount,
        lfs_mdir_t *pdir) {
    int state = 0;

    /* Probe: entry */
    lfs_reloc_log("ENTRY", lfs, dir, pair, attrs, attrcount, pdir, state);

    // calculate changes to the directory
    bool hasdelete = false;
    for (int i = 0; i < attrcount; i++) {
        if (lfs_tag_type3(attrs[i].tag) == LFS_TYPE_CREATE) {
            dir->count += 1;
        } else if (lfs_tag_type3(attrs[i].tag) == LFS_TYPE_DELETE) {
            LFS_ASSERT(dir->count > 0);
            dir->count -= 1;
            hasdelete = true;
        } else if (lfs_tag_type1(attrs[i].tag) == LFS_TYPE_TAIL) {
            dir->tail[0] = ((lfs_block_t*)attrs[i].buffer)[0];
            dir->tail[1] = ((lfs_block_t*)attrs[i].buffer)[1];
            dir->split = (lfs_tag_chunk(attrs[i].tag) & 1);
            lfs_pair_fromle32(dir->tail);
        }
    }

    // should we actually drop the directory block?
    if (hasdelete && dir->count == 0) {
        LFS_ASSERT(pdir);
        int err = lfs_fs_pred(lfs, dir->pair, pdir);
        if (err && err != LFS_ERR_NOENT) {
            lfs_reloc_log("ERROR_FS_PRED", lfs, dir, pair, attrs, attrcount, pdir, err);
            return err;
        }

        if (err != LFS_ERR_NOENT && pdir->split) {
            state = LFS_OK_DROPPED;
            lfs_reloc_log("DROPPED_GOTO_FIXMLIST", lfs, dir, pair, attrs, attrcount, pdir, state);
            goto fixmlist;
        }
    }

    if (dir->erased) {
        // try to commit
        struct lfs_commit commit = {
            .block = dir->pair[0],
            .off = dir->off,
            .ptag = dir->etag,
            .crc = 0xffffffff,

            .begin = dir->off,
            .end = (lfs->cfg->metadata_max ?
                lfs->cfg->metadata_max : lfs->cfg->block_size) - 8,
        };

        // traverse attrs that need to be written out
        lfs_pair_tole32(dir->tail);
        int err = lfs_dir_traverse(lfs,
                dir, dir->off, dir->etag, attrs, attrcount,
                0, 0, 0, 0, 0,
                lfs_dir_commit_commit, &(struct lfs_dir_commit_commit){
                    lfs, &commit});
        lfs_pair_fromle32(dir->tail);
        if (err) {
            lfs_reloc_log("ERROR_TRAVERSE", lfs, dir, pair, attrs, attrcount, pdir, err);
            if (err == LFS_ERR_NOSPC || err == LFS_ERR_CORRUPT) {
                lfs_reloc_log("TRAVERSE_GOTO_COMPACT", lfs, dir, pair, attrs, attrcount, pdir, err);
                goto compact;
            }
            return err;
        }

        // commit any global diffs if we have any
        lfs_gstate_t delta = {0};
        lfs_gstate_xor(&delta, &lfs->gstate);
        lfs_gstate_xor(&delta, &lfs->gdisk);
        lfs_gstate_xor(&delta, &lfs->gdelta);
        delta.tag &= ~LFS_MKTAG(0, 0, 0x3ff);
        if (!lfs_gstate_iszero(&delta)) {
            err = lfs_dir_getgstate(lfs, dir, &delta);
            if (err) {
                lfs_reloc_log("ERROR_GETGSTATE", lfs, dir, pair, attrs, attrcount, pdir, err);
                return err;
            }

            lfs_gstate_tole32(&delta);
            err = lfs_dir_commitattr(lfs, &commit,
                    LFS_MKTAG(LFS_TYPE_MOVESTATE, 0x3ff,
                        sizeof(delta)), &delta);
            if (err) {
                lfs_reloc_log("ERROR_COMMITATTR", lfs, dir, pair, attrs, attrcount, pdir, err);
                if (err == LFS_ERR_NOSPC || err == LFS_ERR_CORRUPT) {
                    lfs_reloc_log("COMMITATTR_GOTO_COMPACT", lfs, dir, pair, attrs, attrcount, pdir, err);
                    goto compact;
                }
                return err;
            }
        }

        // finalize commit with the crc
        err = lfs_dir_commitcrc(lfs, &commit);
        if (err) {
            lfs_reloc_log("ERROR_COMMITCRC", lfs, dir, pair, attrs, attrcount, pdir, err);
            if (err == LFS_ERR_NOSPC || err == LFS_ERR_CORRUPT) {
                lfs_reloc_log("COMMITCRC_GOTO_COMPACT", lfs, dir, pair, attrs, attrcount, pdir, err);
                goto compact;
            }
            return err;
        }

        // successful commit, update dir
        LFS_ASSERT(commit.off % lfs->cfg->prog_size == 0);
        dir->off = commit.off;
        dir->etag = commit.ptag;
        // and update gstate
        lfs->gdisk = lfs->gstate;
        lfs->gdelta = (lfs_gstate_t){0};

        lfs_reloc_log("COMMIT_SUCCESS_GOTO_FIXMLIST", lfs, dir, pair, attrs, attrcount, pdir, 0);
        goto fixmlist;
    }

compact:
    // fall back to compaction
    lfs_reloc_log("COMPACT_BEFORE_CACHE_DROP", lfs, dir, pair, attrs, attrcount, pdir, 0);
    lfs_cache_drop(lfs, &lfs->pcache);
    lfs_reloc_log("COMPACT_AFTER_CACHE_DROP", lfs, dir, pair, attrs, attrcount, pdir, 0);

    state = lfs_dir_splittingcompact(lfs, dir, attrs, attrcount,
            dir, 0, dir->count);
    lfs_reloc_log("COMPACT_RESULT", lfs, dir, pair, attrs, attrcount, pdir, state);
    if (state < 0) {
        lfs_reloc_log("COMPACT_ERROR_RETURN", lfs, dir, pair, attrs, attrcount, pdir, state);
        return state;
    }

    lfs_reloc_log("COMPACT_GOTO_FIXMLIST", lfs, dir, pair, attrs, attrcount, pdir, state);
    goto fixmlist;

fixmlist:;
    // this complicated bit of logic is for fixing up any active
    // metadata-pairs that we may have affected
    //
    // note we have to make two passes since the mdir passed to
    // lfs_dir_commit could also be in this list, and even then
    // we need to copy the pair so they don't get clobbered if we refetch
    // our mdir.
    lfs_reloc_log("FIXMLIST_ENTRY", lfs, dir, pair, attrs, attrcount, pdir, state);

    lfs_block_t oldpair[2] = {pair[0], pair[1]};
    for (struct lfs_mlist *d = lfs->mlist; d; d = d->next) {
        if (lfs_pair_cmp(d->m.pair, oldpair) == 0) {
            d->m = *dir;
            if (d->m.pair != pair) {
                for (int i = 0; i < attrcount; i++) {
                    if (lfs_tag_type3(attrs[i].tag) == LFS_TYPE_DELETE &&
                            d->id == lfs_tag_id(attrs[i].tag) &&
                            d->type != LFS_TYPE_DIR) {
                        d->m.pair[0] = LFS_BLOCK_NULL;
                        d->m.pair[1] = LFS_BLOCK_NULL;
                    } else if (lfs_tag_type3(attrs[i].tag) == LFS_TYPE_DELETE &&
                            d->id > lfs_tag_id(attrs[i].tag)) {
                        d->id -= 1;
                        if (d->type == LFS_TYPE_DIR) {
                            ((lfs_dir_t*)d)->pos -= 1;
                        }
                    } else if (lfs_tag_type3(attrs[i].tag) == LFS_TYPE_CREATE &&
                            d->id >= lfs_tag_id(attrs[i].tag)) {
                        d->id += 1;
                        if (d->type == LFS_TYPE_DIR) {
                            ((lfs_dir_t*)d)->pos += 1;
                        }
                    }
                }
            }

            while (d->id >= d->m.count && d->m.split) {
                // we split and id is on tail now
                if (lfs_pair_cmp(d->m.tail, lfs->root) != 0) {
                    d->id -= d->m.count;
                }
                int err = lfs_dir_fetch(lfs, &d->m, d->m.tail);
                if (err) {
                    lfs_reloc_log("FIXMLIST_FETCH_ERROR", lfs, dir, pair, attrs, attrcount, pdir, err);
                    return err;
                }
            }
        }
    }

    lfs_reloc_log("EXIT", lfs, dir, pair, attrs, attrcount, pdir, state);
    return state;
}
