static void lfs_log_ensure_dir(void) {
    struct stat st;
    if (stat("/tmp/logs", &st) != 0) {
        /* attempt to create directory if it doesn't exist; ignore errors */
        (void)mkdir("/tmp/logs", 0755);
    }
}
static void lfs_log_printf(const char *fmt, ...) {
    lfs_log_ensure_dir();
    FILE *f = fopen("/tmp/logs/274.txt", "a");
    if (!f) {
        return;
    }
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fputs("\n", f);
    fclose(f);
}

lfs_soff_t lfs_dir_tell(lfs_t *lfs, lfs_dir_t *dir) {
    int err = LFS_LOCK(lfs->cfg);
    if (err) {
        return err;
    }

    /* Probe replacing LFS_TRACE("lfs_dir_tell(%p, %p)", ...) */
    lfs_log_printf("PROBE: lfs_dir_tell ENTER: lfs=%p, dir=%p", (void*)lfs, (void*)dir);

    lfs_soff_t res = lfs_dir_tell_(lfs, dir);

    /* Probe replacing LFS_TRACE("lfs_dir_tell -> %lld", res) */
    lfs_log_printf("PROBE: lfs_dir_tell RETURN: lfs=%p, dir=%p, res=%lld",
                   (void*)lfs, (void*)dir, (long long)res);

    LFS_UNLOCK(lfs->cfg);
    return res;
}
