#include <stdio.h>
#include <stdarg.h>
#include <stdint.h>

/*
 * Probe logging helper. Appends human-readable traces to
 * /tmp/logs/274.txt so other agents can observe control flow
 * and state around the LFS_UNLOCK(lfs->cfg) call.
 *
 * This function is intentionally simple and preserves the
 * original logic of lfs_dir_tell. It must not change program
 * behaviour other than producing logs.
 */
static void lfs_probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/274.txt", "a");
    if (!f) {
        return;
    }
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    fprintf(f, "\n");
    va_end(ap);
    fclose(f);
}

lfs_soff_t lfs_dir_tell(lfs_t *lfs, lfs_dir_t *dir) {
    int err = LFS_LOCK(lfs->cfg);

    /* Log entry and lock result. Use the address of the cfg member
     * (not its value) to avoid assumptions about its type. */
    lfs_probe_log("ENTER lfs_dir_tell: lfs=%p dir=%p &cfg=%p LFS_LOCK_result=%d",
                  (void*)lfs, (void*)dir, (void*)&lfs->cfg, err);

    if (err) {
        lfs_probe_log("lfs_dir_tell: LFS_LOCK failed, returning err=%d", err);
        return err;
    }
    LFS_TRACE("lfs_dir_tell(%p, %p)", (void*)lfs, (void*)dir);

    lfs_soff_t res = lfs_dir_tell_(lfs, dir);

    LFS_TRACE("lfs_dir_tell -> %" PRId32, res);

    /* Log state immediately before calling LFS_UNLOCK. This documents
     * that the unlock call is reached during normal control flow. We
     * log the result and the same addresses again so downstream
     * analysis can compare pre/post values to detect any observable
     * change. */
    lfs_probe_log("BEFORE LFS_UNLOCK: lfs=%p dir=%p &cfg=%p res=%lld",
                  (void*)lfs, (void*)dir, (void*)&lfs->cfg, (long long)res);

    LFS_UNLOCK(lfs->cfg);

    /* Log immediately after the unlock call to show that execution
     * continues and that no observable state (as captured by the
     * logged values) changed because of the unlock. This provides
     * evidence useful to reproduce the reported "executed no-op"
     * scenario for LFS_UNLOCK. */
    lfs_probe_log("AFTER  LFS_UNLOCK: lfs=%p dir=%p &cfg=%p res=%lld",
                  (void*)lfs, (void*)dir, (void*)&lfs->cfg, (long long)res);

    return res;
}
