#include <stdio.h>
#include <stdarg.h>

static void lfs_dir_traverse_log(const char *fmt, ...) {
    FILE *f = fopen("/tmp/logs/5.txt", "a");
    if (!f) {
        return;
    }
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fprintf(f, "\n");
    fclose(f);
}

static int lfs_dir_traverse(lfs_t *lfs,
        const lfs_mdir_t *dir, lfs_off_t off, lfs_tag_t ptag,
        const struct lfs_mattr *attrs, int attrcount,
        lfs_tag_t tmask, lfs_tag_t ttag,
        uint16_t begin, uint16_t end, int16_t diff,
        int (*cb)(void *data, lfs_tag_t tag, const void *buffer), void *data) {
    // This function in inherently recursive, but bounded. To allow tool-based
    // analysis without unnecessary code-cost we use an explicit stack
    struct lfs_dir_traverse stack[LFS_DIR_TRAVERSE_DEPTH-1];
    unsigned sp = 0;
    int res;

    /* Log entry state */
    lfs_dir_traverse_log(
        "ENTER lfs_dir_traverse: dir=%p off=%ld ptag=0x%08lx tmask=0x%08lx ttag=0x%08lx begin=%u end=%u diff=%d cb=%p data=%p attrs=%p attrcount=%d",
        (void*)dir, (long)off, (unsigned long)ptag, (unsigned long)tmask,
        (unsigned long)ttag, (unsigned)begin, (unsigned)end, (int)diff,
        (void*)cb, data, (const void*)attrs, attrcount);

    // iterate over directory and attrs
    lfs_tag_t tag;
    const void *buffer;
    struct lfs_diskoff disk = {0};
    while (true) {
        {
            if (off+lfs_tag_dsize(ptag) < dir->off) {
                off += lfs_tag_dsize(ptag);
                int err = lfs_bd_read(lfs,
                        NULL, &lfs->rcache, sizeof(tag),
                        dir->pair[0], off, &tag, sizeof(tag));
                if (err) {
                    lfs_dir_traverse_log("ERROR lfs_bd_read returned %d", err);
                    return err;
                }

                tag = (lfs_frombe32(tag) ^ ptag) | 0x80000000;
                disk.block = dir->pair[0];
                disk.off = off+sizeof(lfs_tag_t);
                buffer = &disk;
                ptag = tag;
            } else if (attrcount > 0) {
                tag = attrs[0].tag;
                buffer = attrs[0].buffer;
                attrs += 1;
                attrcount -= 1;
            } else {
                // finished traversal, pop from stack?
                res = 0;
                break;
            }

            // do we need to filter?
            lfs_tag_t mask = LFS_MKTAG(0x7ff, 0, 0);
            if ((mask & tmask & tag) != (mask & tmask & ttag)) {
                continue;
            }

            if (lfs_tag_id(tmask) != 0) {
                LFS_ASSERT(sp < LFS_DIR_TRAVERSE_DEPTH);
                // recurse, scan for duplicates, and update tag based on
                // creates/deletes
                stack[sp] = (struct lfs_dir_traverse){
                    .dir        = dir,
                    .off        = off,
                    .ptag       = ptag,
                    .attrs      = attrs,
                    .attrcount  = attrcount,
                    .tmask      = tmask,
                    .ttag       = ttag,
                    .begin      = begin,
                    .end        = end,
                    .diff       = diff,
                    .cb         = cb,
                    .data       = data,
                    .tag        = tag,
                    .buffer     = buffer,
                    .disk       = disk,
                };
                sp += 1;

                /* Log the values before clearing the filter parameters */
                lfs_dir_traverse_log(
                    "RECURSE push: sp=%u saved_ttag=0x%08lx saved_tmask=0x%08lx saved_begin=%u saved_end=%u saved_diff=%d cb=%p data=%p",
                    sp, (unsigned long)stack[sp-1].ttag, (unsigned long)stack[sp-1].tmask,
                    (unsigned)stack[sp-1].begin, (unsigned)stack[sp-1].end,
                    (int)stack[sp-1].diff, (void*)stack[sp-1].cb, stack[sp-1].data);

                /*
                 * The following assignments clear the filter context so we can
                 * recurse with a different filter. We log before and after to
                 * capture the modification to the function parameter 'ttag'.
                 */
                lfs_dir_traverse_log(
                    "BEFORE clear: tmask=0x%08lx ttag=0x%08lx begin=%u end=%u diff=%d cb=%p data=%p",
                    (unsigned long)tmask, (unsigned long)ttag, (unsigned)begin, (unsigned)end,
                    (int)diff, (void*)cb, data);

                tmask = 0;
                ttag = 0; /* <-- assignment reported as violating MISRA C 2023 Rule 17.8 */
                begin = 0;
                end = 0;
                diff = 0;
                cb = lfs_dir_traverse_filter;
                data = &stack[sp-1].tag;

                lfs_dir_traverse_log(
                    "AFTER clear: tmask=0x%08lx ttag=0x%08lx begin=%u end=%u diff=%d cb=%p data=%p",
                    (unsigned long)tmask, (unsigned long)ttag, (unsigned)begin, (unsigned)end,
                    (int)diff, (void*)cb, data);

                continue;
            }
        }

popped:
        // in filter range?
        if (lfs_tag_id(tmask) != 0 &&
                !(lfs_tag_id(tag) >= begin && lfs_tag_id(tag) < end)) {
            continue;
        }

        // handle special cases for mcu-side operations
        if (lfs_tag_type3(tag) == LFS_FROM_NOOP) {
            // do nothing
        } else if (lfs_tag_type3(tag) == LFS_FROM_MOVE) {
            // Without this condition, lfs_dir_traverse can exhibit an
            // extremely expensive O(n^3) of nested loops when renaming.
            // This happens because lfs_dir_traverse tries to filter tags by
            // the tags in the source directory, triggering a second
            // lfs_dir_traverse with its own filter operation.
            //
            // traverse with commit
            // '-> traverse with filter
            //     '-> traverse with move
            //         '-> traverse with filter
            //
            // However we don't actually care about filtering the second set of
            // tags, since duplicate tags have no effect when filtering.
            //
            // This check skips this unnecessary recursive filtering explicitly,
            // reducing this runtime from O(n^3) to O(n^2).
            if (cb == lfs_dir_traverse_filter) {
                continue;
            }

            // recurse into move
            stack[sp] = (struct lfs_dir_traverse){
                .dir        = dir,
                .off        = off,
                .ptag       = ptag,
                .attrs      = attrs,
                .attrcount  = attrcount,
                .tmask      = tmask,
                .ttag       = ttag,
                .begin      = begin,
                .end        = end,
                .diff       = diff,
                .cb         = cb,
                .data       = data,
                .tag        = LFS_MKTAG(LFS_FROM_NOOP, 0, 0),
            };
            sp += 1;

            uint16_t fromid = lfs_tag_size(tag);
            uint16_t toid = lfs_tag_id(tag);
            dir = buffer;
            off = 0;
            ptag = 0xffffffff;
            attrs = NULL;
            attrcount = 0;
            tmask = LFS_MKTAG(0x600, 0x3ff, 0);

            /* Log the ttag change for move case */
            lfs_dir_traverse_log(
                "MOVE case BEFORE set: old_ttag=0x%08lx old_tmask=0x%08lx",
                (unsigned long)ttag, (unsigned long)tmask);

            ttag = LFS_MKTAG(LFS_TYPE_STRUCT, 0, 0);

            lfs_dir_traverse_log(
                "MOVE case AFTER set: new_ttag=0x%08lx new_tmask=0x%08lx begin=%u end=%u diff(before)=%d",
                (unsigned long)ttag, (unsigned long)tmask, (unsigned)fromid, (unsigned)(fromid+1), (int)diff);

            begin = fromid;
            end = fromid+1;
            diff = toid-fromid+diff;
        } else if (lfs_tag_type3(tag) == LFS_FROM_USERATTRS) {
            for (unsigned i = 0; i < lfs_tag_size(tag); i++) {
                const struct lfs_attr *a = buffer;
                res = cb(data, LFS_MKTAG(LFS_TYPE_USERATTR + a[i].type,
                        lfs_tag_id(tag) + diff, a[i].size), a[i].buffer);
                if (res < 0) {
                    lfs_dir_traverse_log("CB returned error %d in USERATTRS", res);
                    return res;
                }

                if (res) {
                    break;
                }
            }
        } else {
            res = cb(data, tag + LFS_MKTAG(0, diff, 0), buffer);
            if (res < 0) {
                lfs_dir_traverse_log("CB returned error %d", res);
                return res;
            }

            if (res) {
                break;
            }
        }
    }

    if (sp > 0) {
        /*
         * pop from the stack and return, fortunately all pops share
         * a destination
         */
        lfs_dir_traverse_log("POP before: sp=%u", sp);
        dir         = stack[sp-1].dir;
        off         = stack[sp-1].off;
        ptag        = stack[sp-1].ptag;
        attrs       = stack[sp-1].attrs;
        attrcount   = stack[sp-1].attrcount;
        tmask       = stack[sp-1].tmask;

        /* Log ttag restore */
        lfs_dir_traverse_log(
            "POP restore: restoring ttag from stack value 0x%08lx (was 0x%08lx)",
            (unsigned long)stack[sp-1].ttag, (unsigned long)ttag);

        ttag        = stack[sp-1].ttag;
        begin       = stack[sp-1].begin;
        end         = stack[sp-1].end;
        diff        = stack[sp-1].diff;
        cb          = stack[sp-1].cb;
        data        = stack[sp-1].data;
        tag         = stack[sp-1].tag;
        buffer      = stack[sp-1].buffer;
        disk        = stack[sp-1].disk;
        sp -= 1;

        lfs_dir_traverse_log("POP after: sp=%u restored_ttag=0x%08lx tmask=0x%08lx begin=%u end=%u diff=%d cb=%p data=%p",
            sp, (unsigned long)ttag, (unsigned long)tmask, (unsigned)begin, (unsigned)end, (int)diff, (void*)cb, data);

        goto popped;
    } else {
        lfs_dir_traverse_log("EXIT lfs_dir_traverse: res=%d", res);
        return res;
    }
}
