#include <stdio.h>

/* Probe helper: append control-flow/state logs to /tmp/logs/130.txt
   Keep this as a small, side-effecting helper that does not change
   the logic of lfs_file_rewind. */
static void lfs_probe_log_130(const char *stage, lfs_t *lfs, lfs_file_t *file, int err) {
    FILE *f = fopen("/tmp/logs/130.txt", "a");
    if (!f) {
        return;
    }
    /* Attempt to log useful state: stage, pointers, err, and cfg pointer. */
    void *cfg_ptr = NULL;
    if (lfs) {
        /* lfs->cfg is referenced in the original function (LFS_LOCK/LFS_UNLOCK). */
        cfg_ptr = (void*)lfs->cfg;
    }
    fprintf(f, "probe: stage=%s lfs=%p file=%p err=%d cfg=%p\n",
            stage, (void*)lfs, (void*)file, err, cfg_ptr);
    fclose(f);
}

int lfs_file_rewind(lfs_t *lfs, lfs_file_t *file) {
    int err = LFS_LOCK(lfs->cfg);
    if (err) {
        /* Log that lock failed and we are returning early. */
        lfs_probe_log_130("lock_failed", lfs, file, err);
        return err;
    }
    LFS_TRACE("lfs_file_rewind(%p, %p)", (void*)lfs, (void*)file);

    /* Log just before calling the internal rewind helper. */
    lfs_probe_log_130("before_lfs_file_rewind_", lfs, file, err);

    err = lfs_file_rewind_(lfs, file);

    LFS_TRACE("lfs_file_rewind -> %d", err);

    /* Log state immediately before the unlock. This is the key location
       for demonstrating whether LFS_UNLOCK(lfs->cfg) has any effect. */
    lfs_probe_log_130("after_rewind_before_unlock", lfs, file, err);

    LFS_UNLOCK(lfs->cfg);

    /* Log after the unlock call to show that control reached here and to
       capture the cfg pointer even if LFS_UNLOCK is a no-op in the
       current configuration. */
    lfs_probe_log_130("after_unlock", lfs, file, err);

    return err;
}
