#include <stdio.h>

static void lfs_log_probe(const char *stage, lfs_t *lfs, lfs_file_t *file,
        int err, lfs_soff_t off, int whence) {
    FILE *f = fopen("/tmp/logs/58.txt", "a");
    if (f) {
        /* Log control stage and primary state relevant to the lock/seek flow */
        fprintf(f, "%s: lfs=%p file=%p err=%d off=%" PRId32 " whence=%d\n",
                stage, (void*)lfs, (void*)file, err, off, whence);
        fclose(f);
    }
}

static void lfs_log_res(const char *stage, lfs_t *lfs, lfs_file_t *file,
        lfs_soff_t res) {
    FILE *f = fopen("/tmp/logs/58.txt", "a");
    if (f) {
        fprintf(f, "%s: lfs=%p file=%p res=%" PRId32 "\n",
                stage, (void*)lfs, (void*)file, res);
        fclose(f);
    }
}

lfs_soff_t lfs_file_seek(lfs_t *lfs, lfs_file_t *file,
        lfs_soff_t off, int whence) {
    int err = LFS_LOCK(lfs->cfg);
    /* Probe immediately after the lock to capture the lock result. */
    lfs_log_probe("after_lock", lfs, file, err, off, whence);
    if (err) {
        /* If this branch were ever taken, record it before returning. */
        lfs_log_probe("error_before_return", lfs, file, err, off, whence);
        return err;
    }
    LFS_TRACE("lfs_file_seek(%p, %p, %"PRId32", %d)",
            (void*)lfs, (void*)file, off, whence);
    LFS_ASSERT(lfs_mlist_isopen(lfs->mlist, (struct lfs_mlist*)file));

    /* Record that we are about to call the internal seek helper. */
    lfs_log_probe("before_seek_", lfs, file, err, off, whence);

    lfs_soff_t res = lfs_file_seek_(lfs, file, off, whence);

    /* Record the result of the internal seek helper. */
    lfs_log_res("after_seek_", lfs, file, res);
    LFS_TRACE("lfs_file_seek -> %"PRId32, res);
    LFS_UNLOCK(lfs->cfg);
    return res;
}
