#include <stdio.h>
#include <stdarg.h>
#include <inttypes.h>
#include <stdint.h>

/* Probe logging helper: append human-readable traces to /tmp/logs/138.txt
 * This preserves program logic while recording control flow and key state. */
static void lfs_probe_log(const char *fmt, ...) {
    FILE *f = fopen("/tmp/logs/138.txt", "a");
    if (!f) {
        return;
    }
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fprintf(f, "\n");
    fclose(f);
}

int lfs_file_truncate(lfs_t *lfs, lfs_file_t *file, lfs_off_t size) {
    lfs_probe_log("ENTER lfs_file_truncate: lfs=%p file=%p size=%" PRIuMAX,
            (void*)lfs, (void*)file, (uintmax_t)size);

    int err = LFS_LOCK(lfs->cfg);
    lfs_probe_log("After LFS_LOCK: err=%d", err);

    if (err) {
        lfs_probe_log("lfs_file_truncate returning early due to lock error: %d", err);
        return err;
    }

    LFS_TRACE("lfs_file_truncate(%p, %p, %"PRIu32")",
            (void*)lfs, (void*)file, size);
    LFS_ASSERT(lfs_mlist_isopen(lfs->mlist, (struct lfs_mlist*)file));

    err = lfs_file_truncate_(lfs, file, size);

    lfs_probe_log("After lfs_file_truncate_: err=%d", err);
    LFS_TRACE("lfs_file_truncate -> %d", err);

    /* Log the cfg pointer just before and after the unlock to capture whether
     * any observable state change occurs. In environments where LFS_UNLOCK is a
     * no-op (e.g. defined as ((void)cfg)), these logs will show the call but
     * no change in state. */
    lfs_probe_log("About to call LFS_UNLOCK with cfg=%p", (void*)lfs->cfg);
    LFS_UNLOCK(lfs->cfg);
    lfs_probe_log("After LFS_UNLOCK with cfg=%p (note: LFS_UNLOCK may be a no-op)", (void*)lfs->cfg);

    lfs_probe_log("EXIT lfs_file_truncate: returning %d", err);
    return err;
}
