lfs_ssize_t lfs_getattr(lfs_t *lfs, const char *path,
        uint8_t type, void *buffer, lfs_size_t size) {
    int err = LFS_LOCK(lfs->cfg);
    if (err) {
        return err;
    }

    /* Probe: log entry state (value and address of parameter 'path') */
    {
        /* make a local copy of the pointer value for later comparison */
        const char *path_before = path;
        FILE *fp = fopen("/tmp/logs/205.txt", "a");
        if (fp) {
            fprintf(fp, "ENTRY lfs_getattr: lfs=%p, path_value=%p, path_str=\"%s\", &path=%p, type=%" PRIu8 ", buffer=%p, size=%" PRIu32 "\n",
                    (void*)lfs,
                    (void*)path_before,
                    (path_before != NULL) ? path_before : "(null)",
                    (void*)&path,
                    (uint8_t)type,
                    buffer,
                    (lfs_size_t)size);
            fclose(fp);
        }

        LFS_TRACE("lfs_getattr(%p, \"%s\", %"PRIu8", %p, %"PRIu32")",
                (void*)lfs, path, type, buffer, size);

        lfs_ssize_t res = lfs_getattr_(lfs, path, type, buffer, size);

        /* Probe: log after calling lfs_getattr_ (detect if parameter 'path' was modified) */
        fp = fopen("/tmp/logs/205.txt", "a");
        if (fp) {
            fprintf(fp, "AFTER lfs_getattr_: res=%" PRId32 ", path_value_after=%p, path_str_after=\"%s\", &path=%p, changed=%d\n",
                    (int32_t)res,
                    (void*)path,
                    (path != NULL) ? path : "(null)",
                    (void*)&path,
                    (path != path_before));
            fclose(fp);
        }

        LFS_TRACE("lfs_getattr -> %"PRId32, res);
        LFS_UNLOCK(lfs->cfg);
        return res;
    }
}
