#include <stdio.h>
#include <stdint.h>

/* Probe helper: append a short trace to /tmp/logs/202.txt. */
static void lfs_probe_lock_result(void *cfg_ptr, void *lfs_ptr, const char *path,
        uint8_t type, void *buffer, int err) {
    FILE *f = fopen("/tmp/logs/202.txt", "a");
    if (!f) {
        return;
    }
    fprintf(f, "lfs_getattr ENTER: lfs=%p cfg=%p path=\"%s\" type=%u buffer=%p\n",
            lfs_ptr, cfg_ptr, (path != NULL) ? path : "(null)", (unsigned)type, buffer);
    fprintf(f, "After LFS_LOCK: err=%d (0 means not taken)\n", err);
    fclose(f);
}

static void lfs_probe_if_branch(int err) {
    FILE *f = fopen("/tmp/logs/202.txt", "a");
    if (!f) {
        return;
    }
    fprintf(f, "lfs_getattr: if (err) branch taken -> returning %d\n", err);
    fclose(f);
}

static void lfs_probe_exit_result(void *lfs_ptr, lfs_ssize_t res) {
    FILE *f = fopen("/tmp/logs/202.txt", "a");
    if (!f) {
        return;
    }
    fprintf(f, "lfs_getattr EXIT: lfs=%p result=%" PRId32 "\n", lfs_ptr, (int32_t)res);
    fclose(f);
}

lfs_ssize_t lfs_getattr(lfs_t *lfs, const char *path,
        uint8_t type, void *buffer, lfs_size_t size) {
    int err = LFS_LOCK(lfs->cfg);

    /* Probe the immediate result of LFS_LOCK to capture the value that
       causes the conditional below to be dead (if it is always 0). */
    lfs_probe_lock_result((void*)lfs->cfg, (void*)lfs, path, type, buffer, err);

    if (err) {
        /* Preserve original behavior: log that the branch was taken, then return. */
        lfs_probe_if_branch(err);
        return err;
    }

    LFS_TRACE("lfs_getattr(%p, \"%s\", %"PRIu8", %p, %"PRIu32")",
            (void*)lfs, path, type, buffer, size);

    lfs_ssize_t res = lfs_getattr_(lfs, path, type, buffer, size);

    LFS_TRACE("lfs_getattr -> %"PRId32, res);
    LFS_UNLOCK(lfs->cfg);

    /* Log the function exit and the returned result. */
    lfs_probe_exit_result((void*)lfs, res);
    return res;
}
