#include <stdio.h>
#include <stdarg.h>
#include <string.h>
#include <stdint.h>

/* Probe helper: append formatted messages to /tmp/logs/114.txt */
static void probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/114.txt", "a");
    if (f == NULL) {
        return;
    }
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    fprintf(f, "\n");
    va_end(ap);
    fclose(f);
}

int metal_generic_dev_dma_map(struct metal_bus *bus,
                 struct metal_device *device,
                 uint32_t dir,
                 struct metal_sg *sg_in,
                 int nents_in,
                 struct metal_sg *sg_out)
{
    int i;
    (void)bus;
    probe_log("[probe] executed: (void)bus; (no-op cast to suppress unused-parameter warning)");
    (void)device;
    probe_log("[probe] executed: (void)device; (no-op cast to suppress unused-parameter warning)");

    /* Log initial state: pointers and parameters */
    probe_log("[probe] enter metal_generic_dev_dma_map: bus=%p device=%p dir=%u sg_in=%p nents_in=%d sg_out=%p",
              (void *)bus, (void *)device, (unsigned)dir, (void *)sg_in, nents_in, (void *)sg_out);

    if (sg_out != sg_in) {
        probe_log("[probe] sg_out != sg_in -> memcpy will be called: copying %d entries (%zu bytes)",
                  nents_in, (size_t)(nents_in * (sizeof(struct metal_sg))));
        memcpy(sg_out, sg_in, nents_in*(sizeof(struct metal_sg)));
        probe_log("[probe] memcpy done");
    } else {
        probe_log("[probe] sg_out == sg_in -> memcpy skipped");
    }

    probe_log("[probe] entering loop over %d entries", nents_in);
    for (i = 0; i < nents_in; i++) {
        /* Attempt to log the per-entry state. We cast virt to void* for printing; if virt is not a pointer
         * this cast is only for diagnostic output and should not affect program logic. */
        probe_log("[probe] entry %d: virt=%p len=%lu", i, (void *)sg_out[i].virt, (unsigned long)sg_out[i].len);
        if (dir == METAL_DMA_DEV_W) {
            probe_log("[probe] entry %d: dir indicates device write -> calling metal_cache_flush(virt=%p, len=%lu)",
                      i, (void *)sg_out[i].virt, (unsigned long)sg_out[i].len);
            metal_cache_flush(sg_out[i].virt, sg_out[i].len);
            probe_log("[probe] entry %d: metal_cache_flush returned", i);
        } else {
            probe_log("[probe] entry %d: dir does not indicate device write -> skip metal_cache_flush", i);
        }
        probe_log("[probe] entry %d: calling metal_cache_invalidate(virt=%p, len=%lu)",
                  i, (void *)sg_out[i].virt, (unsigned long)sg_out[i].len);
        metal_cache_invalidate(sg_out[i].virt, sg_out[i].len);
        probe_log("[probe] entry %d: metal_cache_invalidate returned", i);
    }

    probe_log("[probe] exiting metal_generic_dev_dma_map: returning %d", nents_in);

    return nents_in;
}
