#include <stdio.h>
#include <stdarg.h>
#include <string.h>
#include <errno.h>

/* Probe helper: append formatted logs to /tmp/logs/67.txt */
static void probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/67.txt", "a");
    if (f == NULL) {
        return;
    }
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fflush(f);
    fclose(f);
}

int metal_generic_dev_open(struct metal_bus *bus, const char *dev_name,
               struct metal_device **device)
{
    struct metal_list *node;
    struct metal_device *dev;
    int iter = 0;

    /* Log entry and parameters (do not modify the bus) */
    probe_log("ENTER: metal_generic_dev_open called: bus=%p dev_name=%s device_out=%p\n",
              (void *)bus, dev_name ? dev_name : "(null)", (void *)device);

    metal_list_for_each(&_metal.common.generic_device_list, node) {
        iter++;
        dev = metal_container_of(node, struct metal_device, node);

        probe_log("ITER %d: node=%p dev=%p dev->name=%s\n",
                  iter, (void *)node, (void *)dev,
                  (dev && dev->name) ? dev->name : "(null)");

        if (strcmp(dev->name, dev_name) == 0) {
            *device = dev;
            probe_log("MATCH: iter=%d dev=%p name=%s -> setting *device=%p\n",
                      iter, (void *)dev, dev->name, (void *)(*device));

            int ret = metal_generic_dev_sys_open(dev);
            probe_log("CALL: metal_generic_dev_sys_open returned %d for dev=%p name=%s\n",
                      ret, (void *)dev, dev->name);
            return ret;
        }
    }

    probe_log("EXIT: no device found for name=%s -> returning -ENODEV (%d)\n",
              dev_name ? dev_name : "(null)", -ENODEV);

    return -ENODEV;
}
