#include <stdio.h>
#include <string.h>

int metal_generic_dev_open(struct metal_bus *bus, const char *dev_name,
               struct metal_device **device)
{
    struct metal_list *node;
    struct metal_device *dev;
    FILE *fp = fopen("/tmp/logs/67.txt", "a");

    /* Log entry and parameters */
    if (fp != NULL) {
        fprintf(fp, "metal_generic_dev_open: enter bus=%p dev_name=\"%s\" device_ptr=%p\n",
                (void *)bus, (dev_name != NULL) ? dev_name : "(null)", (void *)device);
        fflush(fp);
    }

    metal_list_for_each(&_metal.common.generic_device_list, node) {
        dev = metal_container_of(node, struct metal_device, node);

        /* Probe: log each device inspected and its irq_info pointer (evidence for Rule 8.13) */
        if (fp != NULL) {
            /* dev->name and dev->irq_info are recorded to capture the state related to the violation scenario */
            fprintf(fp, "  inspecting dev=%p name=\"%s\" irq_info=%p\n",
                    (void *)dev,
                    (dev->name != NULL) ? dev->name : "(null)",
                    /* irq_info may be non-const in the original code; we log its address/value */
                    (void *)dev->irq_info);
            fflush(fp);
        }

        if (strcmp(dev->name, dev_name) == 0) {
            *device = dev;

            if (fp != NULL) {
                fprintf(fp, "  match found: assigned device=%p, calling metal_generic_dev_sys_open\n", (void *)dev);
                fclose(fp);
            }

            return metal_generic_dev_sys_open(dev);
        }
    }

    if (fp != NULL) {
        fprintf(fp, "  no matching device found: returning -ENODEV\n");
        fclose(fp);
    }

    return -ENODEV;
}
