#include <stdio.h>
#include <stdint.h>
#include <inttypes.h>
#include <stdatomic.h>
#include <limits.h>
#include <errno.h>

/* Presume struct metal_io_region and related declarations exist elsewhere */

static void metal_io_log(const char *stage, struct metal_io_region *io,
                         unsigned long offset, unsigned char *ptr,
                         int len, int retlen, unsigned int cint, unsigned int i)
{
    FILE *f = fopen("/tmp/logs/36.txt", "a");
    if (!f)
        return;
    fprintf(f, "[%s] offset=%lu ptr=0x%" PRIxPTR " len=%d retlen=%d cint=0x%08x i=%u\n",
            stage, offset, (uintptr_t)ptr, len, retlen, cint, i);
    fclose(f);
}

int metal_io_block_set(struct metal_io_region *io, unsigned long offset,
           unsigned char value, int len)
{
    unsigned char *ptr = metal_io_virt(io, offset);
    int retlen = len;

    /* Log entry state */
    metal_io_log("entry", io, offset, ptr, len, retlen, 0u, 0u);

    if (!ptr)
        return -ERANGE;
    if ((offset + len) > io->size)
        len = io->size - offset;
    retlen = len;

    /* Log after bounds adjustment */
    metal_io_log("after_bounds_adjust", io, ptr, offset, len, retlen, 0u, 0u);

    if (io->ops.block_set) {
        /* Log that we are taking the ops path */
        metal_io_log("using_block_set_op", io, ptr, offset, len, retlen, 0u, 0u);
        (*io->ops.block_set)(
            io, offset, value, memory_order_seq_cst, len);
    } else {
        unsigned int cint = value;
        unsigned int i;

        for (i = 1; i < sizeof(int); i++)
            cint |= ((unsigned int)value << (CHAR_BIT * i));

        /* Log the constructed word and i */
        metal_io_log("constructed_cint", io, ptr, offset, len, retlen, cint, i);

        /* First unaligned bytes to reach int alignment */
        unsigned int iter1 = 0u;
        for (; len && ((uintptr_t)ptr % sizeof(int)); ptr++, len--, iter1++) {
            /* Log each byte write in the first loop */
            metal_io_log("loop1_byte_write", io, ptr, offset, len, retlen, cint, iter1);
            *(unsigned char *)ptr = (unsigned char) value;
        }

        /* Middle loop: writes full ints. This is the loop of interest for the MISRA rule. */
        unsigned int iter2 = 0u;
        for (; len >= (int)sizeof(int); ptr += sizeof(int),
                        len -= sizeof(int), iter2++) {
            /* Log state BEFORE the iteration's write */
            metal_io_log("loop2_int_write_before", io, ptr, offset, len, retlen, cint, iter2);
            *(unsigned int *)ptr = cint;
            /* Log state AFTER the iteration's write */
            metal_io_log("loop2_int_write_after", io, ptr, offset, len, retlen, cint, iter2);
        }

        /* Final bytes */
        unsigned int iter3 = 0u;
        for (; len != 0; ptr++, len--, iter3++) {
            /* Log each byte write in the final loop */
            metal_io_log("loop3_byte_write", io, ptr, offset, len, retlen, cint, iter3);
            *(unsigned char *)ptr = (unsigned char) value;
        }

        atomic_thread_fence(memory_order_seq_cst);

        /* Log exit state from fallback path */
        metal_io_log("fallback_exit", io, ptr, offset, len, retlen, cint, i);
    }
    return retlen;
}
