#include <stdio.h>
#include <stdarg.h>
#include <stdint.h>
#include <limits.h>
#include <stdatomic.h>
#include <errno.h>
#include <sys/stat.h>
#include <sys/types.h>
#include <unistd.h>

/* Probe logging helper: append formatted messages to /tmp/logs/36.txt */
static void probe_log(const char *fmt, ...)
{
    FILE *f;
    va_list ap;

    f = fopen("/tmp/logs/36.txt", "a");
    if (!f) {
        /* try to create directory and reopen */
        mkdir("/tmp/logs", 0777);
        f = fopen("/tmp/logs/36.txt", "a");
        if (!f) {
            return;
        }
    }

    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fclose(f);
}

/* The original function with inserted logging probes. */
int metal_io_block_set(struct metal_io_region *io, unsigned long offset,
           unsigned char value, int len)
{
    unsigned char *ptr = metal_io_virt(io, offset);
    int retlen = len;

    probe_log("ENTRY: metal_io_block_set: io=%p offset=%lu value=%u len=%d ptr=%p\n",
              (void *)io, offset, (unsigned)value, len, (void *)ptr);

    if (!ptr) {
        probe_log("EARLY RETURN: ptr == NULL -> return -ERANGE\n");
        return -ERANGE;
    }
    if ((offset + len) > io->size)
        len = io->size - offset;
    retlen = len;

    probe_log("AFTER ADJUST: len=%d retlen=%d io->size=%lu\n",
              len, retlen, (unsigned long)io->size);

    if (io->ops.block_set) {
        probe_log("BRANCH: using ops.block_set\n");
        (*io->ops.block_set)(
            io, offset, value, memory_order_seq_cst, len);
    } else {
        unsigned int cint = value;
        unsigned int i;

        for (i = 1; i < sizeof(int); i++)
            cint |= ((unsigned int)value << (CHAR_BIT * i));

        probe_log("FALLBACK: byte-fill logic, cint=0x%08x sizeof(int)=%zu\n",
                  cint, sizeof(int));

        /* first partial bytes until word alignment */
        probe_log("START first partial loop: ptr=%p len=%d sizeof(int)=%zu\n",
                  (void *)ptr, len, sizeof(int));
        for (; len && ((uintptr_t)ptr % sizeof(int)); ptr++, len--) {
            probe_log("first-loop ITER: ptr=%p (%#lx) len=%d -> write byte 0x%02x\n",
                      (void *)ptr, (unsigned long)((uintptr_t)ptr), len, (unsigned)value);
            *(unsigned char *)ptr = (unsigned char) value;
        }
        probe_log("END first partial loop: ptr=%p len=%d\n", (void *)ptr, len);

        /* main word-sized loop */
        probe_log("START word loop: ptr=%p len=%d sizeof(int)=%zu\n",
                  (void *)ptr, len, sizeof(int));
        for (; len >= (int)sizeof(int); ptr += sizeof(int),
                        len -= sizeof(int)) {
            probe_log("word-loop ITER: ptr=%p (%#lx) len=%d write 0x%08x\n",
                      (void *)ptr, (unsigned long)((uintptr_t)ptr), len, cint);
            *(unsigned int *)ptr = cint;
        }
        probe_log("END word loop: ptr=%p len=%d\n", (void *)ptr, len);

        /* tail bytes */
        probe_log("START tail loop: ptr=%p len=%d\n", (void *)ptr, len);
        for (; len != 0; ptr++, len--) {
            probe_log("tail-loop ITER: ptr=%p (%#lx) len=%d -> write byte 0x%02x\n",
                      (void *)ptr, (unsigned long)((uintptr_t)ptr), len, (unsigned)value);
            *(unsigned char *)ptr = (unsigned char) value;
        }
        probe_log("END tail loop: ptr=%p len=%d\n", (void *)ptr, len);

        atomic_thread_fence(memory_order_seq_cst);
        probe_log("AFTER fence: exiting fallback branch\n");
    }
    probe_log("EXIT: retlen=%d\n", retlen);
    return retlen;
}
