#include <stdio.h>
#include <sys/mman.h>
#include <sys/types.h>
#include <sys/stat.h>
#include <sys/file.h>
#include <unistd.h>
#include <errno.h>

/* Probe logging function: append human-readable traces to /tmp/logs/109.txt */
static void metal_map_log(const char *stage, int flags, int fd,
                          off_t offset, size_t size, int expand,
                          int error, void *mem)
{
    FILE *f = fopen("/tmp/logs/109.txt", "a");
    if (!f)
        return;
    fprintf(f, "[metal_map] %s\n", stage);
    fprintf(f, "  flags: 0x%x\n", flags);
    fprintf(f, "  fd: %d\n", fd);
    fprintf(f, "  offset: %lld\n", (long long)offset);
    fprintf(f, "  size: %zu\n", size);
    fprintf(f, "  expand: %d\n", expand);
    fprintf(f, "  error: %d\n", error);
    fprintf(f, "  mem: %p\n", mem);
    fprintf(f, "\n");
    fclose(f);
}

int metal_map(int fd, off_t offset, size_t size, int expand, int flags,
              void **result)
{
    int prot = PROT_READ | PROT_WRITE, error;
    void *mem;

    /* Log initial entry state */
    metal_map_log("entry", flags, fd, offset, size, expand, 0, NULL);

    /* Violation point: modifying parameter 'flags' in-place. Log before/after. */
    metal_map_log("before flags |= MAP_SHARED", flags, fd, offset, size, expand, 0, NULL);
    flags |= MAP_SHARED;
    metal_map_log("after flags |= MAP_SHARED", flags, fd, offset, size, expand, 0, NULL);

    if (fd < 0) {
        /* Log branch taken */
        metal_map_log("branch: fd < 0 (about to set fd=-1 and flags=MAP_PRIVATE|MAP_ANONYMOUS)",
                      flags, fd, offset, size, expand, 0, NULL);

        fd = -1;
        flags = MAP_PRIVATE | MAP_ANONYMOUS;

        /* Log the state after modifying flags in this branch */
        metal_map_log("after setting fd=-1 and flags=MAP_PRIVATE|MAP_ANONYMOUS",
                      flags, fd, offset, size, expand, 0, NULL);
    } else if (expand) {
        off_t reqsize = offset + size;
        struct stat stat_buf;

        metal_map_log("branch: expand (before flock)", flags, fd, offset, size, expand, 0, NULL);

        error = flock(fd, LOCK_EX) < 0 ? -errno : 0;
        metal_map_log("after flock LOCK_EX", flags, fd, offset, size, expand, error, NULL);
        if (!error)
            error = fstat(fd, &stat_buf);
        metal_map_log("after fstat", flags, fd, offset, size, expand, error, NULL);
        if (!error && stat_buf.st_size < reqsize)
            error = ftruncate(fd, reqsize);
        metal_map_log("after potential ftruncate", flags, fd, offset, size, expand, error, NULL);
        if (!error)
            flock(fd, LOCK_UN);
        if (error)
            return -errno;
    }

    mem = mmap(NULL, size, prot, flags, fd, offset);
    metal_map_log("after mmap", flags, fd, offset, size, expand, (mem == MAP_FAILED) ? -errno : 0, mem);
    if (mem == MAP_FAILED)
        return -errno;
    *result = mem;

    metal_map_log("return success", flags, fd, offset, size, expand, 0, mem);
    return 0;
}
