#include <stdio.h>
#include <stdarg.h>
#include <stdint.h>
#include <stddef.h>
#include <string.h>
#include <errno.h>

/* Probe helper: append human-readable logs to /tmp/logs/120.txt */
static void probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/120.txt", "a");
    if (!f)
        return;
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fprintf(f, "\n");
    fclose(f);
}

static int metal_uio_dev_dma_map(struct linux_bus *lbus,
                 struct linux_device *ldev,
                 uint32_t dir,
                 struct metal_sg *sg_in,
                 int nents_in,
                 struct metal_sg *sg_out)
{
    int i, j;
    void *vaddr_sg_lo, *vaddr_sg_hi, *vaddr_lo, *vaddr_hi;
    struct metal_io_region *io;

    (void)lbus;
    (void)dir;

    probe_log("ENTER: %s: nents_in=%d, dev_name=%s", __func__, nents_in,
              (ldev && ldev->dev_name) ? ldev->dev_name : "<null>");

    /* Check if the the input virt address is MMIO address */
    for (i = 0; i < nents_in; i++) {
        vaddr_sg_lo = sg_in[i].virt;
        vaddr_sg_hi = vaddr_sg_lo + sg_in[i].len;

        probe_log("ITER i=%d: sg_in[%d].virt=%p, len=%zu, computed vaddr_sg_hi=%p",
                  i, i, vaddr_sg_lo, (size_t)sg_in[i].len, vaddr_sg_hi);

        for (j = 0, io = ldev->device.regions;
             j < (int)ldev->device.num_regions; j++, io++) {
            vaddr_lo = io->virt;
            vaddr_hi = vaddr_lo + io->size;

            probe_log("  REGION j=%d: region.virt=%p, region.size=%zu, computed vaddr_hi=%p",
                      j, vaddr_lo, (size_t)io->size, vaddr_hi);

            if (vaddr_sg_lo >= vaddr_lo &&
                vaddr_sg_hi <= vaddr_hi) {
                probe_log("  MATCH found at region j=%d for sg[%d]: sg_lo=%p sg_hi=%p region_lo=%p region_hi=%p",
                          j, i, vaddr_sg_lo, vaddr_sg_hi, vaddr_lo, vaddr_hi);
                break;
            }
        }
        if (j == (int)ldev->device.num_regions) {
            probe_log("  NO MATCH for sg[%d]: virt=%p len=%zu; checked_regions=%d",
                      i, vaddr_sg_lo, (size_t)sg_in[i].len, (int)ldev->device.num_regions);

            metal_log(METAL_LOG_WARNING,
              "%s,%s: input address isn't MMIO addr: 0x%x,%d.\n",
            __func__, ldev->dev_name, vaddr_sg_lo, sg_in[i].len);

            probe_log("EXIT: %s: returning -EINVAL", __func__);
            return -EINVAL;
        }
    }
    if (sg_out != sg_in) {
        probe_log("COPY sg_out != sg_in: copying %d entries (size=%zu)", nents_in,
                  (size_t)(nents_in * (sizeof(struct metal_sg))));
        memcpy(sg_out, sg_in, nents_in*(sizeof(struct metal_sg)));
    } else {
        probe_log("NO COPY sg_out == sg_in");
    }

    probe_log("EXIT: %s: returning %d", __func__, nents_in);
    return nents_in;
}
