#include <stdio.h>
#include <stdarg.h>
#include <string.h>

/* Probe helper: append human-readable logs to /tmp/logs/122.txt. */
static void probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/122.txt", "a");
    if (!f)
        return;
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fputc('\n', f);
    fclose(f);
}

static int metal_uio_dev_dma_map(struct linux_bus *lbus,
                                 struct linux_device *ldev,
                                 uint32_t dir,
                                 struct metal_sg *sg_in,
                                 int nents_in,
                                 struct metal_sg *sg_out)
{
    int i, j;
    void *vaddr_sg_lo, *vaddr_sg_hi, *vaddr_lo, *vaddr_hi;
    struct metal_io_region *io;

    (void)lbus;
    (void)dir;

    /* Log entry and the fact the (void)dir statement executed (shows dir is ignored) */
    probe_log("ENTRY: %s ldev=%p dev_name=%s dir=0x%08x nents_in=%d sg_in=%p sg_out=%p",
              __func__, (void *)ldev, (ldev ? ldev->dev_name : "(null)"), (unsigned)dir, nents_in, (void *)sg_in, (void *)sg_out);
    probe_log("INFO: (void)dir executed -> 'dir' parameter intentionally ignored (no side effects)");

    /* Check if the the input virt address is MMIO address */
    for (i = 0; i < nents_in; i++) {
        vaddr_sg_lo = sg_in[i].virt;
        vaddr_sg_hi = vaddr_sg_lo + sg_in[i].len;
        probe_log("CHECK_SG: index=%d virt=%p len=%d hi=%p", i, vaddr_sg_lo, sg_in[i].len, vaddr_sg_hi);
        for (j = 0, io = ldev->device.regions;
             j < (int)ldev->device.num_regions; j++, io++) {
            vaddr_lo = io->virt;
            vaddr_hi = vaddr_lo + io->size;
            probe_log("  REGION: idx=%d lo=%p hi=%p size=%zu", j, vaddr_lo, vaddr_hi, io->size);
            if (vaddr_sg_lo >= vaddr_lo &&
                vaddr_sg_hi <= vaddr_hi) {
                probe_log("  MATCH: sg[%d] is within region[%d]", i, j);
                break;
            }
        }
        if (j == (int)ldev->device.num_regions) {
            /* Log the exact failing SG entry to the file for reproducibility */
            probe_log("ERROR: input address isn't MMIO addr: sg[%d] virt=%p len=%d (no containing region found)",
                      i, vaddr_sg_lo, sg_in[i].len);

            /* Preserve original warning behavior */
            metal_log(METAL_LOG_WARNING,
                      "%s,%s: input address isn't MMIO addr: 0x%x,%d.\n",
                      __func__, ldev->dev_name, vaddr_sg_lo, sg_in[i].len);
            return -EINVAL;
        }
    }
    if (sg_out != sg_in) {
        probe_log("ACTION: copying sg_out from sg_in, nents_in=%d", nents_in);
        memcpy(sg_out, sg_in, nents_in * (sizeof(struct metal_sg)));
    } else {
        probe_log("ACTION: sg_out == sg_in, no memcpy performed");
    }
    probe_log("EXIT: %s returning %d", __func__, nents_in);
    return nents_in;
}
