static int metal_uio_dev_open(struct linux_bus *lbus, struct linux_device *ldev)
{
	char *instance, path[SYSFS_PATH_MAX];
	struct linux_driver *ldrv = ldev->ldrv;
	unsigned long *phys, offset = 0, size = 0;
	struct metal_io_region *io;
	struct dlist *dlist;
	int result, i;
	void *virt;
	int irq_info;

	/* Logging probe file handle - opened just before the for-loop to minimize
	   impact on earlier return paths. */
	FILE *logf = NULL;
	int _log_first = 1; /* helper to log initial values on first iteration */


	ldev->fd = -1;
	ldev->device.irq_info = (void *)-1;

	ldev->sdev = sysfs_open_device(lbus->bus_name, ldev->dev_name);
	if (!ldev->sdev) {
		metal_log(METAL_LOG_ERROR, "device %s:%s not found\n",
			  lbus->bus_name, ldev->dev_name);
		return -ENODEV;
	}
	metal_log(METAL_LOG_DEBUG, "opened sysfs device %s:%s\n",
		  lbus->bus_name, ldev->dev_name);

	result = metal_uio_dev_bind(ldev, ldrv);
	if (result)
		return result;

	result = snprintf(path, sizeof(path), "%s/uio", ldev->sdev->path);
	if (result >= (int)sizeof(path))
		return -EOVERFLOW;
	dlist = sysfs_open_directory_list(path);
	if (!dlist) {
		metal_log(METAL_LOG_ERROR, "failed to scan class path %s\n",
			  path);
		return -errno;
	}

	dlist_for_each_data(dlist, instance, char) {
		result = snprintf(ldev->cls_path, sizeof(ldev->cls_path),
				  "%s/%s", path, instance);
		if (result >= (int)sizeof(ldev->cls_path))
			return -EOVERFLOW;
		result = snprintf(ldev->dev_path, sizeof(ldev->dev_path),
				  "/dev/%s", instance);
		if (result >= (int)sizeof(ldev->dev_path))
			return -EOVERFLOW;
		break;
	}
	sysfs_close_list(dlist);

	if (sysfs_path_is_dir(ldev->cls_path) != 0) {
		metal_log(METAL_LOG_ERROR, "invalid device class path %s\n",
			  ldev->cls_path);
		return -ENODEV;
	}

	i = 0;
	do {
		if (!access(ldev->dev_path, F_OK))
			break;
		usleep(10);
		i++;
	} while (i < 1000);
	if (i >= 1000) {
		metal_log(METAL_LOG_ERROR, "failed to open file %s, timeout.\n",
			  ldev->dev_path);
		return -ENODEV;
	}
	result = metal_open(ldev->dev_path, 0);
	if (result < 0) {
		metal_log(METAL_LOG_ERROR, "failed to open device %s\n",
			  ldev->dev_path, strerror(-result));
		return result;
	}
	ldev->fd = result;

	metal_log(METAL_LOG_DEBUG, "opened %s:%s as %s\n",
		  lbus->bus_name, ldev->dev_name, ldev->dev_path);

	/* Open the log file here to capture the for-loop execution and internal
	   state relevant to the reported MISRA 14.2 violation. */
	logf = fopen("/tmp/logs/12.txt", "a");
	if (logf) {
		fprintf(logf, "metal_uio_dev_open: entering region scan for device %s:%s\n",
			ldev->sdev ? ldev->sdev->path : "(no sdev)", ldev->dev_name);
		fprintf(logf, "initial device.num_regions=%d\n", ldev->device.num_regions);
		fflush(logf);
	}

	for (i = 0, result = 0; !result && i < METAL_MAX_DEVICE_REGIONS; i++) {
		/* Log iteration start and initial header-assigned values on first loop
		   iteration. This helps reproduce the scenario where the for header
		   initializes objects other than the loop counter and where the loop
		   condition depends on an object modified in the body. */
		if (logf) {
			fprintf(logf, "loop iteration start: i=%d, result=%d, num_regions=%d\n",
				i, result, ldev->device.num_regions);
			fflush(logf);
		}

		phys = &ldev->region_phys[ldev->device.num_regions];
		result = (result ? result :
			 metal_uio_read_map_attr(ldev, i, "offset", &offset));
		if (logf) {
			fprintf(logf, "after read offset: i=%d, result=%d, offset=%lu\n",
				i, result, (unsigned long)offset);
			fflush(logf);
		}

		result = (result ? result :
			 metal_uio_read_map_attr(ldev, i, "addr", phys));
		if (logf) {
			fprintf(logf, "after read addr: i=%d, result=%d, phys=%lu\n",
				i, result, (unsigned long)(*phys));
			fflush(logf);
		}

		result = (result ? result :
			 metal_uio_read_map_attr(ldev, i, "size", &size));
		if (logf) {
			fprintf(logf, "after read size: i=%d, result=%d, size=%lu\n",
				i, result, (unsigned long)size);
			fflush(logf);
		}

		result = (result ? result :
			 metal_map(ldev->fd, i * getpagesize(), size, 0, 0, &virt));
		if (logf) {
			fprintf(logf, "after metal_map: i=%d, result=%d, virt=%p\n",
				i, result, virt);
			fflush(logf);
		}
		if (!result) {
			io = &ldev->device.regions[ldev->device.num_regions];
			if (logf) {
				fprintf(logf, "mapping succeeded: adding region idx=%d phys=%lu size=%lu virt=%p\n",
					ldev->device.num_regions, (unsigned long)(*phys), (unsigned long)size, virt);
				fflush(logf);
			}
			metal_io_init(io, virt, phys, size, -1, 0, NULL);
			ldev->device.num_regions++;
			if (logf) {
				fprintf(logf, "num_regions incremented to %d\n", ldev->device.num_regions);
				fflush(logf);
			}
		}
		/* mark that first-iteration header log has been emitted */
		if (_log_first) _log_first = 0;
	}

	if (logf) {
		fprintf(logf, "exiting region scan: final i=%d, result=%d, num_regions=%d\n",
			i, result, ldev->device.num_regions);
		fclose(logf);
		logf = NULL;
	}

	irq_info = 1;
	if (write(ldev->fd, &irq_info, sizeof(irq_info)) <= 0) {
		metal_log(METAL_LOG_INFO,
			  "%s: No IRQ for device %s.\n",
			  __func__, ldev->dev_name);
		ldev->device.irq_num =  0;
		ldev->device.irq_info = (void *)-1;
	} else {
		ldev->device.irq_num =  1;
		ldev->device.irq_info = (void *)(intptr_t)ldev->fd;
		metal_linux_irq_register_dev(&ldev->device, ldev->fd);
	}

	return 0;
}
