#include <stdio.h>
#include <stdint.h>
#include <stddef.h>
#include <string.h>

/* Probe logging helper. Appends human-readable traces to /tmp/logs/661.txt. */
static void mhd_log_md5(const char *label,
                        const struct Md5Ctx *ctx,
                        const uint8_t *data,
                        size_t length,
                        unsigned int bytes_have)
{
  FILE *f = fopen("/tmp/logs/661.txt", "a");
  if (f == NULL)
    return;

  fprintf(f, "%s: length=%zu, bytes_have=%u, ctx->count=%llu, ctx->buffer=%p, data=%p",
          label,
          length,
          bytes_have,
          (unsigned long long) ctx->count,
          (void *) ctx->buffer,
          (void *) data);
  if (length > 0 && data != NULL)
  {
    /* Print first up to 8 bytes for context. */
    unsigned int i, show = (length > 8u) ? 8u : (unsigned int) length;
    fprintf(f, ", first_bytes=0x");
    for (i = 0; i < show; ++i)
      fprintf(f, "%02x", (unsigned int) data[i]);
  }
  fprintf(f, "\n");
  fclose(f);
}

void
MHD_MD5_update (struct Md5Ctx *ctx,
                const uint8_t *data,
                size_t length)
{
  unsigned int bytes_have; /**< Number of bytes in the context buffer */

  mhd_assert ((data != NULL) || (length == 0));

  mhd_log_md5("ENTRY", ctx, data, length, 0);

#ifndef MHD_FAVOR_SMALL_CODE
  if (0 == length)
  {
    mhd_log_md5("EARLY_RETURN_ZERO_LENGTH", ctx, data, length, 0);
    return; /* Shortcut, do nothing */
  }
#endif /* MHD_FAVOR_SMALL_CODE */

  /* Note: (count & (MD5_BLOCK_SIZE-1))
           equals (count % MD5_BLOCK_SIZE) for this block size. */
  bytes_have = (unsigned int) (ctx->count & (MD5_BLOCK_SIZE - 1));
  ctx->count += length;

  mhd_log_md5("AFTER_COUNT_UPDATE", ctx, data, length, bytes_have);

  if (0 != bytes_have)
  {
    unsigned int bytes_left = MD5_BLOCK_SIZE - bytes_have;
    if (length >= bytes_left)
    {     /* Combine new data with data in the buffer and
             process the full block. */
      mhd_log_md5("BEFORE_COMBINE_WITH_BUFFER", ctx, data, length, bytes_have);

      memcpy (((uint8_t *) ctx->buffer) + bytes_have,
              data,
              bytes_left);

      data += bytes_left;            /* NOTE: modifies function parameter 'data' */
      length -= bytes_left;          /* NOTE: modifies function parameter 'length' */

      mhd_log_md5("AFTER_COMBINE_ADJUST_LENGTH", ctx, data, length, bytes_have);

      md5_transform (ctx->H, ctx->buffer);
      bytes_have = 0;

      mhd_log_md5("AFTER_COMBINE_TRANSFORM", ctx, data, length, bytes_have);
    }
  }

  mhd_log_md5("BEFORE_WHILE_LOOP", ctx, data, length, bytes_have);

  while (MD5_BLOCK_SIZE <= length)
  {   /* Process any full blocks of new data directly,
         without copying to the buffer. */
    mhd_log_md5("WHILE_ITER_START", ctx, data, length, bytes_have);

    md5_transform (ctx->H, data);
    data += MD5_BLOCK_SIZE;         /* NOTE: modifies function parameter 'data' */
    length -= MD5_BLOCK_SIZE;       /* NOTE: modifies function parameter 'length' */

    mhd_log_md5("WHILE_ITER_END", ctx, data, length, bytes_have);
  }

  if (0 != length)
  {   /* Copy incomplete block of new data (if any)
         to the buffer. */
    mhd_log_md5("BEFORE_COPY_REMAINDER", ctx, data, length, bytes_have);
    memcpy (((uint8_t *) ctx->buffer) + bytes_have, data, length);
    mhd_log_md5("AFTER_COPY_REMAINDER", ctx, data, length, bytes_have);
  }

  mhd_log_md5("EXIT", ctx, data, length, bytes_have);
}
