#include <stdio.h>
#include <stdarg.h>
#include <stddef.h>
#include <stdint.h>

static void mhd_probe_log(const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/661.txt", "a");
  if (f == NULL)
  {
    return;
  }
  va_list ap;
  va_start(ap, fmt);
  vfprintf(f, fmt, ap);
  fputc('\n', f);
  va_end(ap);
  fclose(f);
}

void
MHD_MD5_update (struct Md5Ctx *ctx,
                const uint8_t *data,
                size_t length)
{
  unsigned int bytes_have; /**< Number of bytes in the context buffer */

  mhd_assert ((data != NULL) || (length == 0));

  /* Log entry and initial state */
  mhd_probe_log("MHD_MD5_update ENTRY: ctx=%p data=%p length=%zu ctx->count=%zu",
                (void *)ctx, (const void *)data, length, (size_t)ctx->count);

#ifndef MHD_FAVOR_SMALL_CODE
  if (0 == length)
  {
    mhd_probe_log("MHD_MD5_update: length == 0, returning early");
    return; /* Shortcut, do nothing */
  }
#endif /* MHD_FAVOR_SMALL_CODE */

  /* Note: (count & (MD5_BLOCK_SIZE-1))
           equals (count % MD5_BLOCK_SIZE) for this block size. */
  bytes_have = (unsigned int) (ctx->count & (MD5_BLOCK_SIZE - 1));
  mhd_probe_log("bytes_have computed: %u", bytes_have);

  ctx->count += length;
  mhd_probe_log("ctx->count updated: %zu (added %zu)", (size_t)ctx->count, length);

  if (0 != bytes_have)
  {
    unsigned int bytes_left = MD5_BLOCK_SIZE - bytes_have;
    mhd_probe_log("buffer not empty: bytes_have=%u bytes_left=%u length=%zu", bytes_have, bytes_left, length);
    if (length >= bytes_left)
    {     /* Combine new data with data in the buffer and
             process the full block. */
      mhd_probe_log("branch: length (%zu) >= bytes_left (%u) -- about to memcpy, advance data and modify length", length, bytes_left);
      memcpy (((uint8_t *) ctx->buffer) + bytes_have,
              data,
              bytes_left);
      data += bytes_left;
      mhd_probe_log("after memcpy and data advance: data=%p (advanced by %u)", (const void *)data, bytes_left);

      /* Violation point: parameter 'length' is modified here in original code */
      mhd_probe_log("before modifying parameter 'length': length=%zu", length);
      length -= bytes_left;
      mhd_probe_log("after modifying parameter 'length': length=%zu", length);

      md5_transform (ctx->H, ctx->buffer);
      mhd_probe_log("md5_transform called on combined buffer");
      bytes_have = 0;
    }
    else
    {
      mhd_probe_log("not enough data to fill buffer: need %u more bytes, have %zu", bytes_left, length);
    }
  }

  while (MD5_BLOCK_SIZE <= length)
  {   /* Process any full blocks of new data directly,
         without copying to the buffer. */
    mhd_probe_log("while-loop: processing full block, length=%zu data=%p", length, (const void *)data);
    md5_transform (ctx->H, data);
    data += MD5_BLOCK_SIZE;
    length -= MD5_BLOCK_SIZE;
    mhd_probe_log("while-loop: after transform data=%p length=%zu", (const void *)data, length);
  }

  if (0 != length)
  {   /* Copy incomplete block of new data (if any)
         to the buffer. */
    mhd_probe_log("copying incomplete tail: bytes_have=%u length=%zu data=%p",
                  bytes_have, length, (const void *)data);
    memcpy (((uint8_t *) ctx->buffer) + bytes_have, data, length);
  }

  mhd_probe_log("MHD_MD5_update EXIT: ctx=%p final_length_%zu ctx->count=%zu",
                (void *)ctx, length, (size_t)ctx->count);
}
