#include <stdio.h>
#include <stdlib.h>
#include <stdarg.h>

static void mhd_probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/50.txt", "a");
    if (NULL == f)
        return;
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fclose(f);
}

_MHD_EXTERN enum MHD_Result
MHD_add_response_header (struct MHD_Response *response,
                         const char *header,
                         const char *content)
{
  mhd_probe_log("ENTRY: MHD_add_response_header response=%p header='%s' content='%s'\n",
                (void *)response,
                (header != NULL) ? header : "(null)",
                (content != NULL) ? content : "(null)");

  if (MHD_str_equal_caseless_ (header, MHD_HTTP_HEADER_CONNECTION)) {
    mhd_probe_log("BRANCH: CONNECTION -> calling add_response_header_connection\n");
    return add_response_header_connection (response, content);
  }

  if (MHD_str_equal_caseless_ (header,
                               MHD_HTTP_HEADER_TRANSFER_ENCODING))
  {
    mhd_probe_log("BRANCH: TRANSFER-ENCODING with content='%s' flags_auto=0x%zx flags=0x%zx\n",
                  (content != NULL) ? content : "(null)",
                  (size_t)response->flags_auto,
                  (size_t)response->flags);
    if (! MHD_str_equal_caseless_ (content, "chunked")) {
      mhd_probe_log("DECISION: transfer-encoding not 'chunked' -> MHD_NO\n");
      return MHD_NO;   /* Only "chunked" encoding is allowed */
    }
    if (0 != (response->flags_auto & MHD_RAF_HAS_TRANS_ENC_CHUNKED)) {
      mhd_probe_log("DECISION: already has chunked flag -> MHD_YES\n");
      return MHD_YES;  /* Already has "chunked" encoding header */
    }
    if ( (0 != (response->flags_auto & MHD_RAF_HAS_CONTENT_LENGTH)) &&
         (0 == (MHD_RF_INSANITY_HEADER_CONTENT_LENGTH & response->flags)) ) {
      mhd_probe_log("DECISION: has content-length and no insanity flag -> MHD_NO\n");
      return MHD_NO; /* Has "Content-Length" header and no "Insanity" flag */
    }
    if (MHD_NO != add_response_entry (response,
                                      MHD_HEADER_KIND,
                                      header,
                                      content))
    {
      response->flags_auto |= MHD_RAF_HAS_TRANS_ENC_CHUNKED;
      mhd_probe_log("ACTION: added transfer-encoding chunked -> set flag, return MHD_YES\n");
      return MHD_YES;
    }
    mhd_probe_log("ACTION: failed to add transfer-encoding entry -> MHD_NO\n");
    return MHD_NO;
  }
  if (MHD_str_equal_caseless_ (header,
                               MHD_HTTP_HEADER_DATE))
  {
    mhd_probe_log("BRANCH: DATE header handling flags_auto=0x%zx\n",
                  (size_t)response->flags_auto);
    if (0 != (response->flags_auto & MHD_RAF_HAS_DATE_HDR))
    {
      struct MHD_HTTP_Res_Header *hdr;
      hdr = MHD_get_response_element_n_ (response, MHD_HEADER_KIND,
                                         MHD_HTTP_HEADER_DATE,
                                         MHD_STATICSTR_LEN_ ( \
                                           MHD_HTTP_HEADER_DATE));
      mhd_probe_log("INFO: retrieved existing DATE hdr pointer=%p\n", (void *)hdr);
      /* Log whether the assert would have triggered (mhd_assert is a no-op) */
      if (NULL == hdr)
        mhd_probe_log("ASSERT_NOTICE: hdr == NULL (mhd_assert(NULL != hdr) is no-op)\n");
      else
        mhd_probe_log("ASSERT_NOTICE: hdr != NULL\n");

      mhd_assert (NULL != hdr);
      _MHD_remove_header (response, hdr);
      if (NULL != hdr->value)
        free (hdr->value);
      free (hdr->header);
      free (hdr);
      mhd_probe_log("ACTION: removed old DATE header and freed resources\n");
    }
    if (MHD_NO != add_response_entry (response,
                                      MHD_HEADER_KIND,
                                      header,
                                      content))
    {
      response->flags_auto |= MHD_RAF_HAS_DATE_HDR;
      mhd_probe_log("ACTION: added DATE header -> set flag, return MHD_YES\n");
      return MHD_YES;
    }
    mhd_probe_log("ACTION: failed to add DATE header -> MHD_NO\n");
    return MHD_NO;
  }

  if (MHD_str_equal_caseless_ (header,
                               MHD_HTTP_HEADER_CONTENT_LENGTH))
  {
    mhd_probe_log("BRANCH: CONTENT-LENGTH handling flags_auto=0x%zx flags=0x%zx\n",
                  (size_t)response->flags_auto,
                  (size_t)response->flags);
    /* Generally MHD sets automatically correct "Content-Length" always when
     * needed.
     * Custom "Content-Length" header is allowed only in special cases. */
    if ( (0 != (MHD_RF_INSANITY_HEADER_CONTENT_LENGTH & response->flags)) ||
         ((0 != (MHD_RF_HEAD_ONLY_RESPONSE & response->flags)) &&
          (0 == (response->flags_auto & (MHD_RAF_HAS_TRANS_ENC_CHUNKED
                                         | MHD_RAF_HAS_CONTENT_LENGTH)))) )
    {
      mhd_probe_log("DECISION: allowed to add custom Content-Length -> attempting add_response_entry\n");
      if (MHD_NO != add_response_entry (response,
                                        MHD_HEADER_KIND,
                                        header,
                                        content))
      {
        response->flags_auto |= MHD_RAF_HAS_CONTENT_LENGTH;
        mhd_probe_log("ACTION: added CONTENT-LENGTH -> set flag, return MHD_YES\n");
        return MHD_YES;
      }
      mhd_probe_log("ACTION: failed to add CONTENT-LENGTH entry\n");
    }
    mhd_probe_log("EXIT: CONTENT-LENGTH branch -> MHD_NO\n");
    return MHD_NO;
  }

  mhd_probe_log("FALLTHROUGH: default add_response_entry for header='%s'\n",
                (header != NULL) ? header : "(null)");
  return add_response_entry (response,
                             MHD_HEADER_KIND,
                             header,
                             content);
}
