#include <stdio.h>
#include <stdarg.h>

/* Probe helper: append logs to /tmp/logs/50.txt. Keeps original logic intact. */
static void probe_log (const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/50.txt", "a");
  if (NULL == f)
    return;
  va_list ap;
  va_start (ap, fmt);
  vfprintf (f, fmt, ap);
  fputc ('\n', f);
  va_end (ap);
  fclose (f);
}

_MHD_EXTERN enum MHD_Result
MHD_add_response_header (struct MHD_Response *response,
                         const char *header,
                         const char *content)
{
  /* Log entry and basic state */
  probe_log ("MHD_add_response_header entry: response=%p header='%s' content='%s' flags_auto=0x%X flags=0x%X",
             (void *) response,
             (NULL != header) ? header : "(null)",
             (NULL != content) ? content : "(null)",
             (unsigned int) ((NULL != response) ? response->flags_auto : 0U),
             (unsigned int) ((NULL != response) ? response->flags : 0U));

  if (MHD_str_equal_caseless_ (header, MHD_HTTP_HEADER_CONNECTION))
  {
    probe_log ("branch: CONNECTION header -> delegating to add_response_header_connection");
    return add_response_header_connection (response, content);
  }

  if (MHD_str_equal_caseless_ (header,
                               MHD_HTTP_HEADER_TRANSFER_ENCODING))
  {
    probe_log ("branch: TRANSFER-ENCODING header, content='%s'", (NULL != content) ? content : "(null)");
    if (! MHD_str_equal_caseless_ (content, "chunked"))
    {
      probe_log ("transfer-encoding: content is not 'chunked' -> MHD_NO");
      return MHD_NO;   /* Only "chunked" encoding is allowed */
    }
    if (0 != (response->flags_auto & MHD_RAF_HAS_TRANS_ENC_CHUNKED))
    {
      probe_log ("transfer-encoding: already has chunked -> MHD_YES");
      return MHD_YES;  /* Already has "chunked" encoding header */
    }
    if ( (0 != (response->flags_auto & MHD_RAF_HAS_CONTENT_LENGTH)) &&
         (0 == (MHD_RF_INSANITY_HEADER_CONTENT_LENGTH & response->flags)) )
    {
      probe_log ("transfer-encoding: has content-length without insanity flag -> MHD_NO");
      return MHD_NO; /* Has "Content-Length" header and no "Insanity" flag */
    }
    if (MHD_NO != add_response_entry (response,
                                      MHD_HEADER_KIND,
                                      header,
                                      content))
    {
      probe_log ("transfer-encoding: added entry, setting RAF_HAS_TRANS_ENC_CHUNKED");
      response->flags_auto |= MHD_RAF_HAS_TRANS_ENC_CHUNKED;
      return MHD_YES;
    }
    probe_log ("transfer-encoding: add_response_entry failed -> MHD_NO");
    return MHD_NO;
  }
  if (MHD_str_equal_caseless_ (header,
                               MHD_HTTP_HEADER_DATE))
  {
    probe_log ("branch: DATE header");
    if (0 != (response->flags_auto & MHD_RAF_HAS_DATE_HDR))
    {
      struct MHD_HTTP_Res_Header *hdr;
      probe_log ("date header: existing date header flag set, looking up current header element");
      hdr = MHD_get_response_element_n_ (response, MHD_HEADER_KIND,
                                         MHD_HTTP_HEADER_DATE,
                                         MHD_STATICSTR_LEN_ ( \
                                           MHD_HTTP_HEADER_DATE));
      probe_log ("date header: MHD_get_response_element_n_ returned hdr=%p", (void *) hdr);

      /* Evidence probes around the assertion site: log before and after the mhd_assert
         to demonstrate that the assert is a no-op in the configuration described. */
      probe_log ("date header: about to execute mhd_assert(NULL != hdr)");
      mhd_assert (NULL != hdr);
      probe_log ("date header: returned from mhd_assert(NULL != hdr) -- execution continued");

      /* Continue original behavior: remove and free existing header element */
      _MHD_remove_header (response, hdr);
      probe_log ("date header: removed header element %p", (void *) hdr);
      if (NULL != hdr->value)
      {
        probe_log ("date header: freeing hdr->value %p", (void *) hdr->value);
        free (hdr->value);
      }
      probe_log ("date header: freeing hdr->header %p and hdr %p", (void *) hdr->header, (void *) hdr);
      free (hdr->header);
      free (hdr);
    }
    if (MHD_NO != add_response_entry (response,
                                      MHD_HEADER_KIND,
                                      header,
                                      content))
    {
      probe_log ("date header: added new date entry, setting RAF_HAS_DATE_HDR");
      response->flags_auto |= MHD_RAF_HAS_DATE_HDR;
      return MHD_YES;
    }
    probe_log ("date header: add_response_entry failed -> MHD_NO");
    return MHD_NO;
  }

  if (MHD_str_equal_caseless_ (header,
                               MHD_HTTP_HEADER_CONTENT_LENGTH))
  {
    probe_log ("branch: CONTENT-LENGTH header");
    /* Generally MHD sets automatically correct "Content-Length" always when
     * needed.
     * Custom "Content-Length" header is allowed only in special cases. */
    if ( (0 != (MHD_RF_INSANITY_HEADER_CONTENT_LENGTH & response->flags)) ||
         ((0 != (MHD_RF_HEAD_ONLY_RESPONSE & response->flags)) &&
          (0 == (response->flags_auto & (MHD_RAF_HAS_TRANS_ENC_CHUNKED
                                         | MHD_RAF_HAS_CONTENT_LENGTH)))) )
    {
      probe_log ("content-length: permitted to add custom content-length, attempting add_response_entry");
      if (MHD_NO != add_response_entry (response,
                                        MHD_HEADER_KIND,
                                        header,
                                        content))
      {
        probe_log ("content-length: added entry, setting RAF_HAS_CONTENT_LENGTH");
        response->flags_auto |= MHD_RAF_HAS_CONTENT_LENGTH;
        return MHD_YES;
      }
      probe_log ("content-length: add_response_entry failed -> will return MHD_NO");
    }
    return MHD_NO;
  }

  probe_log ("default: delegating to add_response_entry for header='%s'", (NULL != header) ? header : "(null)");
  return add_response_entry (response,
                             MHD_HEADER_KIND,
                             header,
                             content);
}
