#include <stdio.h>
#include <stdarg.h>
#include <stdlib.h>

/* Probe logging helper: append human-readable logs to /tmp/logs/50.txt */
static void mhd_probe_log(const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/50.txt", "a");
  if (NULL == f)
    return;
  va_list ap;
  va_start(ap, fmt);
  vfprintf(f, fmt, ap);
  fprintf(f, "\n");
  va_end(ap);
  fclose(f);
}

_MHD_EXTERN enum MHD_Result
MHD_add_response_header (struct MHD_Response *response,
                         const char *header,
                         const char *content)
{
  mhd_probe_log("ENTER MHD_add_response_header: header='%s' content='%s' response=%p flags=0x%08x flags_auto=0x%08x",
                (header?header:"(null)"), (content?content:"(null)"), (void *)response,
                (unsigned int)response->flags, (unsigned int)response->flags_auto);

  if (MHD_str_equal_caseless_ (header, MHD_HTTP_HEADER_CONNECTION))
    return add_response_header_connection (response, content);

  if (MHD_str_equal_caseless_ (header,
                               MHD_HTTP_HEADER_TRANSFER_ENCODING))
  {
    mhd_probe_log("BRANCH: Transfer-Encoding -> content='%s'", (content?content:"(null)"));
    if (! MHD_str_equal_caseless_ (content, "chunked"))
    {
      mhd_probe_log("Transfer-Encoding rejected: not 'chunked'");
      return MHD_NO;   /* Only "chunked" encoding is allowed */
    }
    if (0 != (response->flags_auto & MHD_RAF_HAS_TRANS_ENC_CHUNKED))
    {
      mhd_probe_log("Transfer-Encoding already present (chunked)");
      return MHD_YES;  /* Already has "chunked" encoding header */
    }
    if ( (0 != (response->flags_auto & MHD_RAF_HAS_CONTENT_LENGTH)) &&
         (0 == (MHD_RF_INSANITY_HEADER_CONTENT_LENGTH & response->flags)) )
    {
      mhd_probe_log("Transfer-Encoding rejected: has Content-Length and no Insanity flag");
      return MHD_NO; /* Has "Content-Length" header and no "Insanity" flag */
    }
    if (MHD_NO != add_response_entry (response,
                                      MHD_HEADER_KIND,
                                      header,
                                      content))
    {
      response->flags_auto |= MHD_RAF_HAS_TRANS_ENC_CHUNKED;
      mhd_probe_log("Transfer-Encoding added: set RAF_HAS_TRANS_ENC_CHUNKED");
      return MHD_YES;
    }
    mhd_probe_log("Transfer-Encoding add_response_entry failed");
    return MHD_NO;
  }
  if (MHD_str_equal_caseless_ (header,
                               MHD_HTTP_HEADER_DATE))
  {
    mhd_probe_log("BRANCH: Date header processing. initial flags_auto=0x%08x",
                  (unsigned int)response->flags_auto);
    if (0 != (response->flags_auto & MHD_RAF_HAS_DATE_HDR))
    {
      struct MHD_HTTP_Res_Header *hdr;
      hdr = MHD_get_response_element_n_ (response, MHD_HEADER_KIND,
                                         MHD_HTTP_HEADER_DATE,
                                         MHD_STATICSTR_LEN_ ( \
                                           MHD_HTTP_HEADER_DATE));
      mhd_probe_log("After get_response_element_n_: hdr=%p", (void *)hdr);

      /* Original assertion left in place; log surrounding information so
         other agents can see the state that makes this assertion a no-op
         in configurations where mhd_assert is defined as ((void)0). */
      mhd_probe_log("About to execute mhd_assert(NULL != hdr);");
      mhd_assert (NULL != hdr);
      mhd_probe_log("After mhd_assert (note: if mhd_assert is a no-op this will not change behavior)");

      /* Preserve original behavior: remove header when hdr is present */
      if (hdr != NULL)
      {
        mhd_probe_log("Removing existing Date header: hdr=%p value=%p header=%p",
                      (void *)hdr, (void *)hdr->value, (void *)hdr->header);
        _MHD_remove_header (response, hdr);
        if (NULL != hdr->value)
        {
          mhd_probe_log("Freeing hdr->value at %p", (void *)hdr->value);
          free (hdr->value);
        }
        mhd_probe_log("Freeing hdr->header at %p and hdr at %p", (void *)hdr->header, (void *)hdr);
        free (hdr->header);
        free (hdr);
      }
      else
      {
        mhd_probe_log("hdr was NULL: nothing to remove");
      }
    }
    if (MHD_NO != add_response_entry (response,
                                      MHD_HEADER_KIND,
                                      header,
                                      content))
    {
      response->flags_auto |= MHD_RAF_HAS_DATE_HDR;
      mhd_probe_log("Date header added: set RAF_HAS_DATE_HDR. returning MHD_YES");
      return MHD_YES;
    }
    mhd_probe_log("Failed to add Date header: returning MHD_NO");
    return MHD_NO;
  }

  if (MHD_str_equal_caseless_ (header,
                               MHD_HTTP_HEADER_CONTENT_LENGTH))
  {
    mhd_probe_log("BRANCH: Content-Length header processing");
    /* Generally MHD sets automatically correct "Content-Length" always when
     * needed.
     * Custom "Content-Length" header is allowed only in special cases. */
    if ( (0 != (MHD_RF_INSANITY_HEADER_CONTENT_LENGTH & response->flags)) ||
         ((0 != (MHD_RF_HEAD_ONLY_RESPONSE & response->flags)) &&
          (0 == (response->flags_auto & (MHD_RAF_HAS_TRANS_ENC_CHUNKED
                                         | MHD_RAF_HAS_CONTENT_LENGTH)))) )
    {
      if (MHD_NO != add_response_entry (response,
                                        MHD_HEADER_KIND,
                                        header,
                                        content))
      {
        response->flags_auto |= MHD_RAF_HAS_CONTENT_LENGTH;
        mhd_probe_log("Content-Length added: set RAF_HAS_CONTENT_LENGTH");
        return MHD_YES;
      }
    }
    mhd_probe_log("Content-Length branch finished: returning MHD_NO");
    return MHD_NO;
  }

  mhd_probe_log("Default branch: calling add_response_entry for header='%s'", (header?header:"(null)"));
  return add_response_entry (response,
                             MHD_HEADER_KIND,
                             header,
                             content);
}
