#include <stdio.h>
#include <stdarg.h>

static void mhd_log_probe(const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/50.txt", "a");
  if (NULL == f)
    return;
  va_list ap;
  va_start(ap, fmt);
  vfprintf(f, fmt, ap);
  va_end(ap);
  fprintf(f, "\n");
  fclose(f);
}

_MHD_EXTERN enum MHD_Result
MHD_add_response_header (struct MHD_Response *response,
                         const char *header,
                         const char *content)
{
  mhd_log_probe("ENTER: MHD_add_response_header response=%p header='%s' content='%s'",
                (void *) response,
                (header != NULL) ? header : "(null)",
                (content != NULL) ? content : "(null)");

  if (MHD_str_equal_caseless_ (header, MHD_HTTP_HEADER_CONNECTION)) {
    mhd_log_probe("BRANCH: Connection header -> delegating to add_response_header_connection");
    return add_response_header_connection (response, content);
  }

  if (MHD_str_equal_caseless_ (header,
                               MHD_HTTP_HEADER_TRANSFER_ENCODING))
  {
    mhd_log_probe("BRANCH: Transfer-Encoding header with content='%s'",
                  (content != NULL) ? content : "(null)");
    if (! MHD_str_equal_caseless_ (content, "chunked")) {
      mhd_log_probe("REJECT: Transfer-Encoding not 'chunked'");
      return MHD_NO;   /* Only "chunked" encoding is allowed */
    }
    if (0 != (response->flags_auto & MHD_RAF_HAS_TRANS_ENC_CHUNKED)) {
      mhd_log_probe("ALREADY_HAS: Transfer-Encoding 'chunked' present -> return YES");
      return MHD_YES;  /* Already has "chunked" encoding header */
    }
    if ( (0 != (response->flags_auto & MHD_RAF_HAS_CONTENT_LENGTH)) &&
         (0 == (MHD_RF_INSANITY_HEADER_CONTENT_LENGTH & response->flags)) ) {
      mhd_log_probe("REJECT: Has Content-Length and no Insanity flag -> return NO");
      return MHD_NO; /* Has "Content-Length" header and no "Insanity" flag */
    }
    if (MHD_NO != add_response_entry (response,
                                      MHD_HEADER_KIND,
                                      header,
                                      content))
    {
      response->flags_auto |= MHD_RAF_HAS_TRANS_ENC_CHUNKED;
      mhd_log_probe("ADDED: Transfer-Encoding 'chunked' -> set flag and return YES");
      return MHD_YES;
    }
    mhd_log_probe("FAILED: add_response_entry failed for Transfer-Encoding -> return NO");
    return MHD_NO;
  }
  if (MHD_str_equal_caseless_ (header,
                               MHD_HTTP_HEADER_DATE))
  {
    mhd_log_probe("BRANCH: Date header processing start");
    if (0 != (response->flags_auto & MHD_RAF_HAS_DATE_HDR))
    {
      struct MHD_HTTP_Res_Header *hdr;
      mhd_log_probe("INFO: response already has Date header -> attempt to fetch existing header");
      hdr = MHD_get_response_element_n_ (response, MHD_HEADER_KIND,
                                         MHD_HTTP_HEADER_DATE,
                                         MHD_STATICSTR_LEN_ ( \
                                           MHD_HTTP_HEADER_DATE));
      mhd_log_probe("PROBE: fetched hdr pointer = %p", (void *) hdr);

      /* Log state before assertion to capture scenario where assertion is a no-op */
      mhd_log_probe("PROBE_STATE: response=%p flags=0x%08x flags_auto=0x%08x",
                    (void *) response,
                    (unsigned) response->flags,
                    (unsigned) response->flags_auto);

      /* Original assertion kept to preserve behavior; it may be a no-op in some builds */
      mhd_assert (NULL != hdr);

      /* Log whether hdr is NULL after assertion (useful to evidence assertions compiled out) */
      mhd_log_probe("POST_ASSERT: hdr is %s", (hdr == NULL) ? "NULL" : "non-NULL");

      /* The following operations are the original logic; we log just before each one. */
      mhd_log_probe("ACTION: calling _MHD_remove_header with hdr=%p", (void *) hdr);
      _MHD_remove_header (response, hdr);
      if (NULL != hdr->value)
        free (hdr->value);
      free (hdr->header);
      free (hdr);
      mhd_log_probe("ACTION: removed and freed previous Date header");
    }
    if (MHD_NO != add_response_entry (response,
                                      MHD_HEADER_KIND,
                                      header,
                                      content))
    {
      response->flags_auto |= MHD_RAF_HAS_DATE_HDR;
      mhd_log_probe("ADDED: Date header added -> set flag and return YES");
      return MHD_YES;
    }
    mhd_log_probe("FAILED: add_response_entry failed for Date header -> return NO");
    return MHD_NO;
  }

  if (MHD_str_equal_caseless_ (header,
                               MHD_HTTP_HEADER_CONTENT_LENGTH))
  {
    mhd_log_probe("BRANCH: Content-Length header processing start");
    /* Generally MHD sets automatically correct "Content-Length" always when
     * needed.
     * Custom "Content-Length" header is allowed only in special cases. */
    if ( (0 != (MHD_RF_INSANITY_HEADER_CONTENT_LENGTH & response->flags)) ||
         ((0 != (MHD_RF_HEAD_ONLY_RESPONSE & response->flags)) &&
          (0 == (response->flags_auto & (MHD_RAF_HAS_TRANS_ENC_CHUNKED
                                         | MHD_RAF_HAS_CONTENT_LENGTH)))) )
    {
      mhd_log_probe("INFO: conditions met for allowing custom Content-Length header");
      if (MHD_NO != add_response_entry (response,
                                        MHD_HEADER_KIND,
                                        header,
                                        content))
      {
        response->flags_auto |= MHD_RAF_HAS_CONTENT_LENGTH;
        mhd_log_probe("ADDED: Content-Length header added -> set flag and return YES");
        return MHD_YES;
      }
      mhd_log_probe("FAILED: add_response_entry failed for Content-Length -> will return NO");
    }
    return MHD_NO;
  }

  mhd_log_probe("FALLBACK: delegating to add_response_entry for header='%s'",
                (header != NULL) ? header : "(null)");
  return add_response_entry (response,
                             MHD_HEADER_KIND,
                             header,
                             content);
}
