#include <stdio.h>
#include <stdarg.h>
#include <stdlib.h>

/* Probe logger: append human readable traces to /tmp/logs/50.txt */
static void mhd_probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/50.txt", "a");
    if (NULL == f)
        return;
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fputs("\n", f);
    fclose(f);
}

_MHD_EXTERN enum MHD_Result
MHD_add_response_header (struct MHD_Response *response,
                         const char *header,
                         const char *content)
{
  /* Log entry and basic state */
  mhd_probe_log("ENTER: MHD_add_response_header header=%s content=%s response=%p flags_auto=%lu flags=%lu",
                (header ? header : "(null)"),
                (content ? content : "(null)"),
                (void *)response,
                (unsigned long) (response ? response->flags_auto : 0UL),
                (unsigned long) (response ? response->flags : 0UL));

  if (MHD_str_equal_caseless_ (header, MHD_HTTP_HEADER_CONNECTION))
  {
    mhd_probe_log("BRANCH: Connection header -> delegating to add_response_header_connection");
    enum MHD_Result _res = add_response_header_connection (response, content);
    mhd_probe_log("RETURN: add_response_header_connection returned %d", (int)_res);
    return _res;
  }

  if (MHD_str_equal_caseless_ (header,
                               MHD_HTTP_HEADER_TRANSFER_ENCODING))
  {
    mhd_probe_log("BRANCH: Transfer-Encoding header, content=%s", (content ? content : "(null)"));
    if (! MHD_str_equal_caseless_ (content, "chunked"))
    {
      mhd_probe_log("Transfer-Encoding rejected: not 'chunked'");
      return MHD_NO;   /* Only "chunked" encoding is allowed */
    }
    if (0 != (response->flags_auto & MHD_RAF_HAS_TRANS_ENC_CHUNKED))
    {
      mhd_probe_log("Transfer-Encoding already present (chunked)");
      return MHD_YES;  /* Already has "chunked" encoding header */
    }
    if ( (0 != (response->flags_auto & MHD_RAF_HAS_CONTENT_LENGTH)) &&
         (0 == (MHD_RF_INSANITY_HEADER_CONTENT_LENGTH & response->flags)) )
    {
      mhd_probe_log("Transfer-Encoding rejected: has Content-Length and no Insanity flag");
      return MHD_NO; /* Has "Content-Length" header and no "Insanity" flag */
    }
    if (MHD_NO != add_response_entry (response,
                                      MHD_HEADER_KIND,
                                      header,
                                      content))
    {
      response->flags_auto |= MHD_RAF_HAS_TRANS_ENC_CHUNKED;
      mhd_probe_log("Transfer-Encoding added successfully; updated flags_auto=%lu", (unsigned long)response->flags_auto);
      return MHD_YES;
    }
    mhd_probe_log("Transfer-Encoding: add_response_entry failed");
    return MHD_NO;
  }
  if (MHD_str_equal_caseless_ (header,
                               MHD_HTTP_HEADER_DATE))
  {
    mhd_probe_log("BRANCH: Date header");
    if (0 != (response->flags_auto & MHD_RAF_HAS_DATE_HDR))
    {
      struct MHD_HTTP_Res_Header *hdr;
      hdr = MHD_get_response_element_n_ (response, MHD_HEADER_KIND,
                                         MHD_HTTP_HEADER_DATE,
                                         MHD_STATICSTR_LEN_ ( \
                                           MHD_HTTP_HEADER_DATE));
      mhd_probe_log("Date header already present: hdr=%p", (void *)hdr);

      /* Preserve original assertion (may be a no-op depending on configuration) */
      mhd_assert (NULL != hdr);

      /* Log the state that would be checked by the assertion */
      if (NULL == hdr)
        mhd_probe_log("ASSERTION OBSERVED: hdr == NULL (mhd_assert would have been triggered if active)");
      else
        mhd_probe_log("ASSERTION OBSERVED: hdr != NULL");

      _MHD_remove_header (response, hdr);
      if (NULL != hdr->value)
        free (hdr->value);
      free (hdr->header);
      free (hdr);
      mhd_probe_log("Date header removed and memory freed");
    }
    if (MHD_NO != add_response_entry (response,
                                      MHD_HEADER_KIND,
                                      header,
                                      content))
    {
      response->flags_auto |= MHD_RAF_HAS_DATE_HDR;
      mhd_probe_log("Date header added; flags_auto=%lu", (unsigned long)response->flags_auto);
      return MHD_YES;
    }
    mhd_probe_log("Date header: add_response_entry failed");
    return MHD_NO;
  }

  if (MHD_str_equal_caseless_ (header,
                               MHD_HTTP_HEADER_CONTENT_LENGTH))
  {
    mhd_probe_log("BRANCH: Content-Length header");
    /* Generally MHD sets automatically correct "Content-Length" always when
     * needed.
     * Custom "Content-Length" header is allowed only in special cases. */
    if ( (0 != (MHD_RF_INSANITY_HEADER_CONTENT_LENGTH & response->flags)) ||
         ((0 != (MHD_RF_HEAD_ONLY_RESPONSE & response->flags)) &&
          (0 == (response->flags_auto & (MHD_RAF_HAS_TRANS_ENC_CHUNKED
                                         | MHD_RAF_HAS_CONTENT_LENGTH)))) )
    {
      if (MHD_NO != add_response_entry (response,
                                        MHD_HEADER_KIND,
                                        header,
                                        content))
      {
        response->flags_auto |= MHD_RAF_HAS_CONTENT_LENGTH;
        mhd_probe_log("Content-Length added; flags_auto=%lu", (unsigned long)response->flags_auto);
        return MHD_YES;
      }
      mhd_probe_log("Content-Length: add_response_entry failed");
    }
    mhd_probe_log("Content-Length branch falling through to return MHD_NO");
    return MHD_NO;
  }

  mhd_probe_log("BRANCH: Default -> delegating to add_response_entry");
  enum MHD_Result _res_final = add_response_entry (response,
                             MHD_HEADER_KIND,
                             header,
                             content);
  mhd_probe_log("RETURN: add_response_entry returned %d", (int)_res_final);
  return _res_final;
}
