#include <stdio.h>
#include <stdarg.h>

/* Probe logging helper: append logs to /tmp/logs/55.txt. */
static void mhd_log_probe(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/55.txt", "a");
    if (NULL == f)
        return;
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fflush(f);
    fclose(f);
}

_MHD_EXTERN enum MHD_Result
MHD_add_response_header (struct MHD_Response *response,
                         const char *header,
                         const char *content)
{
  /* Log entry with pointers to help reproduce cases where content is NULL
     or otherwise suspicious. Do NOT dereference 'content' here to avoid
     introducing any new undefined behaviour. */
  mhd_log_probe("MHD_add_response_header ENTRY: response=%p header=%p content=%p\n",
                (void *)response, (const void *)header, (const void *)content);

  /* Check "Connection" header path first (preserve original logic). */
  if (MHD_str_equal_caseless_ (header, MHD_HTTP_HEADER_CONNECTION)) {
    mhd_log_probe("MHD_add_response_header: header matches MHD_HTTP_HEADER_CONNECTION\n");
    if (NULL == content)
      mhd_log_probe("MHD_add_response_header: content == NULL -> will call add_response_header_connection with NULL value\n");
    else
      mhd_log_probe("MHD_add_response_header: content != NULL -> content pointer=%p (not dereferenced here)\n",
                    (const void *)content);

    enum MHD_Result res = add_response_header_connection (response, content);
    mhd_log_probe("MHD_add_response_header: add_response_header_connection returned %d\n", (int)res);
    return res;
  }

  if (MHD_str_equal_caseless_ (header,
                               MHD_HTTP_HEADER_TRANSFER_ENCODING))
  {
    mhd_log_probe("MHD_add_response_header: header matches TRANSFER-ENCODING; content=%p\n",
                  (const void *)content);
    if (! MHD_str_equal_caseless_ (content, "chunked")) {
      mhd_log_probe("MHD_add_response_header: TRANSFER-ENCODING content is not 'chunked' -> return MHD_NO\n");
      return MHD_NO;   /* Only "chunked" encoding is allowed */
    }
    if (0 != (response->flags_auto & MHD_RAF_HAS_TRANS_ENC_CHUNKED))
      return MHD_YES;  /* Already has "chunked" encoding header */
    if ( (0 != (response->flags_auto & MHD_RAF_HAS_CONTENT_LENGTH)) &&
         (0 == (MHD_RF_INSANITY_HEADER_CONTENT_LENGTH & response->flags)) )
      return MHD_NO; /* Has "Content-Length" header and no "Insanity" flag */
    if (MHD_NO != add_response_entry (response,
                                      MHD_HEADER_KIND,
                                      header,
                                      content))
    {
      response->flags_auto |= MHD_RAF_HAS_TRANS_ENC_CHUNKED;
      return MHD_YES;
    }
    return MHD_NO;
  }
  if (MHD_str_equal_caseless_ (header,
                               MHD_HTTP_HEADER_DATE))
  {
    mhd_log_probe("MHD_add_response_header: header matches DATE; content=%p\n",
                  (const void *)content);
    if (0 != (response->flags_auto & MHD_RAF_HAS_DATE_HDR))
    {
      struct MHD_HTTP_Res_Header *hdr;
      hdr = MHD_get_response_element_n_ (response, MHD_HEADER_KIND,
                                         MHD_HTTP_HEADER_DATE,
                                         MHD_STATICSTR_LEN_ ( \
                                           MHD_HTTP_HEADER_DATE));
      mhd_assert (NULL != hdr);
      _MHD_remove_header (response, hdr);
      if (NULL != hdr->value)
        free (hdr->value);
      free (hdr->header);
      free (hdr);
    }
    if (MHD_NO != add_response_entry (response,
                                      MHD_HEADER_KIND,
                                      header,
                                      content))
    {
      response->flags_auto |= MHD_RAF_HAS_DATE_HDR;
      return MHD_YES;
    }
    return MHD_NO;
  }

  if (MHD_str_equal_caseless_ (header,
                               MHD_HTTP_HEADER_CONTENT_LENGTH))
  {
    mhd_log_probe("MHD_add_response_header: header matches CONTENT-LENGTH; content=%p\n",
                  (const void *)content);
    /* Generally MHD sets automatically correct "Content-Length" always when
     * needed.
     * Custom "Content-Length" header is allowed only in special cases. */
    if ( (0 != (MHD_RF_INSANITY_HEADER_CONTENT_LENGTH & response->flags)) ||
         ((0 != (MHD_RF_HEAD_ONLY_RESPONSE & response->flags)) &&
          (0 == (response->flags_auto & (MHD_RAF_HAS_TRANS_ENC_CHUNKED
                                         | MHD_RAF_HAS_CONTENT_LENGTH)))) )
    {
      if (MHD_NO != add_response_entry (response,
                                        MHD_HEADER_KIND,
                                        header,
                                        content))
      {
        response->flags_auto |= MHD_RAF_HAS_CONTENT_LENGTH;
        return MHD_YES;
      }
    }
    return MHD_NO;
  }

  mhd_log_probe("MHD_add_response_header: falling through to add_response_entry; header=%p content=%p\n",
                (const void *)header, (const void *)content);

  return add_response_entry (response,
                             MHD_HEADER_KIND,
                             header,
                             content);
}
