#include <stdio.h>
#include <stdarg.h>

/* Probe logging helper: append messages to /tmp/logs/107.txt */
static void mhd_probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/107.txt", "a");
    if (NULL == f)
    {
        return;
    }
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fputc('\n', f);
    fclose(f);
}

_MHD_EXTERN int
MHD_get_connection_values (struct MHD_Connection *connection,
                           enum MHD_ValueKind kind,
                           MHD_KeyValueIterator iterator,
                           void *iterator_cls)
{
  int ret;
  struct MHD_HTTP_Req_Header *pos;

  mhd_probe_log("MHD_get_connection_values: entry connection=%p kind=%d iterator=%p iterator_cls=%p",
                (void *)connection, (int)kind, (void *)iterator, iterator_cls);

  if (NULL == connection)
  {
    mhd_probe_log("MHD_get_connection_values: connection is NULL, returning -1");
    return -1;
  }
  ret = 0;
  mhd_probe_log("MHD_get_connection_values: initial ret=%d", ret);

  for (pos = connection->rq.headers_received; NULL != pos; pos = pos->next)
  {
    mhd_probe_log("iterating header: pos=%p pos->next=%p pos->kind=0x%08x header=%s value=%s",
                  (void *)pos,
                  (void *)pos->next,
                  (unsigned)pos->kind,
                  (pos->header != NULL) ? pos->header : "(null)",
                  (pos->value != NULL) ? pos->value : "(null)");

    if (0 != (pos->kind & kind))
    {
      ret++;
      mhd_probe_log("MHD_get_connection_values: kind matched (pos->kind & kind) != 0, ret incremented to %d", ret);

      if (NULL != iterator)
      {
        int iterator_result = iterator (iterator_cls,
                                       pos->kind,
                                       pos->header,
                                       pos->value);
        mhd_probe_log("MHD_get_connection_values: iterator called, result=%d", iterator_result);
        if (MHD_NO == iterator_result)
        {
          mhd_probe_log("MHD_get_connection_values: iterator returned MHD_NO, returning ret=%d", ret);
          return ret;
        }
      }
    }
  }

  mhd_probe_log("MHD_get_connection_values: loop complete, returning ret=%d", ret);
  return ret;
}
