#include <stdio.h>

/* Probe function to log control flow and state to /tmp/logs/698.txt */
static void
mhd_probe_log(const char *stage, struct MHD_Daemon *daemon)
{
  FILE *f = fopen("/tmp/logs/698.txt", "a");
  if (f != NULL)
    {
      /* Print pointer values and a tag describing where we are. Avoid dereferencing daemon when NULL. */
      fprintf(f, "MHD_ip_count_unlock: %s: daemon=%p master=%p mutex_addr=%p\n",
              stage,
              (void *) daemon,
              (void *) (daemon ? daemon->master : NULL),
              (void *) (daemon ? &daemon->per_ip_connection_mutex : NULL));
      fclose(f);
    }
}

static void
MHD_ip_count_unlock (struct MHD_Daemon *daemon)
{
  /* Log entry and state before the assertion so we capture state even if the assertion
     results in termination in some builds. Keep the original mhd_assert call intact. */
  mhd_probe_log("enter", daemon);
  mhd_assert (NULL == daemon->master);
  mhd_probe_log("after_assert", daemon);
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
  mhd_probe_log("before_mutex_unlock", daemon);
  MHD_mutex_unlock_chk_ (&daemon->per_ip_connection_mutex);
  mhd_probe_log("after_mutex_unlock", daemon);
#else
  mhd_probe_log("no_threads", daemon);
  (void) daemon;
#endif
}
