#include <stdio.h>
#include <stdarg.h>
#include <time.h>
#include <stdbool.h>
#include <stdint.h>
#include <errno.h>
#include <signal.h>
#include <pthread.h>

/* Probe logging helper: writes timestamped messages to /tmp/logs/357.txt. */
static void probe_log (const char *tag, const char *fmt, ...)
{
  FILE *f = fopen ("/tmp/logs/357.txt", "a");
  if (NULL == f)
  {
    return;
  }

  time_t t = time (NULL);
  if ((time_t) -1 != t)
  {
    struct tm tm_buf;
    if (0 == localtime_r (&t, &tm_buf))
    {
      char ts[32];
      if (0 != strftime (ts, (sizeof ts), "%Y-%m-%d %H:%M:%S", &tm_buf))
      {
        (void) fprintf (f, "%s ", ts);
      }
    }
  }

  (void) fprintf (f, "[%s] ", (tag != NULL) ? tag : "LOG");

  va_list ap;
  va_start (ap, fmt);
  (void) vfprintf (f, fmt, ap);
  va_end (ap);

  (void) fprintf (f, "\n");
  (void) fclose (f);
}

static MHD_THRD_RTRN_TYPE_ MHD_THRD_CALL_SPEC_
MHD_polling_thread (void *cls)
{
  struct MHD_Daemon *daemon = cls;
#ifdef HAVE_PTHREAD_SIGMASK
  sigset_t s_mask;
  int err;
#endif /* HAVE_PTHREAD_SIGMASK */

  probe_log ("THREAD", "MHD_polling_thread start: daemon=%p", (void *) daemon);

  MHD_thread_handle_ID_set_current_thread_ID_ (&(daemon->tid));
#ifdef HAVE_PTHREAD_SIGMASK
  if ((0 == sigemptyset (&s_mask)) &&
      (0 == sigaddset (&s_mask, SIGPIPE)))
  {
    err = pthread_sigmask (SIG_BLOCK, &s_mask, NULL);
    probe_log ("SIGMASK", "pthread_sigmask returned err=%d", err);
  }
  else
    err = errno;
  if (0 == err)
  {
    daemon->sigpipe_blocked = true;
    probe_log ("SIGMASK", "SIGPIPE blocked on daemon thread, daemon=%p", (void *) daemon);
  }
#ifdef HAVE_MESSAGES
  else
    MHD_DLOG (daemon,
              _ ("Failed to block SIGPIPE on daemon thread: %s\n"),
              MHD_strerror_ (errno));
#endif /* HAVE_MESSAGES */
#endif /* HAVE_PTHREAD_SIGMASK */

  while (! daemon->shutdown)
  {
    probe_log ("LOOP", "enter loop: daemon=%p shutdown=%d", (void *) daemon, (int) daemon->shutdown);
#ifdef HAVE_POLL
    if (MHD_D_IS_USING_POLL_ (daemon))
    {
      probe_log ("CALL", "MHD_poll: daemon=%p block=%d", (void *) daemon, (int) MHD_YES);
      MHD_poll (daemon, MHD_YES);
      probe_log ("RETURN", "MHD_poll returned: daemon=%p", (void *) daemon);
    }
    else
#endif /* HAVE_POLL */
#ifdef EPOLL_SUPPORT
    if (MHD_D_IS_USING_EPOLL_ (daemon))
    {
      probe_log ("CALL", "MHD_epoll: daemon=%p timeout=%d", (void *) daemon, -1);
      MHD_epoll (daemon, -1);
      probe_log ("RETURN", "MHD_epoll returned: daemon=%p", (void *) daemon);
    }
    else
#endif
    {
      /* Record the call to MHD_select and the value passed for millisec. */
      probe_log ("CALL", "MHD_select: daemon=%p millisec=%d", (void *) daemon, -1);
      MHD_select (daemon, -1);
      probe_log ("RETURN", "MHD_select returned: daemon=%p", (void *) daemon);
    }

    MHD_cleanup_connections (daemon);
    probe_log ("CLEANUP", "MHD_cleanup_connections executed: daemon=%p", (void *) daemon);
  }

  /* Resume any pending for resume connections, join
   * all connection's threads (if any) and finally cleanup
   * everything. */
  if (0 != (MHD_TEST_ALLOW_SUSPEND_RESUME & daemon->options))
  {
    probe_log ("RESUME", "resuming suspended connections: daemon=%p", (void *) daemon);
    resume_suspended_connections (daemon);
  }
  probe_log ("CLOSE", "closing all connections: daemon=%p", (void *) daemon);
  close_all_connections (daemon);

  probe_log ("THREAD", "MHD_polling_thread exit: daemon=%p", (void *) daemon);

  return (MHD_THRD_RTRN_TYPE_) 0;
}
