#include <stdio.h>
#include <stdarg.h>
#include <stdint.h>

static void
mhd_log_probe (const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/10.txt", "a");
  if (NULL == f)
    return;
  va_list ap;
  va_start (ap, fmt);
  vfprintf (f, fmt, ap);
  va_end (ap);
  fputc('\n', f);
  fclose (f);
}

void *
MHD_pool_reallocate (struct MemoryPool *pool,
                     void *old,
                     size_t old_size,
                     size_t new_size)
{
  size_t asize;
  uint8_t *new_blc;

  /* Log entry (avoid dereferencing pool here in case of misuse) */
  mhd_log_probe ("MHD_pool_reallocate ENTRY: pool=%p old=%p old_size=%zu new_size=%zu",
                 (void *) pool, (void *) old, old_size, new_size);

  mhd_assert (pool->end >= pool->pos);
  mhd_assert (pool->size >= pool->end - pool->pos);
  mhd_assert (old != NULL || old_size == 0);
  mhd_assert (pool->size >= old_size);
  mhd_assert (pool->pos == ROUND_TO_ALIGN (pool->pos));
#if defined(MHD_ASAN_POISON_ACTIVE) && defined(HAVE___ASAN_REGION_IS_POISONED)
  mhd_assert (NULL == __asan_region_is_poisoned (old, old_size));
#endif /* MHD_ASAN_POISON_ACTIVE && HAVE___ASAN_REGION_IS_POISONED */

  if (NULL != old)
  {   /* Have previously allocated data */
    const size_t old_offset = mp_ptr_diff_ (old, pool->memory);
    const bool shrinking = (old_size > new_size);

    mhd_log_probe ("old != NULL: old=%p old_offset=%zu old_size=%zu new_size=%zu shrinking=%d pool->pos=%zu pool->end=%zu pool->size=%zu",
                   old, (size_t) old_offset, old_size, new_size, shrinking ? 1 : 0,
                   (size_t) pool->pos, (size_t) pool->end, (size_t) pool->size);

    mhd_assert (mp_ptr_le_ (pool->memory, old));
    /* (pool->memory + pool->size >= (uint8_t*) old + old_size) */
    mhd_assert ((pool->size - _MHD_RED_ZONE_SIZE) >= (old_offset + old_size));
    /* Blocks "from the end" must not be reallocated */
    /* (old_size == 0 || pool->memory + pool->pos > (uint8_t*) old) */
    mhd_assert ((old_size == 0) || \
                (pool->pos > old_offset));
    mhd_assert ((old_size == 0) || \
                ((pool->end - _MHD_RED_ZONE_SIZE) >= (old_offset + old_size)));
    /* Try resizing in-place */
    if (shrinking)
    {     /* Shrinking in-place, zero-out freed part */
      mhd_log_probe ("Shrinking in-place: zeroing memory at %p size=%zu",
                     (void *) ((uint8_t *) old + new_size), old_size - new_size);
      memset ((uint8_t *) old + new_size, 0, old_size - new_size);
      mhd_log_probe ("About to call _MHD_POISON_MEMORY at %p size=%zu (site executes even if macro is (void)0)",
                     (void *) ((uint8_t *) old + new_size), old_size - new_size);
      _MHD_POISON_MEMORY ((uint8_t *) old + new_size, old_size - new_size);
      mhd_log_probe ("Returned from _MHD_POISON_MEMORY (call site executed)");
    }
    if (pool->pos ==
        ROUND_TO_ALIGN_PLUS_RED_ZONE (old_offset + old_size))
    {     /* "old" block is the last allocated block */
      const size_t new_apos =
        ROUND_TO_ALIGN_PLUS_RED_ZONE (old_offset + new_size);
      mhd_log_probe ("old block is last allocated block: old_offset=%zu old_size=%zu new_size=%zu new_apos=%zu pool->end=%zu pool->pos=%zu",
                     old_offset, old_size, new_size, new_apos, (size_t) pool->end, (size_t) pool->pos);
      if (! shrinking)
      {                               /* Grow in-place, check for enough space. */
        if ( (new_apos > pool->end) ||
             (new_apos < pool->pos) ) /* Value wrap */
        {
          mhd_log_probe ("Grow in-place: not enough space or wrap detected: new_apos=%zu pool->end=%zu pool->pos=%zu -> returning NULL",
                         new_apos, (size_t) pool->end, (size_t) pool->pos);
          return NULL;                /* No space */
        }
      }
      /* Resized in-place */
      pool->pos = new_apos;
      mhd_log_probe ("Resized in-place: updated pool->pos=%zu; calling _MHD_UNPOISON_MEMORY on old=%p new_size=%zu",
                     (size_t) pool->pos, old, new_size);
      _MHD_UNPOISON_MEMORY (old, new_size);
      mhd_log_probe ("Returning old pointer after in-place resize: %p", old);
      return old;
    }
    if (shrinking)
    {
      mhd_log_probe ("Shrinking but not last block: returning old (freed part remains allocated): %p", old);
      return old;   /* Resized in-place, freed part remains allocated */
    }
  }
  /* Need to allocate new block */
  asize = ROUND_TO_ALIGN_PLUS_RED_ZONE (new_size);
  mhd_log_probe ("Need new block: computed asize=%zu (for new_size=%zu)", asize, new_size);
  if ( ( (0 == asize) &&
         (0 != new_size) ) || /* Value wrap, too large new_size. */
       (asize > pool->end - pool->pos) ) /* Not enough space */
  {
    mhd_log_probe ("Allocation failure: asize=%zu pool->end=%zu pool->pos=%zu -> returning NULL",
                   asize, (size_t) pool->end, (size_t) pool->pos);
    return NULL;
  }

  new_blc = pool->memory + pool->pos;
  pool->pos += asize;

  mhd_log_probe ("Allocated new block at %p, updated pool->pos=%zu (asize=%zu)",
                 new_blc, (size_t) pool->pos, asize);
  _MHD_UNPOISON_MEMORY (new_blc, new_size);
  mhd_log_probe ("Called _MHD_UNPOISON_MEMORY on new block %p size=%zu", new_blc, new_size);
  if (0 != old_size)
  {
    /* Move data to new block, old block remains allocated */
    mhd_log_probe ("Moving data: memcpy dest=%p src=%p size=%zu", new_blc, old, old_size);
    memcpy (new_blc, old, old_size);
    /* Zero-out old block */
    mhd_log_probe ("Zeroing old block at %p size=%zu", old, old_size);
    memset (old, 0, old_size);
    mhd_log_probe ("About to call _MHD_POISON_MEMORY on old=%p size=%zu (site executes even if macro is (void)0)", old, old_size);
    _MHD_POISON_MEMORY (old, old_size);
    mhd_log_probe ("Returned from _MHD_POISON_MEMORY (call site executed)");
  }
  mhd_log_probe ("Returning new block %p", new_blc);
  return new_blc;
}
