#include <stdio.h>
#include <stdarg.h>
#include <stdint.h>
#include <stddef.h>
#include <stdbool.h>
#include <string.h>

/* Probe logging helper: append formatted logs to /tmp/logs/10.txt */
static void
mhd_probe_log (const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/10.txt", "a");
  if (NULL == f)
    return;
  va_list ap;
  va_start (ap, fmt);
  vfprintf (f, fmt, ap);
  va_end (ap);
  fprintf (f, "\n");
  fclose (f);
}

/* Dump basic pool state and the values that the mhd_asserts would check. */
static void
mhd_probe_log_pool_state (struct MemoryPool *pool,
                          void *old,
                          size_t old_size,
                          size_t new_size)
{
  /* Print pointer and size fields */
  mhd_probe_log ("MHD_pool_reallocate ENTRY: pool=%p memory=%p pos=%zu end=%zu size=%zu old=%p old_size=%zu new_size=%zu",
                 (void *) pool, (void *) pool->memory, pool->pos, pool->end, pool->size, old, old_size, new_size);

  /* Evaluate and log asserted conditions explicitly so their values are visible even if mhd_assert is a no-op. */
  mhd_probe_log ("ASSERT: pool->end >= pool->pos => %s", (pool->end >= pool->pos) ? "true" : "false");
  mhd_probe_log ("ASSERT: pool->size >= pool->end - pool->pos => %s", (pool->size >= pool->end - pool->pos) ? "true" : "false");
  mhd_probe_log ("ASSERT: old != NULL || old_size == 0 => %s", ((old != NULL) || (old_size == 0)) ? "true" : "false");
  mhd_probe_log ("ASSERT: pool->size >= old_size => %s", (pool->size >= old_size) ? "true" : "false");
  /* pos alignment assertion is not evaluated here because ROUND_TO_ALIGN may be project-specific; log raw pos instead */
  mhd_probe_log ("INFO: pool->pos (raw) = %zu", pool->pos);
#ifdef MHD_ASAN_POISON_ACTIVE
#ifdef HAVE___ASAN_REGION_IS_POISONED
  /* If ASAN check available, log its result */
  mhd_probe_log ("ASAN: __asan_region_is_poisoned(old, old_size) => %p", __asan_region_is_poisoned (old, old_size));
#endif
#endif
}

/* The original function, with logging probes inserted but original logic preserved. */
void *
MHD_pool_reallocate (struct MemoryPool *pool,
                     void *old,
                     size_t old_size,
                     size_t new_size)
{
  size_t asize;
  uint8_t *new_blc;

  /* Log initial state and values that mhd_assert would check */
  mhd_probe_log_pool_state (pool, old, old_size, new_size);

  mhd_assert (pool->end >= pool->pos);
  mhd_assert (pool->size >= pool->end - pool->pos);
  mhd_assert (old != NULL || old_size == 0);
  mhd_assert (pool->size >= old_size);
  mhd_assert (pool->pos == ROUND_TO_ALIGN (pool->pos));
#if defined(MHD_ASAN_POISON_ACTIVE) && defined(HAVE___ASAN_REGION_IS_POISONED)
  mhd_assert (NULL == __asan_region_is_poisoned (old, old_size));
#endif /* MHD_ASAN_POISON_ACTIVE && HAVE___ASAN_REGION_IS_POISONED */

  if (NULL != old)
  {   /* Have previously allocated data */
    const size_t old_offset = mp_ptr_diff_ (old, pool->memory);
    const bool shrinking = (old_size > new_size);

    /* Log offset and shrinking decision */
    mhd_probe_log ("HAVE OLD: old_offset=%zu shrinking=%s", old_offset, shrinking ? "true" : "false");

    mhd_assert (mp_ptr_le_ (pool->memory, old));
    /* (pool->memory + pool->size >= (uint8_t*) old + old_size) */
    mhd_assert ((pool->size - _MHD_RED_ZONE_SIZE) >= (old_offset + old_size));
    /* Blocks "from the end" must not be reallocated */
    /* (old_size == 0 || pool->memory + pool->pos > (uint8_t*) old) */
    mhd_assert ((old_size == 0) || \
                (pool->pos > old_offset));
    mhd_assert ((old_size == 0) || \
                ((pool->end - _MHD_RED_ZONE_SIZE) >= (old_offset + old_size)));

    /* Try resizing in-place */
    if (shrinking)
    {     /* Shrinking in-place, zero-out freed part */
      mhd_probe_log ("SHRINK: zeroing from %p + %zu, len=%zu", old, new_size, old_size - new_size);
      memset ((uint8_t *) old + new_size, 0, old_size - new_size);
      _MHD_POISON_MEMORY ((uint8_t *) old + new_size, old_size - new_size);
    }
    if (pool->pos ==
        ROUND_TO_ALIGN_PLUS_RED_ZONE (old_offset + old_size))
    {     /* "old" block is the last allocated block */
      const size_t new_apos =
        ROUND_TO_ALIGN_PLUS_RED_ZONE (old_offset + new_size);
      mhd_probe_log ("LAST_BLOCK: old_offset=%zu old_size=%zu new_size=%zu new_apos=%zu pool->pos=%zu pool->end=%zu",
                     old_offset, old_size, new_size, new_apos, pool->pos, pool->end);
      if (! shrinking)
      {                               /* Grow in-place, check for enough space. */
        if ( (new_apos > pool->end) ||
             (new_apos < pool->pos) ) /* Value wrap */
        {
          mhd_probe_log ("NO_SPACE: cannot grow in-place (new_apos=%zu pool->end=%zu pool->pos=%zu)", new_apos, pool->end, pool->pos);
          return NULL;                /* No space */
        }
      }
      /* Resized in-place */
      pool->pos = new_apos;
      _MHD_UNPOISON_MEMORY (old, new_size);
      mhd_probe_log ("RESIZED_IN_PLACE: returning old=%p new_size=%zu pool->pos=%zu", old, new_size, pool->pos);
      return old;
    }
    if (shrinking)
    {
      mhd_probe_log ("SHRINK_NOT_LAST: resized in-place logically, old remains allocated, returning old=%p new_size=%zu", old, new_size);
      return old;   /* Resized in-place, freed part remains allocated */
    }
  }
  /* Need to allocate new block */
  asize = ROUND_TO_ALIGN_PLUS_RED_ZONE (new_size);
  if ( ( (0 == asize) &&
         (0 != new_size) ) || /* Value wrap, too large new_size. */
       (asize > pool->end - pool->pos) ) /* Not enough space */
  {
    mhd_probe_log ("ALLOC_FAIL: asize=%zu available=%zu (pool->end=%zu pool->pos=%zu) new_size=%zu", asize, pool->end - pool->pos, pool->end, pool->pos, new_size);
    return NULL;
  }

  new_blc = pool->memory + pool->pos;
  pool->pos += asize;

  _MHD_UNPOISON_MEMORY (new_blc, new_size);
  if (0 != old_size)
  {
    /* Move data to new block, old block remains allocated */
    mhd_probe_log ("COPYING: new_blc=%p old=%p old_size=%zu", new_blc, old, old_size);
    memcpy (new_blc, old, old_size);
    /* Zero-out old block */
    memset (old, 0, old_size);
    _MHD_POISON_MEMORY (old, old_size);
  }
  mhd_probe_log ("ALLOC_OK: returning new_blc=%p new_size=%zu pool->pos=%zu", new_blc, new_size, pool->pos);
  return new_blc;
}
