#include <stdio.h>
#include <stdarg.h>
#include <stdint.h>
#include <stddef.h>
#include <string.h>
#include <stdbool.h>

/* Helper logging routine that appends human-readable probes to /tmp/logs/10.txt */
static void mhd_log_probe(const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/10.txt", "a");
  if (NULL == f)
    return;
  va_list ap;
  va_start(ap, fmt);
  vfprintf(f, fmt, ap);
  va_end(ap);
  fputc('\n', f);
  fclose(f);
}

void *
MHD_pool_reallocate (struct MemoryPool *pool,
                     void *old,
                     size_t old_size,
                     size_t new_size)
{
  size_t asize;
  uint8_t *new_blc;

  mhd_assert (pool->end >= pool->pos);
  mhd_assert (pool->size >= pool->end - pool->pos);
  mhd_assert (old != NULL || old_size == 0);
  mhd_assert (pool->size >= old_size);
  mhd_assert (pool->pos == ROUND_TO_ALIGN (pool->pos));
#if defined(MHD_ASAN_POISON_ACTIVE) && defined(HAVE___ASAN_REGION_IS_POISONED)
  mhd_assert (NULL == __asan_region_is_poisoned (old, old_size));
#endif /* MHD_ASAN_POISON_ACTIVE && HAVE___ASAN_REGION_IS_POISONED */

  /* Entry probe */
  mhd_log_probe("MHD_pool_reallocate ENTRY: pool=%p memory=%p pos=%zu end=%zu size=%zu old=%p old_size=%zu new_size=%zu",
                (void *)pool, (void *)pool->memory, (size_t)pool->pos, (size_t)pool->end, (size_t)pool->size,
                old, (size_t)old_size, (size_t)new_size);

  if (NULL != old)
  {   /* Have previously allocated data */
    const size_t old_offset = mp_ptr_diff_ (old, pool->memory);
    const bool shrinking = (old_size > new_size);

    mhd_log_probe("  old block present: old_offset=%zu shrinking=%s",
                  old_offset, shrinking ? "true" : "false");

    mhd_assert (mp_ptr_le_ (pool->memory, old));
    /* (pool->memory + pool->size >= (uint8_t*) old + old_size) */
    mhd_assert ((pool->size - _MHD_RED_ZONE_SIZE) >= (old_offset + old_size));
    /* Blocks "from the end" must not be reallocated */
    /* (old_size == 0 || pool->memory + pool->pos > (uint8_t*) old) */
    mhd_assert ((old_size == 0) || \
                (pool->pos > old_offset));
    mhd_assert ((old_size == 0) || \
                ((pool->end - _MHD_RED_ZONE_SIZE) >= (old_offset + old_size)));
    /* Try resizing in-place */
    if (shrinking)
    {     /* Shrinking in-place, zero-out freed part */
      mhd_log_probe("  shrinking: zeroing memory at %p length=%zu",
                    (void *)((uint8_t *) old + new_size), (size_t)(old_size - new_size));
      memset ((uint8_t *) old + new_size, 0, old_size - new_size);

      mhd_log_probe("  calling _MHD_POISON_MEMORY on %p length=%zu (may be no-op depending on build)",
                    (void *)((uint8_t *) old + new_size), (size_t)(old_size - new_size));
      _MHD_POISON_MEMORY ((uint8_t *) old + new_size, old_size - new_size);
      mhd_log_probe("  returned from _MHD_POISON_MEMORY");
    }
    if (pool->pos ==
        ROUND_TO_ALIGN_PLUS_RED_ZONE (old_offset + old_size))
    {     /* "old" block is the last allocated block */
      const size_t new_apos =
        ROUND_TO_ALIGN_PLUS_RED_ZONE (old_offset + new_size);
      mhd_log_probe("  old is last allocated; old_apos=%zu new_apos=%zu pool->end=%zu pool->pos=%zu",
                    (size_t)ROUND_TO_ALIGN_PLUS_RED_ZONE(old_offset + old_size), (size_t)new_apos,
                    (size_t)pool->end, (size_t)pool->pos);
      if (! shrinking)
      {                               /* Grow in-place, check for enough space. */
        if ( (new_apos > pool->end) ||
             (new_apos < pool->pos) ) /* Value wrap */
        {
          mhd_log_probe("  grow in-place: insufficient space or wrap detected, returning NULL");
          return NULL;                /* No space */
        }
      }
      /* Resized in-place */
      pool->pos = new_apos;
      mhd_log_probe("  resized in-place: updated pool->pos=%zu", (size_t)pool->pos);
      _MHD_UNPOISON_MEMORY (old, new_size);
      mhd_log_probe("  called _MHD_UNPOISON_MEMORY on %p length=%zu", old, (size_t)new_size);
      return old;
    }
    if (shrinking)
    {
      mhd_log_probe("  shrinking but not last block: returning old (%p) - freed part remains allocated", old);
      return old;   /* Resized in-place, freed part remains allocated */
    }
  }
  /* Need to allocate new block */
  asize = ROUND_TO_ALIGN_PLUS_RED_ZONE (new_size);
  mhd_log_probe("  need new block: computed asize=%zu (for new_size=%zu)", (size_t)asize, (size_t)new_size);
  if ( ( (0 == asize) &&
         (0 != new_size) ) || /* Value wrap, too large new_size. */
       (asize > pool->end - pool->pos) ) /* Not enough space */
  {
    mhd_log_probe("  allocation check failed: asize=%zu pool->end=%zu pool->pos=%zu => returning NULL",
                  (size_t)asize, (size_t)pool->end, (size_t)pool->pos);
    return NULL;
  }

  new_blc = pool->memory + pool->pos;
  pool->pos += asize;

  mhd_log_probe("  allocated new block new_blc=%p new_pos=%zu", (void *)new_blc, (size_t)pool->pos);

  _MHD_UNPOISON_MEMORY (new_blc, new_size);
  mhd_log_probe("  called _MHD_UNPOISON_MEMORY on %p length=%zu", (void *)new_blc, (size_t)new_size);
  if (0 != old_size)
  {
    /* Move data to new block, old block remains allocated */
    mhd_log_probe("  copying old data from %p to %p length=%zu", old, (void *)new_blc, (size_t)old_size);
    memcpy (new_blc, old, old_size);
    /* Zero-out old block */
    memset (old, 0, old_size);
    mhd_log_probe("  zeroed old block at %p length=%zu and calling _MHD_POISON_MEMORY on it", old, (size_t)old_size);
    _MHD_POISON_MEMORY (old, old_size);
    mhd_log_probe("  returned from _MHD_POISON_MEMORY for old block");
  }
  mhd_log_probe("MHD_pool_reallocate EXIT: returning %p", (void *)new_blc);
  return new_blc;
}
