#include <stdio.h>
#include <stdint.h>
#include <stddef.h>
#include <stdbool.h>
#include <string.h>

/* Probe helper: append a single log entry describing the current state.
   This helper is intentionally small and does not alter program state. */
static void
mhd_probe_log(const char *stage,
              struct MemoryPool *pool,
              void *old,
              size_t old_size,
              size_t new_size,
              size_t old_offset,
              bool shrinking)
{
  FILE *f = fopen("/tmp/logs/10.txt", "a");
  if (NULL == f)
    return;

  /* Log relevant pointers and sizes. Use %p for pointers and %zu for size_t. */
  fprintf(f, "MHD_POOL_REALLOCATE: stage=%s pool=%p pos=%zu end=%zu pool_size=%zu old=%p old_size=%zu new_size=%zu old_offset=%zu shrinking=%d\n",
          stage,
          (void *)pool,
          (size_t)pool->pos,
          (size_t)pool->end,
          (size_t)pool->size,
          old,
          old_size,
          new_size,
          old_offset,
          shrinking ? 1 : 0);
  fclose(f);
}

void *
MHD_pool_reallocate (struct MemoryPool *pool,
                     void *old,
                     size_t old_size,
                     size_t new_size)
{
  size_t asize;
  uint8_t *new_blc;

  mhd_assert (pool->end >= pool->pos);
  mhd_assert (pool->size >= pool->end - pool->pos);
  mhd_assert (old != NULL || old_size == 0);
  mhd_assert (pool->size >= old_size);
  mhd_assert (pool->pos == ROUND_TO_ALIGN (pool->pos));
#if defined(MHD_ASAN_POISON_ACTIVE) && defined(HAVE___ASAN_REGION_IS_POISONED)
  mhd_assert (NULL == __asan_region_is_poisoned (old, old_size));
#endif /* MHD_ASAN_POISON_ACTIVE && HAVE___ASAN_REGION_IS_POISONED */

  /* Entry probe */
  mhd_probe_log("entry", pool, old, old_size, new_size, (size_t)0, false);

  if (NULL != old)
  {   /* Have previously allocated data */
    const size_t old_offset = mp_ptr_diff_ (old, pool->memory);
    const bool shrinking = (old_size > new_size);

    /* Probe after computing offset and shrinking flag */
    mhd_probe_log("have_old_computed", pool, old, old_size, new_size, old_offset, shrinking);

    mhd_assert (mp_ptr_le_ (pool->memory, old));
    /* (pool->memory + pool->size >= (uint8_t*) old + old_size) */
    mhd_assert ((pool->size - _MHD_RED_ZONE_SIZE) >= (old_offset + old_size));
    /* Blocks "from the end" must not be reallocated */
    /* (old_size == 0 || pool->memory + pool->pos > (uint8_t*) old) */
    mhd_assert ((old_size == 0) || \
                (pool->pos > old_offset));
    mhd_assert ((old_size == 0) || \
                ((pool->end - _MHD_RED_ZONE_SIZE) >= (old_offset + old_size)));
    /* Try resizing in-place */
    if (shrinking)
    {     /* Shrinking in-place, zero-out freed part */
      memset ((uint8_t *) old + new_size, 0, old_size - new_size);
      _MHD_POISON_MEMORY ((uint8_t *) old + new_size, old_size - new_size);

      /* Probe after shrinking in-place */
      mhd_probe_log("shrinking_zeroed", pool, old, old_size, new_size, old_offset, shrinking);
    }
    if (pool->pos ==
        ROUND_TO_ALIGN_PLUS_RED_ZONE (old_offset + old_size))
    {     /* "old" block is the last allocated block */
      const size_t new_apos =
        ROUND_TO_ALIGN_PLUS_RED_ZONE (old_offset + new_size);
      if (! shrinking)
      {                               /* Grow in-place, check for enough space. */
        if ( (new_apos > pool->end) ||
             (new_apos < pool->pos) ) /* Value wrap */
          return NULL;                /* No space */
      }
      /* Resized in-place */
      pool->pos = new_apos;

      /* Probe immediately before the call to _MHD_UNPOISON_MEMORY so logs
         show that the unpoison operation is reached in control flow. */
      mhd_probe_log("about_to_unpoison_inplace", pool, old, old_size, new_size, old_offset, shrinking);

      _MHD_UNPOISON_MEMORY (old, new_size);

      /* Probe immediately after the call as well to capture completion. */
      mhd_probe_log("after_unpoison_inplace", pool, old, old_size, new_size, old_offset, shrinking);

      return old;
    }
    if (shrinking)
      return old;   /* Resized in-place, freed part remains allocated */
  }
  /* Need to allocate new block */
  asize = ROUND_TO_ALIGN_PLUS_RED_ZONE (new_size);
  if ( ( (0 == asize) &&
         (0 != new_size) ) || /* Value wrap, too large new_size. */
       (asize > pool->end - pool->pos) ) /* Not enough space */
    return NULL;

  new_blc = pool->memory + pool->pos;
  pool->pos += asize;

  /* Probe before unpoisoning the newly allocated block */
  mhd_probe_log("about_to_unpoison_new", pool, new_blc, 0, new_size, (size_t)0, false);

  _MHD_UNPOISON_MEMORY (new_blc, new_size);

  /* Probe after unpoisoning the newly allocated block */
  mhd_probe_log("after_unpoison_new", pool, new_blc, 0, new_size, (size_t)0, false);

  if (0 != old_size)
  {
    /* Move data to new block, old block remains allocated */
    memcpy (new_blc, old, old_size);
    /* Zero-out old block */
    memset (old, 0, old_size);
    _MHD_POISON_MEMORY (old, old_size);

    /* Probe after moving and poisoning old */
    mhd_probe_log("moved_and_poisoned_old", pool, old, old_size, new_size, (size_t)0, false);
  }
  return new_blc;
}
