#include <stdio.h>
#include <stdint.h>
#include <stddef.h>
#include <string.h>
#include <stdbool.h>
#include <sys/stat.h>
#include <errno.h>

/* Probe helper: append concise state and control-path information to /tmp/logs/10.txt.
   This helper intentionally does not alter program logic; it only performs best-effort
   logging. */
static void
mhd_log_probe (const char *tag,
               struct MemoryPool *pool,
               void *old,
               size_t old_size,
               void *new_blc,
               size_t new_size)
{
  FILE *f = fopen("/tmp/logs/10.txt", "a");
  if (NULL == f)
  {
    /* Try to create parent directory and reopen; ignore failures silently. */
    (void) mkdir("/tmp/logs", 0755);
    f = fopen("/tmp/logs/10.txt", "a");
  }
  if (NULL != f)
  {
    /* Print tag, pointers and numeric state. Use %p for pointer values. */
    (void) fprintf (f,
                    "%s: pool=%p pos=%zu end=%zu size=%zu old=%p old_size=%zu new=%p new_size=%zu\n",
                    tag,
                    (void *) pool,
                    (NULL != pool) ? pool->pos : (size_t) 0,
                    (NULL != pool) ? pool->end : (size_t) 0,
                    (NULL != pool) ? pool->size : (size_t) 0,
                    old,
                    old_size,
                    new_blc,
                    new_size);
    (void) fclose (f);
  }
}

void *
MHD_pool_reallocate (struct MemoryPool *pool,
                     void *old,
                     size_t old_size,
                     size_t new_size)
{
  size_t asize;
  uint8_t *new_blc;

  /* Log entry to the function with initial state. */
  mhd_log_probe ("enter", pool, old, old_size, NULL, new_size);

  mhd_assert (pool->end >= pool->pos);
  mhd_assert (pool->size >= pool->end - pool->pos);
  mhd_assert (old != NULL || old_size == 0);
  mhd_assert (pool->size >= old_size);
  mhd_assert (pool->pos == ROUND_TO_ALIGN (pool->pos));
#if defined(MHD_ASAN_POISON_ACTIVE) && defined(HAVE___ASAN_REGION_IS_POISONED)
  mhd_assert (NULL == __asan_region_is_poisoned (old, old_size));
#endif /* MHD_ASAN_POISON_ACTIVE && HAVE___ASAN_REGION_IS_POISONED */

  if (NULL != old)
  {   /* Have previously allocated data */
    const size_t old_offset = mp_ptr_diff_ (old, pool->memory);
    const bool shrinking = (old_size > new_size);

    mhd_assert (mp_ptr_le_ (pool->memory, old));
    /* (pool->memory + pool->size >= (uint8_t*) old + old_size) */
    mhd_assert ((pool->size - _MHD_RED_ZONE_SIZE) >= (old_offset + old_size));
    /* Blocks "from the end" must not be reallocated */
    /* (old_size == 0 || pool->memory + pool->pos > (uint8_t*) old) */
    mhd_assert ((old_size == 0) || \
                (pool->pos > old_offset));
    mhd_assert ((old_size == 0) || \
                ((pool->end - _MHD_RED_ZONE_SIZE) >= (old_offset + old_size)));
    /* Try resizing in-place */
    if (shrinking)
    {     /* Shrinking in-place, zero-out freed part */
      memset ((uint8_t *) old + new_size, 0, old_size - new_size);
      _MHD_POISON_MEMORY ((uint8_t *) old + new_size, old_size - new_size);
      /* Log that shrinking occurred. */
      mhd_log_probe ("shrinking_in_place", pool, old, old_size, NULL, new_size);
    }
    if (pool->pos ==
        ROUND_TO_ALIGN_PLUS_RED_ZONE (old_offset + old_size))
    {     /* "old" block is the last allocated block */
      const size_t new_apos =
        ROUND_TO_ALIGN_PLUS_RED_ZONE (old_offset + new_size);
      if (! shrinking)
      {                               /* Grow in-place, check for enough space. */
        if ( (new_apos > pool->end) ||
             (new_apos < pool->pos) ) /* Value wrap */
          return NULL;                /* No space */
      }
      /* Resized in-place */
      pool->pos = new_apos;
      /* Log just before the UNPOISON call that is relevant to the violation scenario. */
      mhd_log_probe ("before_unpoison_old", pool, old, old_size, NULL, new_size);
      _MHD_UNPOISON_MEMORY (old, new_size);
      /* Log just after the UNPOISON call. If the macro expands to (void)0 the logs
         will still show that the code path executed; other agents can inspect these
         logs to corroborate that the call site was reached but may be a no-op. */
      mhd_log_probe ("after_unpoison_old", pool, old, old_size, NULL, new_size);
      return old;
    }
    if (shrinking)
      return old;   /* Resized in-place, freed part remains allocated */
  }
  /* Need to allocate new block */
  asize = ROUND_TO_ALIGN_PLUS_RED_ZONE (new_size);
  if ( ( (0 == asize) &&
         (0 != new_size) ) || /* Value wrap, too large new_size. */
       (asize > pool->end - pool->pos) ) /* Not enough space */
    return NULL;

  new_blc = pool->memory + pool->pos;
  pool->pos += asize;

  /* Log just before UNPOISONing the newly allocated block. */
  mhd_log_probe ("before_unpoison_new", pool, old, old_size, new_blc, new_size);
  _MHD_UNPOISON_MEMORY (new_blc, new_size);
  mhd_log_probe ("after_unpoison_new", pool, old, old_size, new_blc, new_size);

  if (0 != old_size)
  {
    /* Move data to new block, old block remains allocated */
    memcpy (new_blc, old, old_size);
    /* Zero-out old block */
    memset (old, 0, old_size);
    _MHD_POISON_MEMORY (old, old_size);
    mhd_log_probe ("moved_and_poisoned_old", pool, old, old_size, new_blc, new_size);
  }
  /* Log final return. */
  mhd_log_probe ("return_new", pool, old, old_size, new_blc, new_size);
  return new_blc;
}
