#include <stdio.h>
#include <stdint.h>
#include <stddef.h>
#include <string.h>
#include <stdarg.h>
#include <stdbool.h>

/* Probe helper: append logs to /tmp/logs/10.txt. */
static void mhd_probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/10.txt", "a");
    if (NULL == f)
        return;
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fprintf(f, "\n");
    fclose(f);
}

void *
MHD_pool_reallocate (struct MemoryPool *pool,
                     void *old,
                     size_t old_size,
                     size_t new_size)
{
  size_t asize;
  uint8_t *new_blc;

  /* Log entry and basic pool state. */
  mhd_probe_log("MHD_pool_reallocate ENTRY: pool=%p, old=%p, old_size=%zu, new_size=%zu",
                (void *)pool, old, old_size, new_size);
  if (pool != NULL) {
    mhd_probe_log(" pool->memory=%p, pool->pos=%zu, pool->end=%zu, pool->size=%zu",
                  (void *)pool->memory, (size_t)pool->pos, (size_t)pool->end, (size_t)pool->size);
  }

  mhd_assert (pool->end >= pool->pos);
  mhd_assert (pool->size >= pool->end - pool->pos);
  mhd_assert (old != NULL || old_size == 0);
  mhd_assert (pool->size >= old_size);
  mhd_assert (pool->pos == ROUND_TO_ALIGN (pool->pos));
#if defined(MHD_ASAN_POISON_ACTIVE) && defined(HAVE___ASAN_REGION_IS_POISONED)
  mhd_assert (NULL == __asan_region_is_poisoned (old, old_size));
#endif /* MHD_ASAN_POISON_ACTIVE && HAVE___ASAN_REGION_IS_POISONED */

  if (NULL != old)
  {   /* Have previously allocated data */
    const size_t old_offset = mp_ptr_diff_ (old, pool->memory);
    const bool shrinking = (old_size > new_size);

    /* Log evaluation of conditions relevant to the assertion/violation scenario. */
    mhd_probe_log(" IN-BLOCK: old_offset=%zu, shrinking=%s",
                  old_offset, shrinking ? "true" : "false");
    mhd_probe_log(" ASSERT-CHECKS: mp_ptr_le_(pool->memory, old)=%d",
                  (int) mp_ptr_le_ (pool->memory, old));
    mhd_probe_log(" ASSERT-CHECKS: (pool->size - _MHD_RED_ZONE_SIZE) >= (old_offset + old_size) = %d",
                  (int) ((pool->size - _MHD_RED_ZONE_SIZE) >= (old_offset + old_size)));
    /* Log the specific assertion that is reported as a no-op by the other agent. */
    mhd_probe_log(" ASSERT-EXPR: (old_size == 0) || (pool->pos > old_offset) evaluates to %d",
                  (int) (((old_size == 0) || (pool->pos > old_offset))));
    mhd_probe_log(" ASSERT-EXPR2: (old_size == 0) || ((pool->end - _MHD_RED_ZONE_SIZE) >= (old_offset + old_size)) evaluates to %d",
                  (int) (((old_size == 0) || ((pool->end - _MHD_RED_ZONE_SIZE) >= (old_offset + old_size)))));

    mhd_assert (mp_ptr_le_ (pool->memory, old));
    /* (pool->memory + pool->size >= (uint8_t*) old + old_size) */
    mhd_assert ((pool->size - _MHD_RED_ZONE_SIZE) >= (old_offset + old_size));
    /* Blocks "from the end" must not be reallocated */
    /* (old_size == 0 || pool->memory + pool->pos > (uint8_t*) old) */
    mhd_assert ((old_size == 0) || \
                (pool->pos > old_offset));
    mhd_assert ((old_size == 0) || \
                ((pool->end - _MHD_RED_ZONE_SIZE) >= (old_offset + old_size)));
    /* Try resizing in-place */
    if (shrinking)
    {     /* Shrinking in-place, zero-out freed part */
      mhd_probe_log(" SHRINKING: zeroing from %p length %zu", (uint8_t *) old + new_size, old_size - new_size);
      memset ((uint8_t *) old + new_size, 0, old_size - new_size);
      _MHD_POISON_MEMORY ((uint8_t *) old + new_size, old_size - new_size);
    }
    if (pool->pos ==
        ROUND_TO_ALIGN_PLUS_RED_ZONE (old_offset + old_size))
    {     /* "old" block is the last allocated block */
      const size_t new_apos =
        ROUND_TO_ALIGN_PLUS_RED_ZONE (old_offset + new_size);
      mhd_probe_log(" OLD-BLOCK-LAST: pool->pos matches end-of-old; new_apos=%zu", new_apos);
      if (! shrinking)
      {                               /* Grow in-place, check for enough space. */
        if ( (new_apos > pool->end) ||
             (new_apos < pool->pos) ) /* Value wrap */
        {
          mhd_probe_log(" GROW-IN-PLACE: insufficient space or wrap: new_apos=%zu, pool->end=%zu, pool->pos=%zu", new_apos, pool->end, pool->pos);
          return NULL;                /* No space */
        }
      }
      /* Resized in-place */
      pool->pos = new_apos;
      _MHD_UNPOISON_MEMORY (old, new_size);
      mhd_probe_log(" RESIZED-IN-PLACE: returning old=%p, new_size=%zu, pool->pos=%zu", old, new_size, pool->pos);
      return old;
    }
    if (shrinking)
    {
      mhd_probe_log(" SHRINKED-NOT-LAST: returning old=%p (freed part remains allocated)", old);
      return old;   /* Resized in-place, freed part remains allocated */
    }
  }
  /* Need to allocate new block */
  asize = ROUND_TO_ALIGN_PLUS_RED_ZONE (new_size);
  mhd_probe_log(" ALLOC-NEW: computed asize=%zu", asize);
  if ( ( (0 == asize) &&
         (0 != new_size) ) || /* Value wrap, too large new_size. */
       (asize > pool->end - pool->pos) ) /* Not enough space */
  {
    mhd_probe_log(" ALLOC-NEW-FAIL: asize=%zu, available=%zu", asize, pool->end - pool->pos);
    return NULL;
  }

  new_blc = pool->memory + pool->pos;
  pool->pos += asize;

  _MHD_UNPOISON_MEMORY (new_blc, new_size);
  if (0 != old_size)
  {
    /* Move data to new block, old block remains allocated */
    mhd_probe_log(" MOVE-OLD-TO-NEW: new_blc=%p, old=%p, old_size=%zu", new_blc, old, old_size);
    memcpy (new_blc, old, old_size);
    /* Zero-out old block */
    mhd_probe_log(" ZERO-OLD: old=%p, old_size=%zu", old, old_size);
    memset (old, 0, old_size);
    _MHD_POISON_MEMORY (old, old_size);
  }
  mhd_probe_log(" ALLOC-RETURN: new_blc=%p, new_size=%zu, pool->pos=%zu", new_blc, new_size, pool->pos);
  return new_blc;
}
