_MHD_EXTERN enum MHD_Result
MHD_queue_basic_auth_required_response3 (struct MHD_Connection *connection,
                                         const char *realm,
                                         int prefer_utf8,
                                         struct MHD_Response *response)
{
  /* Probe helper: write control-flow/state information to /tmp/logs/578.txt */
  static void mhd_probe_log(const char *location,
                            struct MHD_Connection *connection,
                            const char *realm,
                            size_t pos,
                            size_t suffix_len,
                            size_t h_maxlen,
                            size_t realm_len,
                            size_t realm_quoted_len,
                            const char *note)
  {
    FILE *f = fopen("/tmp/logs/578.txt", "a");
    if (NULL == f)
      return;
    fprintf(f, "[MHD_PROBE] %s\n", location);
    if (connection != NULL)
      fprintf(f, "  connection=%p daemon=%p\n", (void *)connection, (void *)connection->daemon);
    fprintf(f, "  realm=\"%s\"\n", realm ? realm : "(null)");
    fprintf(f, "  pos=%zu suffix_len=%zu h_maxlen=%zu realm_len=%zu realm_quoted_len=%zu\n",
            pos, suffix_len, h_maxlen, realm_len, realm_quoted_len);
    if (note)
      fprintf(f, "  note=%s\n", note);
    fprintf(f, "\n");
    fclose(f);
  }

  static const char prefix[] = "Basic realm=\"";
  static const char suff_charset[] = "\", charset=\"UTF-8\"";
  static const size_t prefix_len = MHD_STATICSTR_LEN_ (prefix);
  static const size_t suff_simple_len = MHD_STATICSTR_LEN_ ("\"");
  static const size_t suff_charset_len =
    MHD_STATICSTR_LEN_ (suff_charset);
  enum MHD_Result ret;
  char *h_str;
  size_t h_maxlen;
  size_t suffix_len;
  size_t realm_len;
  size_t realm_quoted_len;
  size_t pos;

  if (NULL == response)
    return MHD_NO;

  suffix_len = (0 == prefer_utf8) ? suff_simple_len : suff_charset_len;
  realm_len = strlen (realm);
  h_maxlen = prefix_len + realm_len * 2 + suffix_len;

  h_str = (char *) malloc (h_maxlen + 1);
  if (NULL == h_str)
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (connection->daemon,
              "Failed to allocate memory for Basic Authentication header.\n");
#endif /* HAVE_MESSAGES */
    mhd_probe_log("alloc_failed", connection, realm, 0u, suffix_len, h_maxlen, realm_len, 0u, "malloc returned NULL");
    return MHD_NO;
  }
  memcpy (h_str, prefix, prefix_len);
  pos = prefix_len;
  realm_quoted_len = MHD_str_quote (realm, realm_len, h_str + pos,
                                    h_maxlen - prefix_len - suffix_len);
  pos += realm_quoted_len;

  /* Probe immediately before the assert that is a no-op in this configuration */
  mhd_probe_log("before_assert_pos_suffix_hmax", connection, realm, pos, suffix_len, h_maxlen, realm_len, realm_quoted_len, "about to execute mhd_assert(pos + suffix_len <= h_maxlen)");
  mhd_assert (pos + suffix_len <= h_maxlen);
  mhd_probe_log("after_assert_pos_suffix_hmax", connection, realm, pos, suffix_len, h_maxlen, realm_len, realm_quoted_len, "executed mhd_assert(pos + suffix_len <= h_maxlen)");

  if (0 == prefer_utf8)
  {
    h_str[pos++] = '\"';
    h_str[pos++] = 0; /* Zero terminate the result */
    mhd_probe_log("before_assert_pos_le_hmax_plus1", connection, realm, pos, suffix_len, h_maxlen, realm_len, realm_quoted_len, "about to execute mhd_assert(pos <= h_maxlen + 1)");
    mhd_assert (pos <= h_maxlen + 1);
    mhd_probe_log("after_assert_pos_le_hmax_plus1", connection, realm, pos, suffix_len, h_maxlen, realm_len, realm_quoted_len, "executed mhd_assert(pos <= h_maxlen + 1)");
  }
  else
  {
    /* Copy with the final zero-termination */
    mhd_probe_log("before_assert_pos_suffcharset_hmax", connection, realm, pos, suffix_len, h_maxlen, realm_len, realm_quoted_len, "about to execute mhd_assert(pos + suff_charset_len <= h_maxlen)");
    mhd_assert (pos + suff_charset_len <= h_maxlen);
    mhd_probe_log("after_assert_pos_suffcharset_hmax", connection, realm, pos, suffix_len, h_maxlen, realm_len, realm_quoted_len, "executed mhd_assert(pos + suff_charset_len <= h_maxlen)");
    memcpy (h_str + pos, suff_charset, suff_charset_len + 1);
    mhd_probe_log("before_assert_zero_at_end", connection, realm, pos, suffix_len, h_maxlen, realm_len, realm_quoted_len, "about to execute mhd_assert(0 == h_str[pos + suff_charset_len])");
    mhd_assert (0 == h_str[pos + suff_charset_len]);
    mhd_probe_log("after_assert_zero_at_end", connection, realm, pos, suffix_len, h_maxlen, realm_len, realm_quoted_len, "executed mhd_assert(0 == h_str[pos + suff_charset_len])");
  }

  mhd_probe_log("before_add_response_header", connection, realm, pos, suffix_len, h_maxlen, realm_len, realm_quoted_len, "about to call MHD_add_response_header");
  ret = MHD_add_response_header (response,
                                 MHD_HTTP_HEADER_WWW_AUTHENTICATE,
                                 h_str);
  mhd_probe_log("after_add_response_header", connection, realm, pos, suffix_len, h_maxlen, realm_len, realm_quoted_len, ret == MHD_NO ? "MHD_add_response_header returned MHD_NO" : "MHD_add_response_header returned success");
  free (h_str);
  if (MHD_NO != ret)
  {
    ret = MHD_queue_response (connection,
                              MHD_HTTP_UNAUTHORIZED,
                              response);
    mhd_probe_log("after_queue_response", connection, realm, pos, suffix_len, h_maxlen, realm_len, realm_quoted_len, ret == MHD_NO ? "MHD_queue_response returned MHD_NO" : "MHD_queue_response returned success");
  }
  else
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (connection->daemon,
              _ ("Failed to add Basic Authentication header.\n"));
#endif /* HAVE_MESSAGES */
    mhd_probe_log("add_header_failed", connection, realm, pos, suffix_len, h_maxlen, realm_len, realm_quoted_len, "Failed to add header - will not queue response");
  }
  return ret;
}
