#include <stdio.h>
#include <stdarg.h>
#include <string.h>
#include <stdlib.h>

/* Probe helper: append human-readable logs to /tmp/logs/578.txt */
static void mhd_log_probe(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/578.txt", "a");
    if (NULL == f)
        return;
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fputc('\n', f);
    fclose(f);
}

_MHD_EXTERN enum MHD_Result
MHD_queue_basic_auth_required_response3 (struct MHD_Connection *connection,
                                         const char *realm,
                                         int prefer_utf8,
                                         struct MHD_Response *response)
{
  static const char prefix[] = "Basic realm=\"";
  static const char suff_charset[] = "\", charset=\"UTF-8\"";
  static const size_t prefix_len = MHD_STATICSTR_LEN_ (prefix);
  static const size_t suff_simple_len = MHD_STATICSTR_LEN_ ("\"");
  static const size_t suff_charset_len =
    MHD_STATICSTR_LEN_ (suff_charset);
  enum MHD_Result ret;
  char *h_str;
  size_t h_maxlen;
  size_t suffix_len;
  size_t realm_len;
  size_t realm_quoted_len;
  size_t pos;

  /* Log entry and input state */
  mhd_log_probe("enter MHD_queue_basic_auth_required_response3: connection=%p, realm=%p, prefer_utf8=%d, response=%p",
                (void *) connection, (void *) realm, prefer_utf8, (void *) response);

  if (NULL == response)
  {
    mhd_log_probe("response is NULL -> returning MHD_NO");
    return MHD_NO;
  }

  suffix_len = (0 == prefer_utf8) ? suff_simple_len : suff_charset_len;
  realm_len = strlen (realm);
  /* Log computed lengths */
  mhd_log_probe("computed suffix_len=%zu, realm_len=%zu", suffix_len, realm_len);

  h_maxlen = prefix_len + realm_len * 2 + suffix_len;
  mhd_log_probe("computed h_maxlen=%zu (prefix_len=%zu, realm_len*2=%zu, suffix_len=%zu)",
                h_maxlen, prefix_len, realm_len * 2, suffix_len);

  h_str = (char *) malloc (h_maxlen + 1);
  if (NULL == h_str)
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (connection->daemon,
              "Failed to allocate memory for Basic Authentication header.\n");
#endif /* HAVE_MESSAGES */
    mhd_log_probe("malloc failed for h_str (size=%zu) -> returning MHD_NO", h_maxlen + 1);
    return MHD_NO;
  }
  mhd_log_probe("malloc succeeded: h_str=%p (allocated %zu bytes)", (void *) h_str, h_maxlen + 1);

  memcpy (h_str, prefix, prefix_len);
  pos = prefix_len;
  mhd_log_probe("copied prefix, pos=%zu", pos);

  realm_quoted_len = MHD_str_quote (realm, realm_len, h_str + pos,
                                    h_maxlen - prefix_len - suffix_len);
  pos += realm_quoted_len;
  mhd_log_probe("realm quoted: realm_quoted_len=%zu, pos after quote=%zu", realm_quoted_len, pos);

  mhd_assert (pos + suffix_len <= h_maxlen);
  if (0 == prefer_utf8)
  {
    h_str[pos++] = '"';
    h_str[pos++] = 0; /* Zero terminate the result */
    mhd_log_probe("prefer_utf8==0: appended '"' and zero-terminated, pos=%zu", pos);
    mhd_assert (pos <= h_maxlen + 1);
  }
  else
  {
    /* Copy with the final zero-termination */
    mhd_assert (pos + suff_charset_len <= h_maxlen);
    memcpy (h_str + pos, suff_charset, suff_charset_len + 1);
    mhd_log_probe("prefer_utf8!=0: copied suff_charset (len=%zu) at pos=%zu", suff_charset_len, pos);
    mhd_assert (0 == h_str[pos + suff_charset_len]);
  }

  /* Log the candidate header string (truncated to avoid huge logs) */
  {
    size_t to_log = (h_maxlen > 1024) ? 1024 : h_maxlen;
    char tmpbuf[1025];
    memset(tmpbuf, 0, sizeof(tmpbuf));
    strncpy(tmpbuf, h_str, to_log);
    mhd_log_probe("prepared header (truncated to %zu bytes): %s", to_log, tmpbuf);
  }

  ret = MHD_add_response_header (response,
                                 MHD_HTTP_HEADER_WWW_AUTHENTICATE,
                                 h_str);
  mhd_log_probe("MHD_add_response_header returned %d", (int) ret);

  free (h_str);
  mhd_log_probe("freed h_str %p", (void *) h_str);

  if (MHD_NO != ret)
  {
    mhd_log_probe("ret != MHD_NO -> queuing response");
    ret = MHD_queue_response (connection,
                              MHD_HTTP_UNAUTHORIZED,
                              response);
    mhd_log_probe("MHD_queue_response returned %d", (int) ret);
  }
  else
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (connection->daemon,
              _ ("Failed to add Basic Authentication header.\n"));
#endif /* HAVE_MESSAGES */
    mhd_log_probe("MHD_add_response_header failed (ret==MHD_NO)");
  }

  mhd_log_probe("exit MHD_queue_basic_auth_required_response3 -> returning %d", (int) ret);
  return ret;
}
