#include <stdio.h>
#include <stdarg.h>
#include <stdlib.h>
#include <string.h>

/* Probe logging helper: appends logs to /tmp/logs/578.txt. */
static void MHD_probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/578.txt", "a");
    if (NULL == f)
        return;
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    fprintf(f, "\n");
    va_end(ap);
    fclose(f);
}

_MHD_EXTERN enum MHD_Result
MHD_queue_basic_auth_required_response3 (struct MHD_Connection *connection,
                                         const char *realm,
                                         int prefer_utf8,
                                         struct MHD_Response *response)
{
  static const char prefix[] = "Basic realm=\"";
  static const char suff_charset[] = "\", charset=\"UTF-8\"";
  static const size_t prefix_len = MHD_STATICSTR_LEN_ (prefix);
  static const size_t suff_simple_len = MHD_STATICSTR_LEN_ ("\"");
  static const size_t suff_charset_len =
    MHD_STATICSTR_LEN_ (suff_charset);
  enum MHD_Result ret;
  char *h_str;
  size_t h_maxlen;
  size_t suffix_len;
  size_t realm_len;
  size_t realm_quoted_len;
  size_t pos;

  /* Log entry and key parameter/state info */
  MHD_probe_log("MHD_queue_basic_auth_required_response3: entry: connection=%p, realm=%p, prefer_utf8=%d, response=%p",
                (void *) connection, (void *) realm, prefer_utf8, (void *) response);

  if (NULL == response)
  {
    MHD_probe_log("MHD_queue_basic_auth_required_response3: early return: response == NULL -> MHD_NO");
    return MHD_NO;
  }

  suffix_len = (0 == prefer_utf8) ? suff_simple_len : suff_charset_len;
  realm_len = strlen (realm);
  h_maxlen = prefix_len + realm_len * 2 + suffix_len;

  MHD_probe_log("Computed sizes: prefix_len=%zu, realm_len=%zu, suffix_len=%zu, h_maxlen=%zu",
                prefix_len, realm_len, suffix_len, h_maxlen);

  h_str = (char *) malloc (h_maxlen + 1);
  if (NULL == h_str)
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (connection->daemon,
              "Failed to allocate memory for Basic Authentication header.\n");
#endif /* HAVE_MESSAGES */
    MHD_probe_log("MHD_queue_basic_auth_required_response3: malloc failed (h_maxlen=%zu)", h_maxlen);
    return MHD_NO;
  }
  MHD_probe_log("malloc succeeded: h_str=%p", (void *) h_str);

  memcpy (h_str, prefix, prefix_len);
  pos = prefix_len;
  realm_quoted_len = MHD_str_quote (realm, realm_len, h_str + pos,
                                    h_maxlen - prefix_len - suffix_len);
  pos += realm_quoted_len;
  mhd_assert (pos + suffix_len <= h_maxlen);
  MHD_probe_log("After quoting realm: realm_quoted_len=%zu, pos=%zu", realm_quoted_len, pos);

  if (0 == prefer_utf8)
  {
    h_str[pos++] = '"';
    h_str[pos++] = 0; /* Zero terminate the result */
    mhd_assert (pos <= h_maxlen + 1);
    MHD_probe_log("prefer_utf8==0 path: terminated with \" and NUL at pos=%zu", pos - 1);
  }
  else
  {
    /* Copy with the final zero-termination */
    mhd_assert (pos + suff_charset_len <= h_maxlen);
    memcpy (h_str + pos, suff_charset, suff_charset_len + 1);
    mhd_assert (0 == h_str[pos + suff_charset_len]);
    MHD_probe_log("prefer_utf8!=0 path: copied suff_charset starting at pos=%zu", pos);
  }

  /* Log the header string that will be used (null-terminated)
     Protect against accidental huge output by limiting length in log. */
  if (h_str != NULL)
  {
    size_t log_len = strlen(h_str);
    if (log_len > 1024)
      log_len = 1024;
    /* Temporarily copy into a buffer to ensure safe formatting */
    char buf[1025];
    strncpy(buf, h_str, log_len);
    buf[log_len] = '\0';
    MHD_probe_log("Built header (len=%zu): %s", strlen(h_str), buf);
  }

  ret = MHD_add_response_header (response,
                                 MHD_HTTP_HEADER_WWW_AUTHENTICATE,
                                 h_str);
  MHD_probe_log("MHD_add_response_header returned: %d", (int) ret);

  free (h_str);
  MHD_probe_log("Freed h_str");

  if (MHD_NO != ret)
  {
    ret = MHD_queue_response (connection,
                              MHD_HTTP_UNAUTHORIZED,
                              response);
    MHD_probe_log("MHD_queue_response returned: %d", (int) ret);
  }
  else
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (connection->daemon,
              _ ("Failed to add Basic Authentication header.\n"));
#endif /* HAVE_MESSAGES */
    MHD_probe_log("Did not queue response because adding header failed");
  }
  MHD_probe_log("MHD_queue_basic_auth_required_response3: exit with ret=%d", (int) ret);
  return ret;
}
