_MHD_EXTERN enum MHD_Result
MHD_queue_basic_auth_required_response3 (struct MHD_Connection *connection,
                                         const char *realm,
                                         int prefer_utf8,
                                         struct MHD_Response *response)
{
  static const char prefix[] = "Basic realm=\"";
  static const char suff_charset[] = "\", charset=\"UTF-8\"";
  static const size_t prefix_len = MHD_STATICSTR_LEN_ (prefix);
  static const size_t suff_simple_len = MHD_STATICSTR_LEN_ ("\"");
  static const size_t suff_charset_len =
    MHD_STATICSTR_LEN_ (suff_charset);
  enum MHD_Result ret;
  char *h_str;
  size_t h_maxlen;
  size_t suffix_len;
  size_t realm_len;
  size_t realm_quoted_len;
  size_t pos;

  /* Probe logging: record entry and parameter state */
  {
    FILE *f = fopen("/tmp/logs/578.txt", "a");
    if (f)
    {
      fprintf (f, "MHD_queue_basic_auth_required_response3: entry\n");
      fprintf (f, "  connection=%p, realm=%p, prefer_utf8=%d, response=%p\n",
               (void *) connection, (const void *) realm, prefer_utf8,
               (void *) response);
      fclose (f);
    }
  }

  if (NULL == response)
  {
    FILE *f = fopen("/tmp/logs/578.txt", "a");
    if (f)
    {
      fprintf (f, "  early return: response == NULL -> MHD_NO\n");
      fclose (f);
    }
    return MHD_NO;
  }

  suffix_len = (0 == prefer_utf8) ? suff_simple_len : suff_charset_len;
  realm_len = strlen (realm);
  h_maxlen = prefix_len + realm_len * 2 + suffix_len;

  /* Log computed sizes */
  {
    FILE *f = fopen("/tmp/logs/578.txt", "a");
    if (f)
    {
      fprintf (f, "  computed sizes: prefix_len=%zu, realm_len=%zu, suffix_len=%zu, h_maxlen=%zu\n",
               prefix_len, realm_len, suffix_len, h_maxlen);
      if (realm)
        fprintf (f, "  realm (first 200 chars or less): %.*s\n", (int) (realm_len < 200 ? realm_len : 200), realm);
      fclose (f);
    }
  }

  h_str = (char *) malloc (h_maxlen + 1);
  if (NULL == h_str)
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (connection->daemon,
              "Failed to allocate memory for Basic Authentication header.\n");
#endif /* HAVE_MESSAGES */
    FILE *f = fopen("/tmp/logs/578.txt", "a");
    if (f)
    {
      fprintf (f, "  malloc failed for h_str (size %zu)\n", h_maxlen + 1);
      fclose (f);
    }
    return MHD_NO;
  }

  memcpy (h_str, prefix, prefix_len);
  pos = prefix_len;
  realm_quoted_len = MHD_str_quote (realm, realm_len, h_str + pos,
                                    h_maxlen - prefix_len - suffix_len);
  pos += realm_quoted_len;
  mhd_assert (pos + suffix_len <= h_maxlen);

  /* Log after quoting the realm */
  {
    FILE *f = fopen("/tmp/logs/578.txt", "a");
    if (f)
    {
      fprintf (f, "  after quote: realm_quoted_len=%zu, pos=%zu\n",
               realm_quoted_len, pos);
      fclose (f);
    }
  }

  if (0 == prefer_utf8)
  {
    h_str[pos++] = '"';
    h_str[pos++] = 0; /* Zero terminate the result */
    mhd_assert (pos <= h_maxlen + 1);
  }
  else
  {
    /* Copy with the final zero-termination */
    mhd_assert (pos + suff_charset_len <= h_maxlen);
    memcpy (h_str + pos, suff_charset, suff_charset_len + 1);
    mhd_assert (0 == h_str[pos + suff_charset_len]);
  }

  /* Log the constructed header value (truncated if very long) */
  {
    FILE *f = fopen("/tmp/logs/578.txt", "a");
    if (f)
    {
      size_t show = strlen (h_str);
      fprintf (f, "  constructed header (len=%zu): %.*s\n",
               show, (int) (show < 1024 ? show : 1024), h_str);
      fclose (f);
    }
  }

  ret = MHD_add_response_header (response,
                                 MHD_HTTP_HEADER_WWW_AUTHENTICATE,
                                 h_str);

  /* Log result of adding header */
  {
    FILE *f = fopen("/tmp/logs/578.txt", "a");
    if (f)
    {
      fprintf (f, "  MHD_add_response_header returned %d\n", (int) ret);
      fclose (f);
    }
  }

  free (h_str);

  /* Log that we've freed the temporary header buffer */
  {
    FILE *f = fopen("/tmp/logs/578.txt", "a");
    if (f)
    {
      fprintf (f, "  freed h_str\n");
      fclose (f);
    }
  }

  if (MHD_NO != ret)
  {
    ret = MHD_queue_response (connection,
                              MHD_HTTP_UNAUTHORIZED,
                              response);
    /* Log result of queueing response */
    {
      FILE *f = fopen("/tmp/logs/578.txt", "a");
      if (f)
      {
        fprintf (f, "  MHD_queue_response returned %d\n", (int) ret);
        fclose (f);
      }
    }
  }
  else
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (connection->daemon,
              _ ("Failed to add Basic Authentication header.\n"));
#endif /* HAVE_MESSAGES */
    {
      FILE *f = fopen("/tmp/logs/578.txt", "a");
      if (f)
      {
        fprintf (f, "  failed to add header, returning MHD_NO\n");
        fclose (f);
      }
    }
  }

  /* Log final return value */
  {
    FILE *f = fopen("/tmp/logs/578.txt", "a");
    if (f)
    {
      fprintf (f, "MHD_queue_basic_auth_required_response3: exit returning %d\n", (int) ret);
      fclose (f);
    }
  }

  return ret;
}
