#include <stdio.h>
#include <stdarg.h>
#include <string.h>
#include <stdlib.h>

/* Probe helper: append human-readable logs to /tmp/logs/578.txt */
static void mhd_log_probe (const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/578.txt", "a");
  if (NULL == f)
    return;
  va_list ap;
  va_start (ap, fmt);
  vfprintf (f, fmt, ap);
  va_end (ap);
  fputc('\n', f);
  fclose (f);
}

_MHD_EXTERN enum MHD_Result
MHD_queue_basic_auth_required_response3 (struct MHD_Connection *connection,
                                         const char *realm,
                                         int prefer_utf8,
                                         struct MHD_Response *response)
{
  static const char prefix[] = "Basic realm=\"";
  static const char suff_charset[] = "\", charset=\"UTF-8\"";
  static const size_t prefix_len = MHD_STATICSTR_LEN_ (prefix);
  static const size_t suff_simple_len = MHD_STATICSTR_LEN_ ("\"");
  static const size_t suff_charset_len =
    MHD_STATICSTR_LEN_ (suff_charset);
  enum MHD_Result ret;
  char *h_str;
  size_t h_maxlen;
  size_t suffix_len;
  size_t realm_len;
  size_t realm_quoted_len;
  size_t pos;

  /* Log function entry and inputs */
  mhd_log_probe ("ENTER: MHD_queue_basic_auth_required_response3: connection=%p realm=%s prefer_utf8=%d response=%p",
                 (void *) connection, (realm ? realm : "(null)"), prefer_utf8,
                 (void *) response);

  if (NULL == response)
  {
    mhd_log_probe ("EXIT: response is NULL -> returning MHD_NO");
    return MHD_NO;
  }

  suffix_len = (0 == prefer_utf8) ? suff_simple_len : suff_charset_len;
  realm_len = strlen (realm);
  h_maxlen = prefix_len + realm_len * 2 + suffix_len;

  mhd_log_probe ("computed lengths: prefix_len=%zu realm_len=%zu suffix_len=%zu h_maxlen=%zu",
                 prefix_len, realm_len, suffix_len, h_maxlen);

  h_str = (char *) malloc (h_maxlen + 1);
  if (NULL == h_str)
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (connection->daemon,
              "Failed to allocate memory for Basic Authentication header.\n");
#endif /* HAVE_MESSAGES */
    mhd_log_probe ("malloc failed for h_str (size=%zu) -> returning MHD_NO", h_maxlen + 1);
    return MHD_NO;
  }
  memcpy (h_str, prefix, prefix_len);
  pos = prefix_len;
  mhd_log_probe ("after prefix copy: pos=%zu prefix=(%.*s)", pos, (int) prefix_len, prefix);

  realm_quoted_len = MHD_str_quote (realm, realm_len, h_str + pos,
                                    h_maxlen - prefix_len - suffix_len);
  pos += realm_quoted_len;
  mhd_log_probe ("after quote: realm_quoted_len=%zu pos=%zu h_maxlen=%zu",
                 realm_quoted_len, pos, h_maxlen);

  mhd_assert (pos + suffix_len <= h_maxlen);
  if (0 == prefer_utf8)
  {
    mhd_log_probe ("branch: prefer_utf8 == 0 (simple suffix)");
    h_str[pos++] = '\"';
    h_str[pos++] = 0; /* Zero terminate the result */
    mhd_log_probe ("written simple suffix and nul-terminated: pos=%zu h_str=(%s)", pos, h_str);
    mhd_assert (pos <= h_maxlen + 1);
  }
  else
  {
    mhd_log_probe ("branch: prefer_utf8 != 0 (charset suffix)");
    /* Copy with the final zero-termination */
    mhd_assert (pos + suff_charset_len <= h_maxlen);
    memcpy (h_str + pos, suff_charset, suff_charset_len + 1);
    mhd_assert (0 == h_str[pos + suff_charset_len]);
    mhd_log_probe ("copied charset suffix: pos=%zu suff_charset=(%s) full_header=(%s)", pos, suff_charset, h_str);
  }

  ret = MHD_add_response_header (response,
                                 MHD_HTTP_HEADER_WWW_AUTHENTICATE,
                                 h_str);
  mhd_log_probe ("MHD_add_response_header returned %d", (int) ret);

  /* Log header text just before freeing to capture exact bytes used */
  mhd_log_probe ("header text (about to free): %s", h_str);

  free (h_str);
  if (MHD_NO != ret)
  {
    ret = MHD_queue_response (connection,
                              MHD_HTTP_UNAUTHORIZED,
                              response);
    mhd_log_probe ("MHD_queue_response returned %d", (int) ret);
  }
  else
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (connection->daemon,
              _ ("Failed to add Basic Authentication header.\n"));
#endif /* HAVE_MESSAGES */
    mhd_log_probe ("Failed to add Basic Authentication header; ret=MHD_NO");
  }

  mhd_log_probe ("EXIT: returning %d", (int) ret);
  return ret;
}
