#include <stdio.h>
#include <stdarg.h>
#include <time.h>

/* Probe logging helper - writes human-readable logs to /tmp/logs/132.txt */
static void
probe_log_132 (const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/132.txt", "a");
  if (NULL == f)
    return;
  time_t t = time(NULL);
  struct tm *tm = localtime(&t);
  if (tm != NULL)
  {
    char timestr[64];
    if (strftime(timestr, sizeof(timestr), "%Y-%m-%d %H:%M:%S", tm) > 0)
      fprintf(f, "[%s] ", timestr);
  }
  va_list ap;
  va_start(ap, fmt);
  vfprintf(f, fmt, ap);
  va_end(ap);
  fprintf(f, "\n");
  fclose(f);
}

static enum MHD_Result
MHD_select (struct MHD_Daemon *daemon,
            int32_t millisec)
{
  int num_ready;
  fd_set rs;
  fd_set ws;
  fd_set es;
  MHD_socket maxsock;
  struct timeval timeout;
  struct timeval *tv;
  int err_state;
  MHD_socket ls;

  probe_log_132("enter MHD_select: daemon=%p, millisec=%d, shutdown=%d",
                (void *) daemon,
                (int) millisec,
                (daemon ? (int) daemon->shutdown : -1));

  timeout.tv_sec = 0;
  timeout.tv_usec = 0;
  if (daemon->shutdown)
  {
    probe_log_132("early exit: daemon->shutdown true");
    return MHD_NO;
  }
  FD_ZERO (&rs);
  FD_ZERO (&ws);
  FD_ZERO (&es);
  maxsock = MHD_INVALID_SOCKET;
  err_state = MHD_NO;

  probe_log_132("after init: maxsock=%lld, err_state=%d",
                (long long) maxsock,
                err_state);

  if ( (0 != (daemon->options & MHD_TEST_ALLOW_SUSPEND_RESUME)) &&
       (MHD_NO != resume_suspended_connections (daemon)) &&
       (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon)) )
  {
    probe_log_132("suspend/resume branch taken: forcing millisec=0 (was=%d)",
                  (int) millisec);
    millisec = 0;
  }

  if (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon))
  {
    /* single-threaded, go over everything */
    probe_log_132("single-threaded mode: calling internal_get_fdset2");
    if (MHD_NO ==
        internal_get_fdset2 (daemon,
                             &rs,
                             &ws,
                             &es,
                             &maxsock,
                             (int) FD_SETSIZE))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Could not obtain daemon fdsets.\n"));
#endif
      probe_log_132("internal_get_fdset2 failed: setting err_state=MHD_YES");
      err_state = MHD_YES;
    }
    else
    {
      probe_log_132("internal_get_fdset2 succeeded: maxsock=%lld",
                    (long long) maxsock);
    }
  }
  else
  {
    bool itc_added;
    /* accept only, have one thread per connection */
    itc_added = false;
    probe_log_132("thread-per-connection mode: checking ITC and listen_fd");
    if (MHD_ITC_IS_VALID_ (daemon->itc))
    {
      itc_added = MHD_add_to_fd_set_ (MHD_itc_r_fd_ (daemon->itc),
                                      &rs,
                                      &maxsock,
                                      (int) FD_SETSIZE);
      if (! itc_added)
      {
#ifdef HAVE_MESSAGES
        MHD_DLOG (daemon, _ ("Could not add control inter-thread " \
                             "communication channel FD to fdset.\n"));
#endif
        probe_log_132("failed to add ITC fd to fdset: itc_added=false");
        err_state = MHD_YES;
      }
      else
      {
        probe_log_132("added ITC fd to fdset: itc_added=true, maxsock=%lld",
                      (long long) maxsock);
      }
    }
    if ( (MHD_INVALID_SOCKET != (ls = daemon->listen_fd)) &&
         (! daemon->was_quiesced) )
    {
      probe_log_132("listen_fd=%lld, was_quiesced=%d, connections=%d, connection_limit=%d, at_limit=%d",
                    (long long) ls,
                    (int) daemon->was_quiesced,
                    (int) daemon->connections,
                    (int) daemon->connection_limit,
                    (int) daemon->at_limit);
      /* Stop listening if we are at the configured connection limit */
      /* If we're at the connection limit, no point in really
         accepting new connections; however, make sure we do not miss
         the shutdown OR the termination of an existing connection; so
         only do this optimisation if we have a signaling ITC in
         place. */
      if (! itc_added ||
          ((daemon->connections < daemon->connection_limit) &&
           ! daemon->at_limit))
      {
        if (! MHD_add_to_fd_set_ (ls,
                                  &rs,
                                  &maxsock,
                                  (int) FD_SETSIZE))
        {
#ifdef HAVE_MESSAGES
          MHD_DLOG (daemon,
                    _ ("Could not add listen socket to fdset.\n"));
#endif
          probe_log_132("failed to add listen socket to fdset: err_state=MHD_YES");
          err_state = MHD_YES;
        }
        else
        {
          probe_log_132("added listen socket to fdset: listen_fd=%lld, maxsock=%lld",
                        (long long) ls,
                        (long long) maxsock);
        }
      }
      else
      {
        probe_log_132("skipped adding listen socket due to itc_added/limits");
      }
    }
  }

  probe_log_132("before select: err_state=%d, millisec=%d, maxsock=%lld",
                err_state,
                (int) millisec,
                (long long) maxsock);

  if (MHD_NO != err_state)
  {
    probe_log_132("err_state indicates error; forcing millisec=0 (was=%d)",
                  (int) millisec);
    millisec = 0;
  }
  if (0 == millisec)
  {
    timeout.tv_usec = 0;
    timeout.tv_sec = 0;
    tv = &timeout;
    probe_log_132("timeout forced to zero (tv=%p, sec=%ld, usec=%ld)",
                  (void *) tv,
                  (long) timeout.tv_sec,
                  (long) timeout.tv_usec);
  }
  else
  {
    uint64_t mhd_tmo;
    uint64_t select_tmo;

    if ( (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon)) &&
         (MHD_NO != MHD_get_timeout64 (daemon, &mhd_tmo)) )
    {
      if ( (0 < millisec) &&
           (mhd_tmo > (uint64_t) millisec) )
        select_tmo = (uint64_t) millisec;
      else
        select_tmo = mhd_tmo;
      tv = &timeout; /* have timeout value */
      probe_log_132("computed select_tmo from mhd_tmo=%llu and millisec=%d => select_tmo=%llu",
                    (unsigned long long) mhd_tmo,
                    (int) millisec,
                    (unsigned long long) select_tmo);
    }
    else if (0 < millisec)
    {
      select_tmo = (uint64_t) millisec;
      tv = &timeout; /* have timeout value */
      probe_log_132("using millisec as select_tmo=%llu", (unsigned long long) select_tmo);
    }
    else
    {
      select_tmo = 0; /* Not actually used, silent compiler warning */
      tv = NULL;
      probe_log_132("no timeout (tv=NULL)");
    }

    if (NULL != tv)
    { /* have timeout value */
#if (SIZEOF_UINT64_T - 2) >= SIZEOF_STRUCT_TIMEVAL_TV_SEC
      if (select_tmo / 1000 > TIMEVAL_TV_SEC_MAX)
        timeout.tv_sec = TIMEVAL_TV_SEC_MAX;
      else
#endif /* (SIZEOF_UINT64_T - 2) >= SIZEOF_STRUCT_TIMEVAL_TV_SEC */
      timeout.tv_sec = (_MHD_TIMEVAL_TV_SEC_TYPE) (select_tmo / 1000);

      timeout.tv_usec = ((uint16_t) (select_tmo % 1000)) * ((int32_t) 1000);
      probe_log_132("timeout set: sec=%ld, usec=%ld",
                    (long) timeout.tv_sec,
                    (long) timeout.tv_usec);
    }
  }
  num_ready = MHD_SYS_select_ (maxsock + 1,
                               &rs,
                               &ws,
                               &es,
                               tv);

  probe_log_132("after select: num_ready=%d, daemon->shutdown=%d",
                num_ready,
                (daemon ? (int) daemon->shutdown : -1));

  if (daemon->shutdown)
  {
    probe_log_132("exit after select: daemon->shutdown true");
    return MHD_NO;
  }
  if (num_ready < 0)
  {
    const int err = MHD_socket_get_error_ ();
    probe_log_132("select returned error: err=%d", err);
    if (MHD_SCKT_ERR_IS_EINTR_ (err))
    {
      probe_log_132("select interrupted by signal (EINTR): returning %s",
                    (MHD_NO == err_state) ? "MHD_YES" : "MHD_NO");
      return (MHD_NO == err_state) ? MHD_YES : MHD_NO;
    }
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("select failed: %s\n"),
              MHD_socket_strerr_ (err));
#endif
    probe_log_132("select failed and not EINTR: returning MHD_NO");
    return MHD_NO;
  }
  probe_log_132("calling internal_run_from_select with FD_SETSIZE=%d", (int) FD_SETSIZE);
  if (MHD_NO != internal_run_from_select (daemon,
                                          &rs,
                                          &ws,
                                          &es,
                                          (int) FD_SETSIZE))
  {
    probe_log_132("internal_run_from_select returned non-MHD_NO: returning %s",
                  (MHD_NO == err_state) ? "MHD_YES" : "MHD_NO");
    return (MHD_NO == err_state) ? MHD_YES : MHD_NO;
  }
  probe_log_132("internal_run_from_select returned MHD_NO: returning MHD_NO");
  return MHD_NO;
}
