#include <stdio.h>
#include <stdarg.h>

/* Probe logging helper: appends human-readable logs to /tmp/logs/133.txt */
static void
mhd_probe_log (const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/133.txt", "a");
  if (NULL == f)
    return;
  va_list ap;
  va_start (ap, fmt);
  vfprintf (f, fmt, ap);
  va_end (ap);
  fprintf (f, "\n");
  fclose (f);
}

static enum MHD_Result
MHD_select (struct MHD_Daemon *daemon,
            int32_t millisec)
{
  int num_ready;
  fd_set rs;
  fd_set ws;
  fd_set es;
  MHD_socket maxsock;
  struct timeval timeout;
  struct timeval *tv;
  int err_state;
  MHD_socket ls;

  mhd_probe_log ("MHD_select: entry: daemon=%p, millisec=%d, shutdown=%d",
                 (void *) daemon,
                 (int) millisec,
                 (int) (daemon ? daemon->shutdown : 0));

  timeout.tv_sec = 0;
  timeout.tv_usec = 0;
  if (daemon->shutdown)
  {
    mhd_probe_log ("MHD_select: early return due to shutdown");
    return MHD_NO;
  }
  FD_ZERO (&rs);
  FD_ZERO (&ws);
  FD_ZERO (&es);
  maxsock = MHD_INVALID_SOCKET;
  err_state = MHD_NO;
  if ( (0 != (daemon->options & MHD_TEST_ALLOW_SUSPEND_RESUME)) &&
       (MHD_NO != resume_suspended_connections (daemon)) &&
       (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon)) )
  {
    mhd_probe_log ("MHD_select: resume_suspended_connections triggered, millisec before=%d", (int) millisec);
    millisec = 0;
    mhd_probe_log ("MHD_select: millisec forced to 0 due to suspend/resume path");
  }

  if (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon))
  {
    /* single-threaded, go over everything */
    mhd_probe_log ("MHD_select: single-threaded path: calling internal_get_fdset2 with fd_setsize=%d", (int) FD_SETSIZE);
    if (MHD_NO ==
        internal_get_fdset2 (daemon,
                             &rs,
                             &ws,
                             &es,
                             &maxsock,
                             (int) FD_SETSIZE))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Could not obtain daemon fdsets.\n"));
#endif
      mhd_probe_log ("MHD_select: internal_get_fdset2 failed; setting err_state=MHD_YES");
      err_state = MHD_YES;
    }
    else
    {
      mhd_probe_log ("MHD_select: internal_get_fdset2 succeeded; maxsock=%lld",
                     (long long) maxsock);
    }
  }
  else
  {
    bool itc_added;
    /* accept only, have one thread per connection */
    itc_added = false;
    if (MHD_ITC_IS_VALID_ (daemon->itc))
    {
      mhd_probe_log ("MHD_select: thread-per-conn path: adding ITC fd to set with fd_setsize=%d",
                     (int) FD_SETSIZE);
      itc_added = MHD_add_to_fd_set_ (MHD_itc_r_fd_ (daemon->itc),
                                      &rs,
                                      &maxsock,
                                      (int) FD_SETSIZE);
      mhd_probe_log ("MHD_select: MHD_add_to_fd_set_(ITC) returned %d, itc_added=%d",
                     (int) (itc_added ? 1 : 0),
                     (int) itc_added);
      if (! itc_added)
      {
#ifdef HAVE_MESSAGES
        MHD_DLOG (daemon, _ ("Could not add control inter-thread " \
                             "communication channel FD to fdset.\n"));
#endif
        mhd_probe_log ("MHD_select: failed to add ITC fd; setting err_state=MHD_YES");
        err_state = MHD_YES;
      }
    }
    if ( (MHD_INVALID_SOCKET != (ls = daemon->listen_fd)) &&
         (! daemon->was_quiesced) )
    {
      /* Stop listening if we are at the configured connection limit */
      /* If we're at the connection limit, no point in really
         accepting new connections; however, make sure we do not miss
         the shutdown OR the termination of an existing connection; so
         only do this optimisation if we have a signaling ITC in
         place. */
      if (! itc_added ||
          ((daemon->connections < daemon->connection_limit) &&
           ! daemon->at_limit))
      {
        mhd_probe_log ("MHD_select: attempting to add listen socket %lld to fdset with fd_setsize=%d",
                       (long long) ls,
                       (int) FD_SETSIZE);
        if (! MHD_add_to_fd_set_ (ls,
                                  &rs,
                                  &maxsock,
                                  (int) FD_SETSIZE))
        {
#ifdef HAVE_MESSAGES
          MHD_DLOG (daemon,
                    _ ("Could not add listen socket to fdset.\n"));
#endif
          mhd_probe_log ("MHD_select: failed to add listen socket; setting err_state=MHD_YES");
          err_state = MHD_YES;
        }
        else
        {
          mhd_probe_log ("MHD_select: listen socket added; maxsock now=%lld",
                         (long long) maxsock);
        }
      }
    }
  }

  if (MHD_NO != err_state)
  {
    mhd_probe_log ("MHD_select: err_state != MHD_NO -> forcing millisec=0 (was=%d)", (int) millisec);
    millisec = 0;
  }
  if (0 == millisec)
  {
    timeout.tv_usec = 0;
    timeout.tv_sec = 0;
    tv = &timeout;
    mhd_probe_log ("MHD_select: using immediate timeout (tv != NULL)");
  }
  else
  {
    uint64_t mhd_tmo;
    uint64_t select_tmo;

    if ( (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon)) &&
         (MHD_NO != MHD_get_timeout64 (daemon, &mhd_tmo)) )
    {
      if ( (0 < millisec) &&
           (mhd_tmo > (uint64_t) millisec) )
        select_tmo = (uint64_t) millisec;
      else
        select_tmo = mhd_tmo;
      tv = &timeout; /* have timeout value */
      mhd_probe_log ("MHD_select: computed select_tmo=%llu (from mhd_tmo=%llu millisec=%d)",
                     (unsigned long long) select_tmo,
                     (unsigned long long) mhd_tmo,
                     (int) millisec);
    }
    else if (0 < millisec)
    {
      select_tmo = (uint64_t) millisec;
      tv = &timeout; /* have timeout value */
      mhd_probe_log ("MHD_select: using millisec=%d as select_tmo=%llu", (int) millisec, (unsigned long long) select_tmo);
    }
    else
    {
      select_tmo = 0; /* Not actually used, silent compiler warning */
      tv = NULL;
      mhd_probe_log ("MHD_select: no timeout (tv == NULL)");
    }

    if (NULL != tv)
    { /* have timeout value */
#if (SIZEOF_UINT64_T - 2) >= SIZEOF_STRUCT_TIMEVAL_TV_SEC
      if (select_tmo / 1000 > TIMEVAL_TV_SEC_MAX)
        timeout.tv_sec = TIMEVAL_TV_SEC_MAX;
      else
#endif /* (SIZEOF_UINT64_T - 2) >= SIZEOF_STRUCT_TIMEVAL_TV_SEC */
      timeout.tv_sec = (_MHD_TIMEVAL_TV_SEC_TYPE) (select_tmo / 1000);

      timeout.tv_usec = ((uint16_t) (select_tmo % 1000)) * ((int32_t) 1000);
      mhd_probe_log ("MHD_select: timeout computed: tv_sec=%lld tv_usec=%lld",
                     (long long) timeout.tv_sec,
                     (long long) timeout.tv_usec);
    }
  }
  num_ready = MHD_SYS_select_ (maxsock + 1,
                               &rs,
                               &ws,
                               &es,
                               tv);
  mhd_probe_log ("MHD_select: select returned num_ready=%d, maxsock=%lld, tv=%p",
                 num_ready,
                 (long long) maxsock,
                 (void *) tv);
  if (daemon->shutdown)
  {
    mhd_probe_log ("MHD_select: shutdown detected after select; returning MHD_NO");
    return MHD_NO;
  }
  if (num_ready < 0)
  {
    const int err = MHD_socket_get_error_ ();
    mhd_probe_log ("MHD_select: select error err=%d", err);
    if (MHD_SCKT_ERR_IS_EINTR_ (err))
      return (MHD_NO == err_state) ? MHD_YES : MHD_NO;
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("select failed: %s\n"),
              MHD_socket_strerr_ (err));
#endif
    mhd_probe_log ("MHD_select: select failed and is not EINTR; returning MHD_NO");
    return MHD_NO;
  }
  mhd_probe_log ("MHD_select: calling internal_run_from_select with fd_setsize=%d",
                 (int) FD_SETSIZE);
  if (MHD_NO != internal_run_from_select (daemon,
                                          &rs,
                                          &ws,
                                          &es,
                                          (int) FD_SETSIZE))
  {
    mhd_probe_log ("MHD_select: internal_run_from_select indicated activity; returning based on err_state=%d", err_state);
    return (MHD_NO == err_state) ? MHD_YES : MHD_NO;
  }
  mhd_probe_log ("MHD_select: exiting normally (no activity from internal_run_from_select)");
  return MHD_NO;
}
