#include <stdio.h>
#include <stdint.h>
#include <stddef.h>

/* Probe function(s) to log control flow and fd_set state to a file. */
static void
MHD_select_probe_log_state (const char *stage,
                            struct MHD_Daemon *daemon,
                            int32_t millisec,
                            int num_ready,
                            const fd_set *rs,
                            const fd_set *ws,
                            const fd_set *es,
                            MHD_socket maxsock)
{
  FILE *f = fopen("/tmp/logs/131.txt", "a");
  if (NULL == f)
    return;

  /* Log basic control-flow and scalar state */
  fprintf (f,
           "MHD_select: stage=%s daemon=%p millisec=%d num_ready=%d maxsock=%lld\n",
           stage,
           (void *) daemon,
           (int) millisec,
           (int) num_ready,
           (long long) (intptr_t) maxsock);

  /* Log addresses and raw contents of the fd_set objects. We avoid
     calling FD_ISSET here to not require casting away const; instead
     we write the raw bytes so an external tool/agent can inspect the
     bits and reconstruct which fds were set. */
  if (rs != NULL)
  {
    fprintf (f, "rs addr=%p size=%zu\n", (const void *) rs, sizeof (*rs));
    fwrite ((const void *) rs, 1, sizeof (*rs), f);
    fprintf (f, "\n");
  }
  if (ws != NULL)
  {
    fprintf (f, "ws addr=%p size=%zu\n", (const void *) ws, sizeof (*ws));
    fwrite ((const void *) ws, 1, sizeof (*ws), f);
    fprintf (f, "\n");
  }
  if (es != NULL)
  {
    fprintf (f, "es addr=%p size=%zu\n", (const void *) es, sizeof (*es));
    fwrite ((const void *) es, 1, sizeof (*es), f);
    fprintf (f, "\n");
  }

  fflush (f);
  fclose (f);
}

static enum MHD_Result
MHD_select (struct MHD_Daemon *daemon,
            int32_t millisec)
{
  int num_ready;
  fd_set rs;
  fd_set ws;
  fd_set es;
  MHD_socket maxsock;
  struct timeval timeout;
  struct timeval *tv;
  int err_state;
  MHD_socket ls;

  timeout.tv_sec = 0;
  timeout.tv_usec = 0;
  if (daemon->shutdown)
    return MHD_NO;
  FD_ZERO (&rs);
  FD_ZERO (&ws);
  FD_ZERO (&es);
  maxsock = MHD_INVALID_SOCKET;
  err_state = MHD_NO;
  if ( (0 != (daemon->options & MHD_TEST_ALLOW_SUSPEND_RESUME)) &&
       (MHD_NO != resume_suspended_connections (daemon)) &&
       (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon)) )
    millisec = 0;

  if (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon))
  {
    /* single-threaded, go over everything */
    if (MHD_NO ==
        internal_get_fdset2 (daemon,
                             &rs,
                             &ws,
                             &es,
                             &maxsock,
                             (int) FD_SETSIZE))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Could not obtain daemon fdsets.\n"));
#endif
      err_state = MHD_YES;
    }
  }
  else
  {
    bool itc_added;
    /* accept only, have one thread per connection */
    itc_added = false;
    if (MHD_ITC_IS_VALID_ (daemon->itc))
    {
      itc_added = MHD_add_to_fd_set_ (MHD_itc_r_fd_ (daemon->itc),
                                      &rs,
                                      &maxsock,
                                      (int) FD_SETSIZE);
      if (! itc_added)
      {
#ifdef HAVE_MESSAGES
        MHD_DLOG (daemon, _ ("Could not add control inter-thread " \
                             "communication channel FD to fdset.\n"));
#endif
        err_state = MHD_YES;
      }
    }
    if ( (MHD_INVALID_SOCKET != (ls = daemon->listen_fd)) &&
         (! daemon->was_quiesced) )
    {
      /* Stop listening if we are at the configured connection limit */
      /* If we're at the connection limit, no point in really
         accepting new connections; however, make sure we do not miss
         the shutdown OR the termination of an existing connection; so
         only do this optimisation if we have a signaling ITC in
         place. */
      if (! itc_added ||
          ((daemon->connections < daemon->connection_limit) &&
           ! daemon->at_limit))
      {
        if (! MHD_add_to_fd_set_ (ls,
                                  &rs,
                                  &maxsock,
                                  (int) FD_SETSIZE))
        {
#ifdef HAVE_MESSAGES
          MHD_DLOG (daemon,
                    _ ("Could not add listen socket to fdset.\n"));
#endif
          err_state = MHD_YES;
        }
      }
    }
  }

  if (MHD_NO != err_state)
    millisec = 0;
  if (0 == millisec)
  {
    timeout.tv_usec = 0;
    timeout.tv_sec = 0;
    tv = &timeout;
  }
  else
  {
    uint64_t mhd_tmo;
    uint64_t select_tmo;

    if ( (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon)) &&
         (MHD_NO != MHD_get_timeout64 (daemon, &mhd_tmo)) )
    {
      if ( (0 < millisec) &&
           (mhd_tmo > (uint64_t) millisec) )
        select_tmo = (uint64_t) millisec;
      else
        select_tmo = mhd_tmo;
      tv = &timeout; /* have timeout value */
    }
    else if (0 < millisec)
    {
      select_tmo = (uint64_t) millisec;
      tv = &timeout; /* have timeout value */
    }
    else
    {
      select_tmo = 0; /* Not actually used, silent compiler warning */
      tv = NULL;
    }

    if (NULL != tv)
    { /* have timeout value */
#if (SIZEOF_UINT64_T - 2) >= SIZEOF_STRUCT_TIMEVAL_TV_SEC
      if (select_tmo / 1000 > TIMEVAL_TV_SEC_MAX)
        timeout.tv_sec = TIMEVAL_TV_SEC_MAX;
      else
#endif /* (SIZEOF_UINT64_T - 2) >= SIZEOF_STRUCT_TIMEVAL_TV_SEC */
      timeout.tv_sec = (_MHD_TIMEVAL_TV_SEC_TYPE) (select_tmo / 1000);

      timeout.tv_usec = ((uint16_t) (select_tmo % 1000)) * ((int32_t) 1000);
    }
  }
  num_ready = MHD_SYS_select_ (maxsock + 1,
                               &rs,
                               &ws,
                               &es,
                               tv);

  /* Log the state immediately after select returned so external
     agents can observe the fd_set bitmaps and other pertinent info. */
  MHD_select_probe_log_state ("after_select",
                              daemon,
                              millisec,
                              num_ready,
                              &rs,
                              &ws,
                              &es,
                              maxsock);

  if (daemon->shutdown)
    return MHD_NO;
  if (num_ready < 0)
  {
    const int err = MHD_socket_get_error_ ();

    /* Log failure case so agents can reproduce the failed-select path. */
    MHD_select_probe_log_state ("select_failed",
                                daemon,
                                millisec,
                                num_ready,
                                &rs,
                                &ws,
                                &es,
                                maxsock);

    if (MHD_SCKT_ERR_IS_EINTR_ (err))
      return (MHD_NO == err_state) ? MHD_YES : MHD_NO;
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("select failed: %s\n"),
              MHD_socket_strerr_ (err));
#endif
    return MHD_NO;
  }

  /* Log just before invoking internal_run_from_select so that callers
     can compare the fd_set contents passed in to how they are used
     inside that function (where the reported const-dropping occurs). */
  MHD_select_probe_log_state ("before_internal_run_from_select",
                              daemon,
                              millisec,
                              num_ready,
                              &rs,
                              &ws,
                              &es,
                              maxsock);

  if (MHD_NO != internal_run_from_select (daemon,
                                          &rs,
                                          &ws,
                                          &es,
                                          (int) FD_SETSIZE))
    return (MHD_NO == err_state) ? MHD_YES : MHD_NO;
  return MHD_NO;
}
