static void mhd_log(const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/131.txt", "a");
  if (!f)
    return;
  va_list ap;
  va_start(ap, fmt);
  vfprintf(f, fmt, ap);
  va_end(ap);
  fprintf(f, "\n");
  fclose(f);
}

static void mhd_log_fdset_states(const char *label, const fd_set *r, const fd_set *w, const fd_set *e, int fdcount)
{
  FILE *f = fopen("/tmp/logs/131.txt", "a");
  if (!f)
    return;
  fprintf(f, "%s: fdcount=%d\n", label, fdcount);
  fprintf(f, " read fds:");
  for (int i = 0; i < fdcount; ++i)
    if (FD_ISSET(i, (fd_set *) r)) /* r/w/e are non-const in caller; cast here only for fprintf use */
      fprintf(f, " %d", i);
  fprintf(f, "\n write fds:");
  for (int i = 0; i < fdcount; ++i)
    if (FD_ISSET(i, (fd_set *) w))
      fprintf(f, " %d", i);
  fprintf(f, "\n except fds:");
  for (int i = 0; i < fdcount; ++i)
    if (FD_ISSET(i, (fd_set *) e))
      fprintf(f, " %d", i);
  fprintf(f, "\n\n");
  fclose(f);
}

static enum MHD_Result
MHD_select (struct MHD_Daemon *daemon,
            int32_t millisec)
{
  int num_ready;
  fd_set rs;
  fd_set ws;
  fd_set es;
  MHD_socket maxsock;
  struct timeval timeout;
  struct timeval *tv;
  int err_state;
  MHD_socket ls;

  mhd_log("MHD_select: enter daemon=%p millisec=%d", (void *) daemon, (int) millisec);

  timeout.tv_sec = 0;
  timeout.tv_usec = 0;
  if (daemon->shutdown)
  {
    mhd_log("MHD_select: early exit due to shutdown");
    return MHD_NO;
  }
  FD_ZERO (&rs);
  FD_ZERO (&ws);
  FD_ZERO (&es);
  maxsock = MHD_INVALID_SOCKET;
  err_state = MHD_NO;
  if ( (0 != (daemon->options & MHD_TEST_ALLOW_SUSPEND_RESUME)) &&
       (MHD_NO != resume_suspended_connections (daemon)) &&
       (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon)) )
    millisec = 0;

  mhd_log("MHD_select: after init maxsock=%ld options=0x%08x connections=%d connection_limit=%d was_quiesced=%d",
          (long) maxsock,
          (unsigned int) daemon->options,
          (int) daemon->connections,
          (int) daemon->connection_limit,
          (int) daemon->was_quiesced);

  if (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon))
  {
    /* single-threaded, go over everything */
    if (MHD_NO ==
        internal_get_fdset2 (daemon,
                             &rs,
                             &ws,
                             &es,
                             &maxsock,
                             (int) FD_SETSIZE))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Could not obtain daemon fdsets.\n"));
#endif
      mhd_log("MHD_select: internal_get_fdset2 failed");
      err_state = MHD_YES;
    }
  }
  else
  {
    bool itc_added;
    /* accept only, have one thread per connection */
    itc_added = false;
    if (MHD_ITC_IS_VALID_ (daemon->itc))
    {
      itc_added = MHD_add_to_fd_set_ (MHD_itc_r_fd_ (daemon->itc),
                                      &rs,
                                      &maxsock,
                                      (int) FD_SETSIZE);
      if (! itc_added)
      {
#ifdef HAVE_MESSAGES
        MHD_DLOG (daemon, _ ("Could not add control inter-thread " \
                             "communication channel FD to fdset.\n"));
#endif
        mhd_log("MHD_select: failed to add ITC fd to rs");
        err_state = MHD_YES;
      }
    }
    if ( (MHD_INVALID_SOCKET != (ls = daemon->listen_fd)) &&
         (! daemon->was_quiesced) )
    {
      /* Stop listening if we are at the configured connection limit */
      /* If we're at the connection limit, no point in really
         accepting new connections; however, make sure we do not miss
         the shutdown OR the termination of an existing connection; so
         only do this optimisation if we have a signaling ITC in
         place. */
      if (! itc_added ||
          ((daemon->connections < daemon->connection_limit) &&
           ! daemon->at_limit))
      {
        if (! MHD_add_to_fd_set_ (ls,
                                  &rs,
                                  &maxsock,
                                  (int) FD_SETSIZE))
        {
#ifdef HAVE_MESSAGES
          MHD_DLOG (daemon,
                    _ ("Could not add listen socket to fdset.\n"));
#endif
          mhd_log("MHD_select: failed to add listen socket to rs");
          err_state = MHD_YES;
        }
      }
    }
  }

  if (MHD_NO != err_state)
    millisec = 0;
  if (0 == millisec)
  {
    timeout.tv_usec = 0;
    timeout.tv_sec = 0;
    tv = &timeout;
  }
  else
  {
    uint64_t mhd_tmo;
    uint64_t select_tmo;

    if ( (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon)) &&
         (MHD_NO != MHD_get_timeout64 (daemon, &mhd_tmo)) )
    {
      if ( (0 < millisec) &&
           (mhd_tmo > (uint64_t) millisec) )
        select_tmo = (uint64_t) millisec;
      else
        select_tmo = mhd_tmo;
      tv = &timeout; /* have timeout value */
    }
    else if (0 < millisec)
    {
      select_tmo = (uint64_t) millisec;
      tv = &timeout; /* have timeout value */
    }
    else
    {
      select_tmo = 0; /* Not actually used, silent compiler warning */
      tv = NULL;
    }

    if (NULL != tv)
    { /* have timeout value */
#if (SIZEOF_UINT64_T - 2) >= SIZEOF_STRUCT_TIMEVAL_TV_SEC
      if (select_tmo / 1000 > TIMEVAL_TV_SEC_MAX)
        timeout.tv_sec = TIMEVAL_TV_SEC_MAX;
      else
#endif /* (SIZEOF_UINT64_T - 2) >= SIZEOF_STRUCT_TIMEVAL_TV_SEC */
      timeout.tv_sec = (_MHD_TIMEVAL_TV_SEC_TYPE) (select_tmo / 1000);

      timeout.tv_usec = ((uint16_t) (select_tmo % 1000)) * ((int32_t) 1000);
    }
  }
  mhd_log("MHD_select: calling select maxsock=%ld tv=%p tv_sec=%ld tv_usec=%ld",
          (long) maxsock,
          (void *) tv,
          (long) timeout.tv_sec,
          (long) timeout.tv_usec);
  num_ready = MHD_SYS_select_ (maxsock + 1,
                               &rs,
                               &ws,
                               &es,
                               tv);
  mhd_log("MHD_select: select returned=%d", num_ready);

  if (daemon->shutdown)
  {
    mhd_log("MHD_select: exit after select due to shutdown");
    return MHD_NO;
  }
  if (num_ready < 0)
  {
    const int err = MHD_socket_get_error_ ();
    mhd_log("MHD_select: select error=%d", err);
    if (MHD_SCKT_ERR_IS_EINTR_ (err))
    {
      mhd_log("MHD_select: interrupted by signal, err_state=%d", err_state);
      return (MHD_NO == err_state) ? MHD_YES : MHD_NO;
    }
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("select failed: %s\n"),
              MHD_socket_strerr_ (err));
#endif
    return MHD_NO;
  }

  /* Probe: log fdset contents before handing them to internal_run_from_select
     This helps reproduce the scenario where a write fdset is later treated as const
     inside internal_run_from_select and a cast-away-const is performed there. */
  mhd_log("MHD_select: about to call internal_run_from_select(daemon=%p, rs=%p, ws=%p, es=%p, fdcount=%d)",
          (void *) daemon,
          (void *) &rs,
          (void *) &ws,
          (void *) &es,
          (int) FD_SETSIZE);

  /* Log the actual FDs that are ready in each set */
  mhd_log_fdset_states("MHD_select fdset snapshot", &rs, &ws, &es, (int) FD_SETSIZE);

  if (MHD_NO != internal_run_from_select (daemon,
                                          &rs,
                                          &ws,
                                          &es,
                                          (int) FD_SETSIZE))
  {
    mhd_log("MHD_select: internal_run_from_select returned non-MHD_NO, err_state=%d", err_state);
    return (MHD_NO == err_state) ? MHD_YES : MHD_NO;
  }
  mhd_log("MHD_select: exiting normally");
  return MHD_NO;
}
