static void
mhd_log_probe (const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/341.txt", "a");
  if (NULL == f)
    return;
  va_list ap;
  va_start (ap, fmt);
  vfprintf (f, fmt, ap);
  fprintf (f, "\n");
  va_end (ap);
  fclose (f);
}

ssize_t
MHD_send_data_ (struct MHD_Connection *connection,
                const char *buffer,
                size_t buffer_size,
                bool push_data)
{
  MHD_socket s = connection->socket_fd;
  ssize_t ret;
#ifdef HTTPS_SUPPORT
  const bool tls_conn = (connection->daemon->options & MHD_USE_TLS);
#else  /* ! HTTPS_SUPPORT */
  const bool tls_conn = false;
#endif /* ! HTTPS_SUPPORT */

  /* Entry log: capture initial state */
  mhd_log_probe ("ENTER: conn=%p socket=%ld buffer=%p buffer_size=%zu push_data=%d tls_conn=%d",
                 (void *) connection,
                 (long) s,
                 (const void *) buffer,
                 buffer_size,
                 (int) push_data,
                 (int) tls_conn);

  if ( (MHD_INVALID_SOCKET == s) ||
       (MHD_CONNECTION_CLOSED == connection->state) )
  {
    mhd_log_probe ("EARLY_RETURN: not connected or socket invalid (s=%ld, state=%d)", (long) s, (int) connection->state);
    return MHD_ERR_NOTCONN_;
  }

  if (buffer_size > SSIZE_MAX)
  {
    mhd_log_probe ("BRANCH: buffer_size (%zu) > SSIZE_MAX (%zu) - about to assign buffer_size=SSIZE_MAX and push_data=false",
                   buffer_size, (size_t) SSIZE_MAX);
    buffer_size = SSIZE_MAX; /* Max return value */
    push_data = false; /* Incomplete send */
    mhd_log_probe ("AFTER_ASSIGNMENT: buffer_size=%zu push_data=%d",
                   buffer_size, (int) push_data);
  }

  if (tls_conn)
  {
#ifdef HTTPS_SUPPORT
    mhd_log_probe ("TLS_PATH: calling pre_send_setopt with push_data=%d", (int) push_data);
    pre_send_setopt (connection, (! tls_conn), push_data);
    ret = gnutls_record_send (connection->tls_session,
                              buffer,
                              buffer_size);
    mhd_log_probe ("TLS_RET: gnutls_record_send returned %zd", ret);
    if (GNUTLS_E_AGAIN == ret)
    {
#ifdef EPOLL_SUPPORT
      connection->epoll_state &=
        ~((enum MHD_EpollState) MHD_EPOLL_STATE_WRITE_READY);
#endif
      mhd_log_probe ("TLS_EAGAIN: returning MHD_ERR_AGAIN_");
      return MHD_ERR_AGAIN_;
    }
    if (GNUTLS_E_INTERRUPTED == ret)
    {
      mhd_log_probe ("TLS_INTERRUPTED: returning MHD_ERR_AGAIN_");
      return MHD_ERR_AGAIN_;
    }
    if ( (GNUTLS_E_ENCRYPTION_FAILED == ret) ||
         (GNUTLS_E_INVALID_SESSION == ret) ||
         (GNUTLS_E_COMPRESSION_FAILED == ret) ||
         (GNUTLS_E_EXPIRED == ret) ||
         (GNUTLS_E_HASH_FAILED == ret) )
    {
      mhd_log_probe ("TLS_FATAL_ERR: ret=%zd -> MHD_ERR_TLS_", ret);
      return MHD_ERR_TLS_;
    }
    if ( (GNUTLS_E_PUSH_ERROR == ret) ||
         (GNUTLS_E_INTERNAL_ERROR == ret) ||
         (GNUTLS_E_CRYPTODEV_IOCTL_ERROR == ret) ||
         (GNUTLS_E_CRYPTODEV_DEVICE_ERROR == ret) )
    {
      mhd_log_probe ("TLS_PIPE_ERR: ret=%zd -> MHD_ERR_PIPE_", ret);
      return MHD_ERR_PIPE_;
    }
#if defined(GNUTLS_E_PREMATURE_TERMINATION)
    if (GNUTLS_E_PREMATURE_TERMINATION == ret)
    {
      mhd_log_probe ("TLS_PREMATURE_TERMINATION -> MHD_ERR_CONNRESET_");
      return MHD_ERR_CONNRESET_;
    }
#elif defined(GNUTLS_E_UNEXPECTED_PACKET_LENGTH)
    if (GNUTLS_E_UNEXPECTED_PACKET_LENGTH == ret)
    {
      mhd_log_probe ("TLS_UNEXPECTED_PACKET_LENGTH -> MHD_ERR_CONNRESET_");
      return MHD_ERR_CONNRESET_;
    }
#endif /* GNUTLS_E_UNEXPECTED_PACKET_LENGTH */
    if (GNUTLS_E_MEMORY_ERROR == ret)
    {
      mhd_log_probe ("TLS_MEMORY_ERROR -> MHD_ERR_NOMEM_");
      return MHD_ERR_NOMEM_;
    }
    if (ret < 0)
    {
      /* Treat any other error as hard error. */
      mhd_log_probe ("TLS_OTHER_ERROR: ret=%zd -> MHD_ERR_NOTCONN_", ret);
      return MHD_ERR_NOTCONN_;
    }
#ifdef EPOLL_SUPPORT
    /* Unlike non-TLS connections, do not reset "write-ready" if
     * sent amount smaller than provided amount, as TLS
     * connections may break data into smaller parts for sending. */
#endif /* EPOLL_SUPPORT */
#else  /* ! HTTPS_SUPPORT  */
    ret = MHD_ERR_NOTCONN_;
    mhd_log_probe ("TLS_NOT_SUPPORTED: returning MHD_ERR_NOTCONN_");
#endif /* ! HTTPS_SUPPORT  */
  }
  else
  {
    /* plaintext transmission */
    if (buffer_size > MHD_SCKT_SEND_MAX_SIZE_)
    {
      mhd_log_probe ("BRANCH: plaintext buffer_size (%zu) > MHD_SCKT_SEND_MAX_SIZE_ (%zu) - about to cap and set push_data=false",
                     buffer_size, (size_t) MHD_SCKT_SEND_MAX_SIZE_);
      buffer_size = MHD_SCKT_SEND_MAX_SIZE_; /* send() return value limit */
      push_data = false; /* Incomplete send */
      mhd_log_probe ("AFTER_ASSIGNMENT_PLAINTEXT: buffer_size=%zu push_data=%d",
                     buffer_size, (int) push_data);
    }

    mhd_log_probe ("PLAINTEXT_PATH: calling pre_send_setopt with push_data=%d", (int) push_data);
    pre_send_setopt (connection, (! tls_conn), push_data);
#ifdef MHD_USE_MSG_MORE
    ret = MHD_send4_ (s,
                      buffer,
                      buffer_size,
                      push_data ? 0 : MSG_MORE);
#else
    ret = MHD_send4_ (s,
                      buffer,
                      buffer_size,
                      0);
#endif
    mhd_log_probe ("PLAINTEXT_SEND_RET: ret=%zd", ret);

    if (0 > ret)
    {
      const int err = MHD_socket_get_error_ ();

      mhd_log_probe ("PLAINTEXT_SEND_ERR: ret=%zd err=%d", ret, err);

      if (MHD_SCKT_ERR_IS_EAGAIN_ (err))
      {
#ifdef EPOLL_SUPPORT
        /* EAGAIN, no longer write-ready */
        connection->epoll_state &=
          ~((enum MHD_EpollState) MHD_EPOLL_STATE_WRITE_READY);
#endif /* EPOLL_SUPPORT */
        mhd_log_probe ("PLAINTEXT_EAGAIN: returning MHD_ERR_AGAIN_");
        return MHD_ERR_AGAIN_;
      }
      if (MHD_SCKT_ERR_IS_EINTR_ (err))
      {
        mhd_log_probe ("PLAINTEXT_EINTR: returning MHD_ERR_AGAIN_");
        return MHD_ERR_AGAIN_;
      }
      if (MHD_SCKT_ERR_IS_REMOTE_DISCNN_ (err))
      {
        mhd_log_probe ("PLAINTEXT_REMOTE_DISCONNECT: returning MHD_ERR_CONNRESET_");
        return MHD_ERR_CONNRESET_;
      }
      if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_EPIPE_))
      {
        mhd_log_probe ("PLAINTEXT_EPIPE: returning MHD_ERR_PIPE_");
        return MHD_ERR_PIPE_;
      }
      if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_EOPNOTSUPP_))
      {
        mhd_log_probe ("PLAINTEXT_EOPNOTSUPP: returning MHD_ERR_OPNOTSUPP_");
        return MHD_ERR_OPNOTSUPP_;
      }
      if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_ENOTCONN_))
      {
        mhd_log_probe ("PLAINTEXT_ENOTCONN: returning MHD_ERR_NOTCONN_");
        return MHD_ERR_NOTCONN_;
      }
      if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_EINVAL_))
      {
        mhd_log_probe ("PLAINTEXT_EINVAL: returning MHD_ERR_INVAL_");
        return MHD_ERR_INVAL_;
      }
      if (MHD_SCKT_ERR_IS_LOW_RESOURCES_ (err))
      {
        mhd_log_probe ("PLAINTEXT_LOW_RESOURCES: returning MHD_ERR_NOMEM_");
        return MHD_ERR_NOMEM_;
      }
      if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_EBADF_))
      {
        mhd_log_probe ("PLAINTEXT_EBADF: returning MHD_ERR_BADF_");
        return MHD_ERR_BADF_;
      }
      /* Treat any other error as a hard error. */
      mhd_log_probe ("PLAINTEXT_OTHER_ERROR: returning MHD_ERR_NOTCONN_");
      return MHD_ERR_NOTCONN_;
    }
#ifdef EPOLL_SUPPORT
    else if (buffer_size > (size_t) ret)
      connection->epoll_state &=
        ~((enum MHD_EpollState) MHD_EPOLL_STATE_WRITE_READY);
#endif /* EPOLL_SUPPORT */
  }

  /* If there is a need to push the data from network buffers
   * call post_send_setopt(). */
  /* If TLS connection is used then next final send() will be
   * without MSG_MORE support. If non-TLS connection is used
   * it's unknown whether sendfile() will be used or not so
   * assume that next call will be the same, like this call. */
  if ( (push_data) &&
       (buffer_size == (size_t) ret) )
  {
    mhd_log_probe ("POST_SEND_COND: calling post_send_setopt (push_data=%d buffer_size=%zu ret=%zd)",
                   (int) push_data, buffer_size, ret);
    post_send_setopt (connection, (! tls_conn), push_data);
  }

  mhd_log_probe ("RETURN: ret=%zd final_buffer_size=%zu final_push_data=%d",
                 ret, buffer_size, (int) push_data);

  return ret;
}

