#include <stdio.h>
#include <stdarg.h>

static void log_probe(const char *fmt, ...)
{
  va_list ap;
  FILE *f = fopen("/tmp/logs/338.txt", "a");
  if (f == NULL)
    return;
  va_start(ap, fmt);
  vfprintf(f, fmt, ap);
  va_end(ap);
  fprintf(f, "\n");
  fclose(f);
}

ssize_t
MHD_send_data_ (struct MHD_Connection *connection,
                const char *buffer,
                size_t buffer_size,
                bool push_data)
{
  MHD_socket s = connection->socket_fd;
  ssize_t ret;
#ifdef HTTPS_SUPPORT
  const bool tls_conn = (connection->daemon->options & MHD_USE_TLS);
#else  /* ! HTTPS_SUPPORT */
  const bool tls_conn = false;
#endif /* ! HTTPS_SUPPORT */

  /* Entry log */
  log_probe("MHD_send_data_: entry: connection=%p socket_fd=%d state=%d buffer=%p buffer_size=%zu push_data=%d tls_conn=%d",
            (void *) connection, (int) s, (int) connection->state,
            (const void *) buffer, buffer_size, (int) push_data, (int) tls_conn);

  if ( (MHD_INVALID_SOCKET == s) ||
       (MHD_CONNECTION_CLOSED == connection->state) )
  {
    log_probe("MHD_send_data_: early return: invalid socket or closed connection (socket=%d state=%d)", (int) s, (int) connection->state);
    return MHD_ERR_NOTCONN_;
  }

  if (buffer_size > SSIZE_MAX)
  {
    buffer_size = SSIZE_MAX; /* Max return value */
    push_data = false; /* Incomplete send */
    log_probe("MHD_send_data_: truncated buffer_size to SSIZE_MAX=%zu, push_data set to false", (size_t) SSIZE_MAX);
  }

  if (tls_conn)
  {
#ifdef HTTPS_SUPPORT
    log_probe("MHD_send_data_: TLS branch: calling pre_send_setopt (is_tls=%d push_data=%d)", (int) (! tls_conn), (int) push_data);
    pre_send_setopt (connection, (! tls_conn), push_data);
    ret = gnutls_record_send (connection->tls_session,
                              buffer,
                              buffer_size);
    log_probe("MHD_send_data_: gnutls_record_send returned %zd", ret);
    if (GNUTLS_E_AGAIN == ret)
    {
#ifdef EPOLL_SUPPORT
      connection->epoll_state &=
        ~((enum MHD_EpollState) MHD_EPOLL_STATE_WRITE_READY);
#endif
      log_probe("MHD_send_data_: TLS GNUTLS_E_AGAIN -> MHD_ERR_AGAIN_");
      return MHD_ERR_AGAIN_;
    }
    if (GNUTLS_E_INTERRUPTED == ret)
    {
      log_probe("MHD_send_data_: TLS GNUTLS_E_INTERRUPTED -> MHD_ERR_AGAIN_");
      return MHD_ERR_AGAIN_;
    }
    if ( (GNUTLS_E_ENCRYPTION_FAILED == ret) ||
         (GNUTLS_E_INVALID_SESSION == ret) ||
         (GNUTLS_E_COMPRESSION_FAILED == ret) ||
         (GNUTLS_E_EXPIRED == ret) ||
         (GNUTLS_E_HASH_FAILED == ret) )
    {
      log_probe("MHD_send_data_: TLS unrecoverable TLS error %zd -> MHD_ERR_TLS_", ret);
      return MHD_ERR_TLS_;
    }
    if ( (GNUTLS_E_PUSH_ERROR == ret) ||
         (GNUTLS_E_INTERNAL_ERROR == ret) ||
         (GNUTLS_E_CRYPTODEV_IOCTL_ERROR == ret) ||
         (GNUTLS_E_CRYPTODEV_DEVICE_ERROR == ret) )
    {
      log_probe("MHD_send_data_: TLS pipe/internal error %zd -> MHD_ERR_PIPE_", ret);
      return MHD_ERR_PIPE_;
    }
#if defined(GNUTLS_E_PREMATURE_TERMINATION)
    if (GNUTLS_E_PREMATURE_TERMINATION == ret)
    {
      log_probe("MHD_send_data_: TLS premature termination -> MHD_ERR_CONNRESET_");
      return MHD_ERR_CONNRESET_;
    }
#elif defined(GNUTLS_E_UNEXPECTED_PACKET_LENGTH)
    if (GNUTLS_E_UNEXPECTED_PACKET_LENGTH == ret)
    {
      log_probe("MHD_send_data_: TLS unexpected packet length -> MHD_ERR_CONNRESET_");
      return MHD_ERR_CONNRESET_;
    }
#endif /* GNUTLS_E_UNEXPECTED_PACKET_LENGTH */
    if (GNUTLS_E_MEMORY_ERROR == ret)
    {
      log_probe("MHD_send_data_: TLS memory error -> MHD_ERR_NOMEM_");
      return MHD_ERR_NOMEM_;
    }
    if (ret < 0)
    {
      /* Treat any other error as hard error. */
      log_probe("MHD_send_data_: TLS unknown negative ret %zd -> MHD_ERR_NOTCONN_", ret);
      return MHD_ERR_NOTCONN_;
    }
#ifdef EPOLL_SUPPORT
    /* Unlike non-TLS connections, do not reset "write-ready" if
     * sent amount smaller than provided amount, as TLS
     * connections may break data into smaller parts for sending. */
#endif /* EPOLL_SUPPORT */
#else  /* ! HTTPS_SUPPORT  */
    ret = MHD_ERR_NOTCONN_;
    log_probe("MHD_send_data_: TLS support not compiled in -> ret=MHD_ERR_NOTCONN_");
#endif /* ! HTTPS_SUPPORT  */
  }
  else
  {
    /* plaintext transmission */
    if (buffer_size > MHD_SCKT_SEND_MAX_SIZE_)
    {
      buffer_size = MHD_SCKT_SEND_MAX_SIZE_; /* send() return value limit */
      push_data = false; /* Incomplete send */
      log_probe("MHD_send_data_: truncated buffer_size to MHD_SCKT_SEND_MAX_SIZE_=%zu, push_data set to false", (size_t) MHD_SCKT_SEND_MAX_SIZE_);
    }

    log_probe("MHD_send_data_: plaintext branch: calling pre_send_setopt (is_tls=%d push_data=%d)", (int) (! tls_conn), (int) push_data);
    pre_send_setopt (connection, (! tls_conn), push_data);
#ifdef MHD_USE_MSG_MORE
    ret = MHD_send4_ (s,
                      buffer,
                      buffer_size,
                      push_data ? 0 : MSG_MORE);
#else
    ret = MHD_send4_ (s,
                      buffer,
                      buffer_size,
                      0);
#endif

    log_probe("MHD_send_data_: MHD_send4_ returned %zd", ret);

    if (0 > ret)
    {
      const int err = MHD_socket_get_error_ ();
      log_probe("MHD_send_data_: send error: err=%d", err);

      if (MHD_SCKT_ERR_IS_EAGAIN_ (err))
      {
#ifdef EPOLL_SUPPORT
        /* EAGAIN, no longer write-ready */
        connection->epoll_state &=
          ~((enum MHD_EpollState) MHD_EPOLL_STATE_WRITE_READY);
#endif /* EPOLL_SUPPORT */
        log_probe("MHD_send_data_: send EAGAIN -> MHD_ERR_AGAIN_");
        return MHD_ERR_AGAIN_;
      }
      if (MHD_SCKT_ERR_IS_EINTR_ (err))
      {
        log_probe("MHD_send_data_: send EINTR -> MHD_ERR_AGAIN_");
        return MHD_ERR_AGAIN_;
      }
      if (MHD_SCKT_ERR_IS_REMOTE_DISCNN_ (err))
      {
        log_probe("MHD_send_data_: send remote disconnect -> MHD_ERR_CONNRESET_");
        return MHD_ERR_CONNRESET_;
      }
      if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_EPIPE_))
      {
        log_probe("MHD_send_data_: send EPIPE -> MHD_ERR_PIPE_");
        return MHD_ERR_PIPE_;
      }
      if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_EOPNOTSUPP_))
      {
        log_probe("MHD_send_data_: send EOPNOTSUPP -> MHD_ERR_OPNOTSUPP_");
        return MHD_ERR_OPNOTSUPP_;
      }
      if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_ENOTCONN_))
      {
        log_probe("MHD_send_data_: send ENOTCONN -> MHD_ERR_NOTCONN_");
        return MHD_ERR_NOTCONN_;
      }
      if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_EINVAL_))
      {
        log_probe("MHD_send_data_: send EINVAL -> MHD_ERR_INVAL_");
        return MHD_ERR_INVAL_;
      }
      if (MHD_SCKT_ERR_IS_LOW_RESOURCES_ (err))
      {
        log_probe("MHD_send_data_: send low resources -> MHD_ERR_NOMEM_");
        return MHD_ERR_NOMEM_;
      }
      if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_EBADF_))
      {
        log_probe("MHD_send_data_: send EBADF -> MHD_ERR_BADF_");
        return MHD_ERR_BADF_;
      }
      /* Treat any other error as a hard error. */
      log_probe("MHD_send_data_: send unknown error %d -> MHD_ERR_NOTCONN_", err);
      return MHD_ERR_NOTCONN_;
    }
#ifdef EPOLL_SUPPORT
    else if (buffer_size > (size_t) ret)
      connection->epoll_state &=
        ~((enum MHD_EpollState) MHD_EPOLL_STATE_WRITE_READY);
#endif /* EPOLL_SUPPORT */
  }

  /* If there is a need to push the data from network buffers
   * call post_send_setopt(). */
  /* If TLS connection is used then next final send() will be
   * without MSG_MORE support. If non-TLS connection is used
   * it's unknown whether sendfile() will be used or not so
   * assume that next call will be the same, like this call. */
  if ( (push_data) &&
       (buffer_size == (size_t) ret) )
  {
    log_probe("MHD_send_data_: calling post_send_setopt (is_tls=%d push_data=%d) because push_data and buffer_size==ret (buffer_size=%zu ret=%zd)",
              (int) (! tls_conn), (int) push_data, buffer_size, ret);
    post_send_setopt (connection, (! tls_conn), push_data);
  }

  log_probe("MHD_send_data_: exit returning ret=%zd", ret);
  return ret;
}
