#include <stdio.h>
#include <stdint.h>
#include <inttypes.h>
#include <stdbool.h>

/* Probe helper: append a concise snapshot of daemon state to the log file. */
static void
mhd_probe_log_state (const char *tag, struct MHD_Daemon *daemon)
{
  FILE *f = fopen("/tmp/logs/362.txt", "a");
  if (NULL == f)
    return;

  /* Print commonly used fields seen in MHD_stop_daemon to help reproduce
     the assertion / dead-code scenario. Cast fields to integer/pointer
     representations to avoid depending on concrete typedefs here. */
  (void) fprintf (f,
                  "%s: daemon=%p master=%p shutdown=%d options=0x%08x "
                  "worker_pool=%p worker_pool_size=%u tid=%" PRIuPTR
                  " was_quiesced=%d listen_fd=%" PRIuPTR "\n",
                  tag,
                  (void *) daemon,
                  (void *) daemon->master,
                  (int) daemon->shutdown,
                  (unsigned) daemon->options,
                  (void *) daemon->worker_pool,
                  (unsigned) daemon->worker_pool_size,
                  (uintptr_t) daemon->tid,
                  (int) daemon->was_quiesced,
                  (uintptr_t) daemon->listen_fd);
  (void) fclose (f);
}

static void
mhd_probe_log_assert_eval (const char *tag, int lhs, int rhs)
{
  FILE *f = fopen("/tmp/logs/362.txt", "a");
  if (NULL == f)
    return;
  (void) fprintf (f, "%s: assertion parts -> lhs=%d rhs=%d (lhs || rhs == %d)\n",
                  tag, lhs, rhs, (lhs || rhs));
  (void) fclose (f);
}

_MHD_EXTERN void
MHD_stop_daemon (struct MHD_Daemon *daemon)
{
  MHD_socket fd;
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
  unsigned int i;
#endif

  /* Entry probe */
  mhd_probe_log_state ("MHD_stop_daemon ENTER", daemon);

  if (NULL == daemon)
  {
    mhd_probe_log_state ("MHD_stop_daemon: daemon == NULL, returning", daemon);
    return;
  }
  if ( (daemon->shutdown) && (NULL == daemon->master) )
    MHD_PANIC (_ ("MHD_stop_daemon() was called twice."));

  mhd_assert ((0 == (daemon->options & MHD_USE_SELECT_INTERNALLY)) || \
              (NULL != daemon->worker_pool) || \
              (MHD_thread_handle_ID_is_valid_handle_ (daemon->tid)));
  mhd_assert (((0 != (daemon->options & MHD_USE_SELECT_INTERNALLY)) &&
               (NULL == daemon->worker_pool)) || \
              (! MHD_thread_handle_ID_is_valid_handle_ (daemon->tid)));

  /* Slave daemons must be stopped by master daemon. */
  /* Probe the specific assertion that is reported as a dead-code site. */
  mhd_probe_log_state ("Before slave-master mhd_assert", daemon);
  mhd_probe_log_assert_eval ("slave-master assertion evaluation",
                             (NULL == daemon->master) ? 1 : 0,
                             (daemon->shutdown) ? 1 : 0);

  mhd_assert ( (NULL == daemon->master) || (daemon->shutdown) );

  /* Log the change of shutdown flag: record previous state then after set. */
  mhd_probe_log_state ("Before setting daemon->shutdown = true", daemon);
  daemon->shutdown = true;
  mhd_probe_log_state ("After setting daemon->shutdown = true", daemon);

  if (daemon->was_quiesced)
    fd = MHD_INVALID_SOCKET; /* Do not use FD if daemon was quiesced */
  else
    fd = daemon->listen_fd;

#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
  if (NULL != daemon->worker_pool)
  {   /* Master daemon with worker pool. */
    mhd_assert (1 < daemon->worker_pool_size);
    mhd_assert (MHD_D_IS_USING_THREADS_ (daemon));

    /* Let workers shutdown in parallel. */
    for (i = 0; i < daemon->worker_pool_size; ++i)
    {
      daemon->worker_pool[i].shutdown = true;
      if (MHD_ITC_IS_VALID_ (daemon->worker_pool[i].itc))
      {
        if (! MHD_itc_activate_ (daemon->worker_pool[i].itc,
                                 "e"))
          MHD_PANIC (_ ("Failed to signal shutdown via inter-thread " \
                        "communication channel.\n"));
      }
      else
        mhd_assert (MHD_INVALID_SOCKET != fd);
    }
#ifdef HAVE_LISTEN_SHUTDOWN
    if (MHD_INVALID_SOCKET != fd)
    {
      (void) shutdown (fd,
                       SHUT_RDWR);
    }
#endif /* HAVE_LISTEN_SHUTDOWN */
    for (i = 0; i < daemon->worker_pool_size; ++i)
    {
      MHD_stop_daemon (&daemon->worker_pool[i]);
    }
    free (daemon->worker_pool);
    mhd_assert (MHD_ITC_IS_INVALID_ (daemon->itc));
#ifdef EPOLL_SUPPORT
    mhd_assert (-1 == daemon->epoll_fd);
#if defined(HTTPS_SUPPORT) && defined(UPGRADE_SUPPORT)
    mhd_assert (-1 == daemon->epoll_upgrade_fd);
#endif /* HTTPS_SUPPORT && UPGRADE_SUPPORT */
#endif /* EPOLL_SUPPORT */
  }
  else
#endif
  {   /* Worker daemon or single daemon. */
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
    if (MHD_D_IS_USING_THREADS_ (daemon))
    {     /* Worker daemon or single daemon with internal thread(s). */
      mhd_assert (0 == daemon->worker_pool_size);
      /* Separate thread(s) is used for polling sockets. */
      if (MHD_ITC_IS_VALID_ (daemon->itc))
      {
        if (! MHD_itc_activate_ (daemon->itc,
                                 "e"))
          MHD_PANIC (_ ("Failed to signal shutdown via inter-thread " \
                        "communication channel.\n"));
      }
      else
      {
#ifdef HAVE_LISTEN_SHUTDOWN
        if (MHD_INVALID_SOCKET != fd)
        {
          if (NULL == daemon->master)
            (void) shutdown (fd,
                             SHUT_RDWR);
        }
        else
#endif /* HAVE_LISTEN_SHUTDOWN */
        mhd_assert (false); /* Should never happen */
      }

      if (! MHD_thread_handle_ID_join_thread_ (daemon->tid))
      {
        MHD_PANIC (_ ("Failed to join a thread.\n"));
      }
      /* close_all_connections() was called in daemon thread. */
    }
    else
#endif
    {
      /* No internal threads are used for polling sockets. */
      close_all_connections (daemon);
    }
    mhd_assert (NULL == daemon->connections_head);
    mhd_assert (NULL == daemon->cleanup_head);
    mhd_assert (NULL == daemon->suspended_connections_head);
    mhd_assert (NULL == daemon->new_connections_head);
#if defined(UPGRADE_SUPPORT) && defined(HTTPS_SUPPORT)
    mhd_assert (NULL == daemon->urh_head);
#endif /* UPGRADE_SUPPORT && HTTPS_SUPPORT */

    if (MHD_ITC_IS_VALID_ (daemon->itc))
      MHD_itc_destroy_chk_ (daemon->itc);

#ifdef EPOLL_SUPPORT
    if (MHD_D_IS_USING_EPOLL_ (daemon) &&
        (-1 != daemon->epoll_fd) )
      MHD_socket_close_chk_ (daemon->epoll_fd);
#if defined(HTTPS_SUPPORT) && defined(UPGRADE_SUPPORT)
    if (MHD_D_IS_USING_EPOLL_ (daemon) &&
        (-1 != daemon->epoll_upgrade_fd) )
      MHD_socket_close_chk_ (daemon->epoll_upgrade_fd);
#endif /* HTTPS_SUPPORT && UPGRADE_SUPPORT */
#endif /* EPOLL_SUPPORT */

#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
    MHD_mutex_destroy_chk_ (&daemon->cleanup_connection_mutex);
    MHD_mutex_destroy_chk_ (&daemon->new_connections_mutex);
#endif
  }

  if (NULL == daemon->master)
  {   /* Cleanup that should be done only one time in master/single daemon.
       * Do not perform this cleanup in worker daemons. */

    if (MHD_INVALID_SOCKET != fd)
      MHD_socket_close_chk_ (fd);

    /* TLS clean up */
#ifdef HTTPS_SUPPORT
    if (daemon->have_dhparams)
    {
      gnutls_dh_params_deinit (daemon->https_mem_dhparams);
      daemon->have_dhparams = false;
    }
    if (0 != (daemon->options & MHD_USE_TLS))
    {
      gnutls_priority_deinit (daemon->priority_cache);
      if (daemon->x509_cred)
        gnutls_certificate_free_credentials (daemon->x509_cred);
      if (daemon->psk_cred)
        gnutls_psk_free_server_credentials (daemon->psk_cred);
    }
#endif /* HTTPS_SUPPORT */

#ifdef DAUTH_SUPPORT
    free (daemon->digest_auth_random_copy);
    free (daemon->nnc);
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
    MHD_mutex_destroy_chk_ (&daemon->nnc_lock);
#endif
#endif
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
    MHD_mutex_destroy_chk_ (&daemon->per_ip_connection_mutex);
#endif
    /* Final probe before freeing the daemon structure. */
    mhd_probe_log_state ("Master cleanup: about to free daemon", daemon);
    free (daemon);
    mhd_probe_log_state ("Master cleanup: freed daemon (note: pointer now dangling)", daemon);
  }

  /* Exit probe */
  mhd_probe_log_state ("MHD_stop_daemon EXIT", daemon);
}
