#include <stdio.h>
#include <stdarg.h>
#include <string.h>
#include <stdlib.h>
#include <stdbool.h>
#include <stddef.h>

/* Probe logging helper: append human-readable traces to /tmp/logs/58.txt */
static void
mhd_log_probe (const char *fmt, ...)
{
  FILE *f = fopen ("/tmp/logs/58.txt", "a");
  if (NULL == f)
    return;
  va_list ap;
  va_start (ap, fmt);
  vfprintf (f, fmt, ap);
  va_end (ap);
  fputc ('\n', f);
  fclose (f);
}

static enum MHD_Result
add_response_header_connection (struct MHD_Response *response,
                                const char *value)
{
  static const char *key = MHD_HTTP_HEADER_CONNECTION;
  /** the length of the "Connection" key */
  static const size_t key_len =
    MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_CONNECTION);
  size_t value_len; /**< the length of the @a value */
  size_t old_value_len; /**< the length of the existing "Connection" value */
  size_t buf_size;  /**< the size of the buffer */
  size_t norm_len;  /**< the length of the normalised value */
  char *buf;        /**< the temporal buffer */
  struct MHD_HTTP_Res_Header *hdr; /**< existing "Connection" header */
  bool value_has_close; /**< the @a value has "close" token */
  bool already_has_close; /**< existing "Connection" header has "close" token */
  size_t pos = 0;   /**< position of addition in the @a buf */

  /* Log entry, input state */
  mhd_log_probe ("ENTER add_response_header_connection: response=%p, value='%s'",
                 (void *) response, (NULL != value) ? value : "(null)");

  if ( (NULL != strchr (value, '\r')) ||
       (NULL != strchr (value, '\n')) )
  {
    mhd_log_probe ("Rejected: value contains CR or LF");
    return MHD_NO;
  }

  if (0 != (response->flags_auto & MHD_RAF_HAS_CONNECTION_HDR))
  {
    hdr = MHD_get_response_element_n_ (response, MHD_HEADER_KIND,
                                       key, key_len);
    already_has_close =
      (0 != (response->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE));

    /* Probe the eventual assertion: already_has_close == (0 == memcmp (hdr->value, "close", 5)) */
    if (hdr != NULL)
    {
      int memcmp_res = memcmp (hdr->value, "close", 5);
      bool assert_expr1 = (already_has_close == (0 == memcmp_res));
      mhd_log_probe ("ASSERT CHECK 1: hdr=%p, hdr->value='%s', hdr->value_size=%zu, already_has_close=%d, memcmp_res=%d, expr=%d",
                     (void *) hdr,
                     hdr->value ? hdr->value : "(null)",
                     (size_t) hdr->value_size,
                     (int) already_has_close,
                     memcmp_res == 0 ? 0 : memcmp_res,
                     (int) assert_expr1);
    }
    else
    {
      mhd_log_probe ("ASSERT CHECK 1: hdr is NULL, already_has_close=%d",
                     (int) already_has_close);
    }
    mhd_assert (already_has_close == (0 == memcmp (hdr->value, "close", 5)));

    /* Probe the NULL assertion */
    bool assert_expr2 = (NULL != hdr);
    mhd_log_probe ("ASSERT CHECK 2: (NULL != hdr) => %d",
                   (int) assert_expr2);
    mhd_assert (NULL != hdr);
  }
  else
  {
    hdr = NULL;
    already_has_close = false;

    /* Probe the assertion that no existing header is present */
    struct MHD_HTTP_Res_Header *tmp_hdr = MHD_get_response_element_n_ (response,
                                                                      MHD_HEADER_KIND,
                                                                      key, key_len);
    mhd_log_probe ("ASSERT CHECK 3: MHD_get_response_element_n_ returned %p (expected NULL)",
                   (void *) tmp_hdr);
    mhd_assert (NULL == tmp_hdr);

    mhd_log_probe ("ASSERT CHECK 4: (response->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE) == 0 => %d",
                   (int) (0 == (response->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE)));
    mhd_assert (0 == (response->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE));
  }
  if (NULL != hdr)
    old_value_len = hdr->value_size + 2; /* additional size for ", " */
  else
    old_value_len = 0;

  value_len = strlen (value);
  mhd_log_probe ("value_len=%zu, old_value_len=%zu", value_len, old_value_len);

  if (value_len >= SSIZE_MAX)
  {
    mhd_log_probe ("Rejected: value_len >= SSIZE_MAX");
    return MHD_NO;
  }
  /* Additional space for normalisation and zero-termination */
  norm_len = value_len + value_len / 2 + 1;
  if (norm_len >= SSIZE_MAX)
  {
    mhd_log_probe ("Rejected: norm_len >= SSIZE_MAX");
    return MHD_NO;
  }
  buf_size = old_value_len + (size_t) norm_len;

  buf = malloc (buf_size);
  if (NULL == buf)
  {
    mhd_log_probe ("malloc failed for buf_size=%zu", buf_size);
    return MHD_NO;
  }
  if (1)
  { /* local scope */
    ssize_t norm_len_s = (ssize_t) norm_len;
    /* Remove "close" token (if any), it will be moved to the front */
    value_has_close = MHD_str_remove_token_caseless_ (value, value_len, "close",
                                                      MHD_STATICSTR_LEN_ ( \
                                                        "close"),
                                                      buf + old_value_len,
                                                      &norm_len_s);
    mhd_log_probe ("After MHD_str_remove_token_caseless_: value_has_close=%d, norm_len_s=%zd",
                   (int) value_has_close, norm_len_s);

    mhd_log_probe ("ASSERT CHECK 5: (0 <= norm_len_s) => %d", (int) (0 <= norm_len_s));
    mhd_assert (0 <= norm_len_s);

    if (0 > norm_len_s)
    {
      /* Must never happen with realistic sizes */
      free (buf);
      mhd_log_probe ("Rejected: norm_len_s < 0");
      return MHD_NO;
    }
    else
      norm_len = (size_t) norm_len_s;
  }
#ifdef UPGRADE_SUPPORT
  if ( (NULL != response->upgrade_handler) && value_has_close)
  { /* The "close" token cannot be used with connection "upgrade" */
    free (buf);
    mhd_log_probe ("Rejected: upgrade_handler present and value_has_close") ;
    return MHD_NO;
  }
#endif /* UPGRADE_SUPPORT */
  if (0 != norm_len)
    MHD_str_remove_tokens_caseless_ (buf + old_value_len, &norm_len,
                                     "keep-alive",
                                     MHD_STATICSTR_LEN_ ("keep-alive"));
  if (0 == norm_len)
  { /* New value is empty after normalisation */
    if (! value_has_close)
    { /* The new value had no tokens */
      free (buf);
      mhd_log_probe ("Rejected: norm_len==0 and no close token");
      return MHD_NO;
    }
    if (already_has_close)
    { /* The "close" token is already present, nothing to modify */
      free (buf);
      mhd_log_probe ("No modification needed: already_has_close") ;
      return MHD_YES;
    }
  }
  /* Add "close" token if required */
  if (value_has_close && ! already_has_close)
  {
    /* Need to insert "close" token at the first position */
    size_t close_comma_len = strlen ("close, ");
    mhd_log_probe ("About to insert 'close' token: buf_size=%zu, old_value_len=%zu, norm_len=%zu, required_min=%zu",
                   buf_size, old_value_len, norm_len,
                   (size_t) (old_value_len + norm_len + close_comma_len + 1));
    mhd_assert (buf_size >= old_value_len + norm_len   \
                + MHD_STATICSTR_LEN_ ("close, ") + 1);
    if (0 != norm_len)
      memmove (buf + MHD_STATICSTR_LEN_ ("close, ") + old_value_len,
               buf + old_value_len, norm_len + 1);
    memcpy (buf, "close", MHD_STATICSTR_LEN_ ("close"));
    pos += MHD_STATICSTR_LEN_ ("close");
  }
  /* Add old value tokens (if any) */
  if (0 != old_value_len)
  {
    if (0 != pos)
    {
      buf[pos++] = ',';
      buf[pos++] = ' ';
    }
    memcpy (buf + pos, hdr->value,
            hdr->value_size);
    pos += hdr->value_size;
  }
  /* Add new value token (if any) */
  if (0 != norm_len)
  {
    if (0 != pos)
    {
      buf[pos++] = ',';
      buf[pos++] = ' ';
    }
    /* The new value tokens must be already at the correct position */
    {
      bool assert_expr = (value_has_close && ! already_has_close) ?
        (MHD_STATICSTR_LEN_ ("close, ") + old_value_len == pos) :
        (old_value_len == pos);
      mhd_log_probe ("ASSERT CHECK 6: position invariant: value_has_close=%d, already_has_close=%d, old_value_len=%zu, pos=%zu, expr=%d",
                     (int) value_has_close, (int) already_has_close,
                     old_value_len, pos, (int) assert_expr);
    }
    mhd_assert ((value_has_close && ! already_has_close) ? \
                (MHD_STATICSTR_LEN_ ("close, ") + old_value_len == pos) : \
                (old_value_len == pos));
    pos += norm_len;
  }
  mhd_log_probe ("Before null-terminate: buf_size=%zu, pos=%zu", buf_size, pos);
  mhd_assert (buf_size > pos);
  buf[pos] = 0; /* Null terminate the result */

  if (NULL == hdr)
  {
    struct MHD_HTTP_Res_Header *new_hdr; /**< new "Connection" header */
    /* Create new response header entry */
    new_hdr = MHD_calloc_ (1, sizeof (struct MHD_HTTP_Res_Header));
    if (NULL != new_hdr)
    {
      new_hdr->header = malloc (key_len + 1);
      if (NULL != new_hdr->header)
      {
        memcpy (new_hdr->header, key, key_len + 1);
        new_hdr->header_size = key_len;
        new_hdr->value = buf;
        new_hdr->value_size = pos;
        new_hdr->kind = MHD_HEADER_KIND;
        if (value_has_close)
          response->flags_auto = (MHD_RAF_HAS_CONNECTION_HDR
                                  | MHD_RAF_HAS_CONNECTION_CLOSE);
        else
          response->flags_auto = MHD_RAF_HAS_CONNECTION_HDR;
        _MHD_insert_header_first (response, new_hdr);
        mhd_log_probe ("Inserted new header: header='%s', value='%s', value_size=%zu, flags_auto=0x%zx",
                       new_hdr->header ? new_hdr->header : "(null)",
                       new_hdr->value ? new_hdr->value : "(null)",
                       new_hdr->value_size, (size_t) response->flags_auto);
        return MHD_YES;
      }
      free (new_hdr);
    }
    free (buf);
    mhd_log_probe ("Failed to create new header: allocation failure");
    return MHD_NO;
  }

  /* Update existing header entry */
  mhd_log_probe ("Updating existing header: old_value='%s', old_value_size=%zu -> new_value='%s', new_value_size=%zu", hdr->value ? hdr->value : "(null)", hdr->value_size, buf ? buf : "(null)", pos);
  free (hdr->value);
  hdr->value = buf;
  hdr->value_size = pos;
  if (value_has_close && ! already_has_close)
    response->flags_auto |= MHD_RAF_HAS_CONNECTION_CLOSE;
  mhd_log_probe ("EXIT add_response_header_connection: return MHD_YES, response->flags_auto=0x%zx", (size_t) response->flags_auto);
  return MHD_YES;
}
