#include <stdio.h>
#include <stdarg.h>
#include <string.h>
#include <stdlib.h>
#include <stdbool.h>
#include <stdint.h>

static void
mhd_probe_log (const char *fmt, ...)
{
  FILE *f = fopen ("/tmp/logs/58.txt", "a");
  if (NULL == f)
    return;
  va_list ap;
  va_start (ap, fmt);
  vfprintf (f, fmt, ap);
  fprintf (f, "\n");
  va_end (ap);
  fclose (f);
}

static enum MHD_Result
add_response_header_connection (struct MHD_Response *response,
                                const char *value)
{
  static const char *key = MHD_HTTP_HEADER_CONNECTION;
  /** the length of the "Connection" key */
  static const size_t key_len =
    MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_CONNECTION);
  size_t value_len; /**< the length of the @a value */
  size_t old_value_len; /**< the length of the existing "Connection" value */
  size_t buf_size;  /**< the size of the buffer */
  size_t norm_len;  /**< the length of the normalised value */
  char *buf;        /**< the temporal buffer */
  struct MHD_HTTP_Res_Header *hdr; /**< existing "Connection" header */
  bool value_has_close; /**< the @a value has "close" token */
  bool already_has_close; /**< existing "Connection" header has "close" token */
  size_t pos = 0;   /**< position of addition in the @a buf */

  /* Log entry and basic state */
  mhd_probe_log ("ENTER add_response_header_connection: response=%p, value=%s",
                 (void *) response, value ? value : "(null)");

  if ( (NULL != strchr (value, '\r')) ||
       (NULL != strchr (value, '\n')) )
  {
    mhd_probe_log ("REJECT: value contains CR or LF -- returning MHD_NO");
    return MHD_NO;
  }

  if (0 != (response->flags_auto & MHD_RAF_HAS_CONNECTION_HDR))
  {
    hdr = MHD_get_response_element_n_ (response, MHD_HEADER_KIND,
                                       key, key_len);
    already_has_close =
      (0 != (response->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE));

    /* Probe state before the (no-op) asserts. Do NOT call helper functions
       that were only present inside the asserts in the original source to
       avoid changing control flow or side-effects. */
    if (NULL != hdr)
    {
      /* Log header pointer and available fields (safe because hdr != NULL).
         Do not call other library functions here. */
      mhd_probe_log ("BRANCH: existing header present hdr=%p header_size=%zu value_size=%zu already_has_close=%d",
                     (void *) hdr, hdr->header_size, hdr->value_size,
                     already_has_close ? 1 : 0);
    }
    else
    {
      mhd_probe_log ("BRANCH: flags indicate header present but hdr==NULL (hdr=%p)",
                     (void *) hdr);
    }
  }
  else
  {
    hdr = NULL;
    already_has_close = false;
    /* Original code only used MHD_get_response_element_n_ inside mhd_assert
       here; to preserve original observable behaviour we do not call it. */
    mhd_probe_log ("BRANCH: no existing header, hdr set to NULL, flags_auto=0x%zx",
                   response->flags_auto);
  }
  if (NULL != hdr)
    old_value_len = hdr->value_size + 2; /* additional size for ", " */
  else
    old_value_len = 0;

  mhd_probe_log ("old_value_len=%zu", old_value_len);

  value_len = strlen (value);
  mhd_probe_log ("value_len=%zu", value_len);
  if (value_len >= SSIZE_MAX)
  {
    mhd_probe_log ("REJECT: value_len >= SSIZE_MAX -- returning MHD_NO");
    return MHD_NO;
  }
  /* Additional space for normalisation and zero-termination */
  norm_len = value_len + value_len / 2 + 1;
  if (norm_len >= SSIZE_MAX)
  {
    mhd_probe_log ("REJECT: norm_len >= SSIZE_MAX -- returning MHD_NO");
    return MHD_NO;
  }
  buf_size = old_value_len + (size_t) norm_len;

  mhd_probe_log ("Allocating buf_size=%zu", buf_size);
  buf = malloc (buf_size);
  if (NULL == buf)
  {
    mhd_probe_log ("ALLOC FAILED: malloc returned NULL -- returning MHD_NO");
    return MHD_NO;
  }
  if (1)
  { /* local scope */
    ssize_t norm_len_s = (ssize_t) norm_len;
    /* Remove "close" token (if any), it will be moved to the front */
    value_has_close = MHD_str_remove_token_caseless_ (value, value_len, "close",
                                                      MHD_STATICSTR_LEN_ ( \
                                                        "close"),
                                                      buf + old_value_len,
                                                      &norm_len_s);

    /* Log the result of token removal (this call exists in the original
       control flow, so logging its result does not introduce new calls). */
    mhd_probe_log ("MHD_str_remove_token_caseless_: value_has_close=%d norm_len_s=%zd",
                   value_has_close ? 1 : 0, norm_len_s);

    mhd_assert (0 <= norm_len_s);
    if (0 > norm_len_s)
    {
      /* Must never happen with realistic sizes */
      mhd_probe_log ("ERROR: norm_len_s < 0 -- freeing buffer and returning MHD_NO");
      free (buf);
      return MHD_NO;
    }
    else
      norm_len = (size_t) norm_len_s;
  }
#ifdef UPGRADE_SUPPORT
  if ( (NULL != response->upgrade_handler) && value_has_close)
  { /* The "close" token cannot be used with connection "upgrade" */
    mhd_probe_log ("REJECT: upgrade_handler present and value_has_close -- freeing buf and returning MHD_NO");
    free (buf);
    return MHD_NO;
  }
#endif /* UPGRADE_SUPPORT */
  if (0 != norm_len)
    MHD_str_remove_tokens_caseless_ (buf + old_value_len, &norm_len,
                                     "keep-alive",
                                     MHD_STATICSTR_LEN_ ("keep-alive"));

  mhd_probe_log ("After normalisation: norm_len=%zu value_has_close=%d", norm_len, value_has_close ? 1 : 0);

  if (0 == norm_len)
  { /* New value is empty after normalisation */
    if (! value_has_close)
    { /* The new value had no tokens */
      mhd_probe_log ("REJECT: normalized value empty and no close token -- freeing buf and returning MHD_NO");
      free (buf);
      return MHD_NO;
    }
    if (already_has_close)
    { /* The "close" token is already present, nothing to modify */
      mhd_probe_log ("NO-OP: already_has_close is true and normalized value empty -- freeing buf and returning MHD_YES");
      free (buf);
      return MHD_YES;
    }
  }
  /* Add "close" token if required */
  if (value_has_close && ! already_has_close)
  {
    /* Need to insert "close" token at the first position */
    mhd_assert (buf_size >= old_value_len + norm_len   \
                + MHD_STATICSTR_LEN_ ("close, ") + 1);
    if (0 != norm_len)
      memmove (buf + MHD_STATICSTR_LEN_ ("close, ") + old_value_len,
               buf + old_value_len, norm_len + 1);
    memcpy (buf, "close", MHD_STATICSTR_LEN_ ("close"));
    pos += MHD_STATICSTR_LEN_ ("close");
    mhd_probe_log ("Inserted 'close' token: pos=%zu", pos);
  }
  /* Add old value tokens (if any) */
  if (0 != old_value_len)
  {
    if (0 != pos)
    {
      buf[pos++] = ',';
      buf[pos++] = ' ';
    }
    memcpy (buf + pos, hdr->value,
            hdr->value_size);
    pos += hdr->value_size;
    mhd_probe_log ("Appended old header tokens, new pos=%zu", pos);
  }
  /* Add new value token (if any) */
  if (0 != norm_len)
  {
    if (0 != pos)
    {
      buf[pos++] = ',';
      buf[pos++] = ' ';
    }
    /* The new value tokens must be already at the correct position */
    mhd_assert ((value_has_close && ! already_has_close) ? \
                (MHD_STATICSTR_LEN_ ("close, ") + old_value_len == pos) : \
                (old_value_len == pos));
    pos += norm_len;
    mhd_probe_log ("Appended new normalized tokens, pos after append=%zu", pos);
  }
  mhd_assert (buf_size > pos);
  buf[pos] = 0; /* Null terminate the result */

  mhd_probe_log ("Final header buffer: buf_size=%zu pos=%zu buf='%s'", buf_size, pos, buf);

  if (NULL == hdr)
  {
    struct MHD_HTTP_Res_Header *new_hdr; /**< new "Connection" header */
    /* Create new response header entry */
    new_hdr = MHD_calloc_ (1, sizeof (struct MHD_HTTP_Res_Header));
    if (NULL != new_hdr)
    {
      new_hdr->header = malloc (key_len + 1);
      if (NULL != new_hdr->header)
      {
        memcpy (new_hdr->header, key, key_len + 1);
        new_hdr->header_size = key_len;
        new_hdr->value = buf;
        new_hdr->value_size = pos;
        new_hdr->kind = MHD_HEADER_KIND;
        if (value_has_close)
          response->flags_auto = (MHD_RAF_HAS_CONNECTION_HDR
                                  | MHD_RAF_HAS_CONNECTION_CLOSE);
        else
          response->flags_auto = MHD_RAF_HAS_CONNECTION_HDR;
        _MHD_insert_header_first (response, new_hdr);
        mhd_probe_log ("Inserted new header new_hdr=%p flags_auto=0x%zx returning MHD_YES",
                       (void *) new_hdr, response->flags_auto);
        return MHD_YES;
      }
      free (new_hdr);
    }
    free (buf);
    mhd_probe_log ("FAILED to create new header structures -- returning MHD_NO");
    return MHD_NO;
  }

  /* Update existing header entry */
  free (hdr->value);
  hdr->value = buf;
  hdr->value_size = pos;
  if (value_has_close && ! already_has_close)
    response->flags_auto |= MHD_RAF_HAS_CONNECTION_CLOSE;
  mhd_probe_log ("Updated existing header hdr=%p value_size=%zu flags_auto=0x%zx returning MHD_YES",
                 (void *) hdr, hdr->value_size, response->flags_auto);
  return MHD_YES;
}
