#include <stdio.h>
#include <stdarg.h>
#include <stdlib.h>
#include <string.h>
#include <sys/stat.h>
#include <sys/types.h>
#include <errno.h>

/* Probe logging helper. Non-intrusive: creates /tmp/logs if necessary and
   appends human-readable trace information to /tmp/logs/58.txt. */
static void
mhd_probe_logf (const char *fmt, ...)
{
  va_list ap;
  FILE *f;
  /* Try to create directory; ignore errors if it already exists. */
  (void) mkdir("/tmp/logs", 0755);
  f = fopen("/tmp/logs/58.txt", "a");
  if (NULL == f)
    return;
  va_start (ap, fmt);
  vfprintf (f, fmt, ap);
  va_end (ap);
  fprintf (f, "\n");
  fflush (f);
  fclose (f);
}

static enum MHD_Result
add_response_header_connection (struct MHD_Response *response,
                                const char *value)
{
  static const char *key = MHD_HTTP_HEADER_CONNECTION;
  /** the length of the "Connection" key */
  static const size_t key_len =
    MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_CONNECTION);
  size_t value_len; /**< the length of the @a value */
  size_t old_value_len; /**< the length of the existing "Connection" value */
  size_t buf_size;  /**< the size of the buffer */
  size_t norm_len;  /**< the length of the normalised value */
  char *buf;        /**< the temporal buffer */
  struct MHD_HTTP_Res_Header *hdr; /**< existing "Connection" header */
  bool value_has_close; /**< the @a value has "close" token */
  bool already_has_close; /**< existing "Connection" header has "close" token */
  size_t pos = 0;   /**< position of addition in the @a buf */

  /* Log entry and basic inputs */
  mhd_probe_logf ("add_response_header_connection: entry: response=%p, value=%p\nvalue_str=%s, flags_auto=0x%zx",
                  (void *) response, (const void *) value, value ? value : "(null)",
                  (size_t) (response ? response->flags_auto : 0));

  if ( (NULL != strchr (value, '\r')) ||
       (NULL != strchr (value, '\n')) )
  {
    mhd_probe_logf ("add_response_header_connection: rejecting value because it contains CR or LF");
    return MHD_NO;
  }

  if (0 != (response->flags_auto & MHD_RAF_HAS_CONNECTION_HDR))
  {
    hdr = MHD_get_response_element_n_ (response, MHD_HEADER_KIND,
                                       key, key_len);
    already_has_close =
      (0 != (response->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE));

    /* Evaluate the expressions that would be checked by mhd_assert and log them. */
    int cmp_close = 0;
    if (hdr && hdr->value && hdr->value_size >= 5)
      cmp_close = (0 == memcmp (hdr->value, "close", 5));
    mhd_probe_logf ("branch: has_connection_hdr: hdr=%p, hdr->value=%p, hdr->value_size=%zu, already_has_close=%d, memcmp_close=%d",
                    (void *) hdr, hdr ? (void *) hdr->value : NULL,
                    hdr ? hdr->value_size : 0, (int) already_has_close, cmp_close);

    /* Keep the original mhd_assert calls but log the same conditions beforehand
       so that evidence of the (potentially no-op) assertions is available. */
    mhd_probe_logf ("about to mhd_assert: already_has_close == (0 == memcmp(hdr->value, \"close\", 5)) -> %d",
                    (int) (already_has_close == (0 == memcmp (hdr ? hdr->value : NULL, "close", 5))));
    mhd_assert (already_has_close == (0 == memcmp (hdr->value, "close", 5)));

    mhd_probe_logf ("about to mhd_assert: NULL != hdr -> %d",
                    (int) (NULL != hdr));
    mhd_assert (NULL != hdr);
  }
  else
  {
    hdr = NULL;
    already_has_close = false;

    struct MHD_HTTP_Res_Header *tmp_hdr =
      MHD_get_response_element_n_ (response, MHD_HEADER_KIND, key, key_len);

    mhd_probe_logf ("branch: no_connection_hdr: hdr_lookup=%p, flags_auto=0x%zx",
                    (void *) tmp_hdr, (size_t) response->flags_auto);

    mhd_probe_logf ("about to mhd_assert: NULL == MHD_get_response_element_n_(...) -> %d",
                    (int) (NULL == tmp_hdr));
    mhd_assert (NULL == tmp_hdr);

    mhd_probe_logf ("about to mhd_assert: 0 == (response->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE) -> %d",
                    (int) (0 == (response->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE)));
    mhd_assert (0 == (response->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE));
  }
  if (NULL != hdr)
    old_value_len = hdr->value_size + 2; /* additional size for ", " */
  else
    old_value_len = 0;

  mhd_probe_logf ("after header handling: hdr=%p, old_value_len=%zu, already_has_close=%d",
                  (void *) hdr, old_value_len, (int) already_has_close);

  value_len = strlen (value);
  mhd_probe_logf ("value_len=%zu", value_len);
  if (value_len >= SSIZE_MAX)
  {
    mhd_probe_logf ("value_len >= SSIZE_MAX -> reject");
    return MHD_NO;
  }
  /* Additional space for normalisation and zero-termination */
  norm_len = value_len + value_len / 2 + 1;
  mhd_probe_logf ("norm_len(initial)=%zu", norm_len);
  if (norm_len >= SSIZE_MAX)
  {
    mhd_probe_logf ("norm_len >= SSIZE_MAX -> reject");
    return MHD_NO;
  }
  buf_size = old_value_len + (size_t) norm_len;

  mhd_probe_logf ("allocating buf_size=%zu", buf_size);
  buf = malloc (buf_size);
  if (NULL == buf)
  {
    mhd_probe_logf ("malloc failed for size %zu", buf_size);
    return MHD_NO;
  }
  if (1)
  { /* local scope */
    ssize_t norm_len_s = (ssize_t) norm_len;
    /* Remove "close" token (if any), it will be moved to the front */
    value_has_close = MHD_str_remove_token_caseless_ (value, value_len, "close",
                                                      MHD_STATICSTR_LEN_ ( \
                                                        "close"),
                                                      buf + old_value_len,
                                                      &norm_len_s);
    mhd_probe_logf ("after MHD_str_remove_token_caseless_: value_has_close=%d, norm_len_s=%zd",
                    (int) value_has_close, norm_len_s);

    mhd_probe_logf ("about to mhd_assert: 0 <= norm_len_s -> %d",
                    (int) (0 <= norm_len_s));
    mhd_assert (0 <= norm_len_s);
    if (0 > norm_len_s)
    {
      /* Must never happen with realistic sizes */
      free (buf);
      mhd_probe_logf ("norm_len_s < 0 -> free(buf) and return MHD_NO");
      return MHD_NO;
    }
    else
      norm_len = (size_t) norm_len_s;
  }
#ifdef UPGRADE_SUPPORT
  if ( (NULL != response->upgrade_handler) && value_has_close)
  { /* The "close" token cannot be used with connection "upgrade" */
    free (buf);
    mhd_probe_logf ("upgrade_handler present and value_has_close -> reject");
    return MHD_NO;
  }
#endif /* UPGRADE_SUPPORT */
  if (0 != norm_len)
  {
    mhd_probe_logf ("before remove_keep_alive: norm_len=%zu", norm_len);
    MHD_str_remove_tokens_caseless_ (buf + old_value_len, &norm_len,
                                     "keep-alive",
                                     MHD_STATICSTR_LEN_ ("keep-alive"));
    mhd_probe_logf ("after remove_keep_alive: norm_len=%zu", norm_len);
  }
  if (0 == norm_len)
  { /* New value is empty after normalisation */
    if (! value_has_close)
    { /* The new value had no tokens */
      free (buf);
      mhd_probe_logf ("norm_len == 0 and no close token -> free(buf) and return MHD_NO");
      return MHD_NO;
    }
    if (already_has_close)
    { /* The "close" token is already present, nothing to modify */
      free (buf);
      mhd_probe_logf ("norm_len == 0 and already_has_close -> free(buf) and return MHD_YES");
      return MHD_YES;
    }
  }
  /* Add "close" token if required */
  if (value_has_close && ! already_has_close)
  {
    /* Need to insert "close" token at the first position */
    mhd_probe_logf ("about to mhd_assert: buf_size >= old_value_len + norm_len + close_len + 1 -> buf_size=%zu, old_value_len=%zu, norm_len=%zu",
                    buf_size, old_value_len, norm_len);
    mhd_assert (buf_size >= old_value_len + norm_len   \
                + MHD_STATICSTR_LEN_ ("close, ") + 1);
    if (0 != norm_len)
      memmove (buf + MHD_STATICSTR_LEN_ ("close, ") + old_value_len,
               buf + old_value_len, norm_len + 1);
    memcpy (buf, "close", MHD_STATICSTR_LEN_ ("close"));
    pos += MHD_STATICSTR_LEN_ ("close");

    mhd_probe_logf ("inserted close token: pos=%zu", pos);
  }
  /* Add old value tokens (if any) */
  if (0 != old_value_len)
  {
    if (0 != pos)
    {
      buf[pos++] = ',';
      buf[pos++] = ' ';
    }
    memcpy (buf + pos, hdr->value,
            hdr->value_size);
    pos += hdr->value_size;

    mhd_probe_logf ("added old header value: pos=%zu", pos);
  }
  /* Add new value token (if any) */
  if (0 != norm_len)
  {
    if (0 != pos)
    {
      buf[pos++] = ',';
      buf[pos++] = ' ';
    }
    /* The new value tokens must be already at the correct position */
    mhd_probe_logf ("about to mhd_assert: position check: pos=%zu, old_value_len=%zu, value_has_close=%d, already_has_close=%d, expected_pos_when_close=%zu",
                    pos, old_value_len, (int) value_has_close, (int) already_has_close,
                    (size_t) (MHD_STATICSTR_LEN_ ("close, ") + old_value_len));
    mhd_assert ((value_has_close && ! already_has_close) ? \
                (MHD_STATICSTR_LEN_ ("close, ") + old_value_len == pos) : \
                (old_value_len == pos));
    pos += norm_len;

    mhd_probe_logf ("after adding new value: pos=%zu", pos);
  }
  mhd_probe_logf ("about to final mhd_assert: buf_size > pos -> buf_size=%zu, pos=%zu -> %d",
                  buf_size, pos, (int) (buf_size > pos));
  mhd_assert (buf_size > pos);
  buf[pos] = 0; /* Null terminate the result */

  if (NULL == hdr)
  {
    struct MHD_HTTP_Res_Header *new_hdr; /**< new "Connection" header */
    /* Create new response header entry */
    new_hdr = MHD_calloc_ (1, sizeof (struct MHD_HTTP_Res_Header));
    if (NULL != new_hdr)
    {
      new_hdr->header = malloc (key_len + 1);
      if (NULL != new_hdr->header)
      {
        memcpy (new_hdr->header, key, key_len + 1);
        new_hdr->header_size = key_len;
        new_hdr->value = buf;
        new_hdr->value_size = pos;
        new_hdr->kind = MHD_HEADER_KIND;
        if (value_has_close)
          response->flags_auto = (MHD_RAF_HAS_CONNECTION_HDR
                                  | MHD_RAF_HAS_CONNECTION_CLOSE);
        else
          response->flags_auto = MHD_RAF_HAS_CONNECTION_HDR;
        _MHD_insert_header_first (response, new_hdr);
        mhd_probe_logf ("inserted new header: new_hdr=%p, header=%s, value=%s, flags_auto=0x%zx",
                        (void *) new_hdr, new_hdr->header ? new_hdr->header : "(null)",
                        new_hdr->value ? new_hdr->value : "(null)", (size_t) response->flags_auto);
        return MHD_YES;
      }
      free (new_hdr);
    }
    free (buf);
    mhd_probe_logf ("failed to create new header -> return MHD_NO");
    return MHD_NO;
  }

  /* Update existing header entry */
  free (hdr->value);
  hdr->value = buf;
  hdr->value_size = pos;
  if (value_has_close && ! already_has_close)
    response->flags_auto |= MHD_RAF_HAS_CONNECTION_CLOSE;

  mhd_probe_logf ("updated existing header: hdr=%p, new_value=%s, value_size=%zu, flags_auto=0x%zx",
                  (void *) hdr, hdr->value ? hdr->value : "(null)", hdr->value_size, (size_t) response->flags_auto);
  return MHD_YES;
}
